import React, { useEffect, useRef, useState} from "react";
import { useNavigate } from 'react-router-dom';
import "./style.css"
import {
  nowInSec,
  SkyWayAuthToken,
  SkyWayContext,
  SkyWayRoom,
  //SkyWayStreamFactory,
  uuidV4,
  RemoteVideoStream
} from '@skyway-sdk/room';

/**
 * „Éõ„Éº„É†ÁîªÈù¢„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
 * @param {object} props - „Éó„É≠„Éë„ÉÜ„Ç£
 * @param {function} props.setIsLoggedIn - „É≠„Ç∞„Ç§„É≥Áä∂ÊÖã„ÇíË®≠ÂÆö„Åô„ÇãÈñ¢Êï∞
 */
function Home({setIsLoggedIn}) {

    const remoteVideoRef = useRef(null); // „É™„É¢„Éº„ÉàÊò†ÂÉè„ÇíË°®Á§∫„Åô„Çã„Åü„ÇÅ„ÅÆvideoË¶ÅÁ¥†„Å∏„ÅÆÂèÇÁÖß

    const [cameraStatus, setCameraStatus] = useState("Êé•Á∂ö„ÇíÂæÖÊ©ü‰∏≠..."); // „Ç´„É°„É©Êé•Á∂öÁä∂ÊÖã
    const [isConnectedToRoom, setIsConnectedToRoom] = useState(false); // SkyWay„É´„Éº„É†„Å∏„ÅÆÊé•Á∂öÁä∂ÊÖã
    const [hasRemoteStream, setHasRemoteStream] = useState(false); // „É™„É¢„Éº„Éà„Çπ„Éà„É™„Éº„É†„ÅÆÊúâÁÑ°

    const [skywayContext, setSkywayContext] = useState(null);
    const [skywayRoom, setSkywayRoom] = useState(null);

    // Âõ∫ÂÆö„ÅÆ„É´„Éº„É†ID
    const FIXED_ROOM_ID = 'robot-control-room-v2';

    const navigate = useNavigate();

  /**
   * „É≠„Ç∞„Ç¢„Ç¶„Éà„Éú„Çø„É≥„ÇØ„É™„ÉÉ„ÇØÊôÇ„ÅÆ„Éè„É≥„Éâ„É©
   */

    const handleLogout = () => {
    setIsLoggedIn(false); // „É≠„Ç∞„Ç§„É≥Áä∂ÊÖã„Çífalse„Å´Ë®≠ÂÆö
    navigate('/'); // „É≠„Ç∞„Ç¢„Ç¶„ÉàÂæå„ÄÅ„É≠„Ç∞„Ç§„É≥„Éö„Éº„Ç∏„Å∏ÈÅ∑Áßª
    };

    //token„ÅÆÁîüÊàê
    const token = new SkyWayAuthToken({
        jti: uuidV4(),
        iat: nowInSec(),
        exp: nowInSec() + 60 * 60 * 24,
        version: 3,
        scope: {
            appId: "4490dcb5-dbe8-4028-ba0a-6d0d143e4515", //appId„ÅÆË®≠ÂÆö
            rooms: [
            {
            name: "*",
            methods: ["create", "close", "updateMetadata"],
            member: {
                name: "*",
                methods: ["publish", "subscribe", "updateMetadata"]
            }
            }],
            turn: {
            enabled: true
            }
        }
    }).encode("40HHGEIOx0BRByPdxqwUswK0a+7v2JaeaJ9CcFjMdAQ="); //secret„ÅÆË®≠ÂÆö

    //„Çπ„Éà„É™„Éº„Éü„É≥„Ç∞ÁîªÈù¢„ÅÆÈÖç‰ø°Âá¶ÁêÜ
    useEffect(() => {
        let currentSkywayContext = null;
        let currentSkywayRoom = null;

        const initSkyWay = async () => {
            // SkyWay SDK„ÅÆ„ÇØ„É©„Çπ„Åå„Ç∞„É≠„Éº„Éê„É´„Çπ„Ç≥„Éº„Éó„ÅßÂà©Áî®ÂèØËÉΩ„Åß„ÅÇ„Çã„ÅãÁ¢∫Ë™ç
            if (typeof SkyWayContext === 'undefined' || typeof SkyWayRoom === 'undefined' || typeof RemoteVideoStream === 'undefined') {
                console.error("SkyWay SDK„ÅÆ„ÇØ„É©„Çπ„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì„ÄÇHTML„ÅßSkyWay SDK„ÅåÊ≠£„Åó„ÅèË™≠„ÅøËæº„Åæ„Çå„Å¶„ÅÑ„Çã„ÅãÁ¢∫Ë™ç„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ");
                setCameraStatus("SkyWay SDK„ÅåË™≠„ÅøËæº„Åæ„Çå„Å¶„ÅÑ„Åæ„Åõ„Çì„ÄÇ");
                return;
            }

            try {
                setCameraStatus("SkyWay Context„ÇíÂàùÊúüÂåñ‰∏≠...");
                const token = token;
                if (!token) {
                    setCameraStatus("SkyWay„Éà„Éº„ÇØ„É≥„ÅÆÁîüÊàê„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ");
                    return;
                }

                currentSkywayContext = await SkyWayContext.Create(token);
                setSkywayContext(currentSkywayContext);
                console.log("SkyWayContext initialized.");

                setCameraStatus(`„É´„Éº„É† '${FIXED_ROOM_ID}' „Å´Êé•Á∂ö‰∏≠...`);
                currentSkywayRoom = await SkyWayRoom.FindOrCreate(currentSkywayContext, {
                    type: 'p2p', // P2P„É´„Éº„É†„Çø„Ç§„Éó„Çí‰ΩøÁî®
                    name: FIXED_ROOM_ID,
                });
                setSkywayRoom(currentSkywayRoom);
                setIsConnectedToRoom(true);
                setCameraStatus(`„É´„Éº„É† '${FIXED_ROOM_ID}' „Å´Êé•Á∂ö„Åó„Åæ„Åó„Åü„ÄÇ„Çπ„Éà„É™„Éº„É†„ÇíÂæÖÊ©ü‰∏≠...`);

                // „É™„É¢„Éº„Éà„É°„É≥„Éê„Éº„Åå„É´„Éº„É†„Å´ÂèÇÂä†„Åó„Åü„Å®„Åç„ÅÆ„Ç§„Éô„É≥„Éà„É™„Çπ„Éä„Éº
                currentSkywayRoom.onMemberJoined.add(async ({ member }) => {
                    console.log(`„É°„É≥„Éê„Éº„ÅåÂèÇÂä†„Åó„Åæ„Åó„Åü: ${member.id}`);
                    // ÂèÇÂä†„Åó„Åü„É°„É≥„Éê„Éº„ÅåÊó¢„Å´ÂÖ¨Èñã„Åó„Å¶„ÅÑ„Çã„Çπ„Éà„É™„Éº„É†„ÇíË≥ºË™≠
                    member.publications.forEach(async (publication) => {
                        // „Éì„Éá„Ç™„Çπ„Éà„É™„Éº„É†„ÅÆ„Åø„ÇíË≥ºË™≠
                        if (publication.contentType === 'video') {
                            console.log(`ÂèÇÂä†„É°„É≥„Éê„Éº ${member.id} „Åå„Éì„Éá„Ç™„Çπ„Éà„É™„Éº„É†„ÇíÂÖ¨ÈñãÊ∏à„Åø: ${publication.id}`);
                            const { stream } = await currentSkywayRoom.subscribe(publication.id);
                            if (remoteVideoRef.current && stream instanceof RemoteVideoStream) {
                                remoteVideoRef.current.srcObject = stream;
                                setHasRemoteStream(true);
                                setCameraStatus("„É≠„Éú„ÉÉ„ÉàÊò†ÂÉè„ÇíÂèó‰ø°‰∏≠");
                                console.log("„É™„É¢„Éº„ÉàÊò†ÂÉè„ÇíË°®Á§∫‰∏≠„ÄÇ");
                            }
                        }
                    });
                });

                // „É™„É¢„Éº„Éà„É°„É≥„Éê„Éº„Åå„Çπ„Éà„É™„Éº„É†„ÇíÂÖ¨Èñã„Åó„Åü„Å®„Åç„ÅÆ„Ç§„Éô„É≥„Éà„É™„Çπ„Éä„Éº (Âæå„Åã„ÇâÂÖ¨Èñã„Åï„Çå„ÇãÂ†¥Âêà„ÅÆ„Åü„ÇÅ)
                currentSkywayRoom.onStreamPublished.add(async ({ publication }) => {
                    // Ëá™Ë∫´„ÅÆÂÖ¨Èñã„Çπ„Éà„É™„Éº„É†„ÅØË≥ºË™≠„Åó„Å™„ÅÑÔºà„Åì„ÅÆ„Ç¢„Éó„É™„Åß„ÅØÂÖ¨Èñã„Åó„Å™„ÅÑ„ÅåÂøµ„ÅÆ„Åü„ÇÅÔºâ
                    if (publication.origin.id === currentSkywayRoom.localPerson.id) {
                        return;
                    }
                    // „Éì„Éá„Ç™„Çπ„Éà„É™„Éº„É†„ÅÆ„Åø„ÇíË≥ºË™≠
                    if (publication.contentType === 'video') {
                        console.log(`„É™„É¢„Éº„Éà„Éì„Éá„Ç™„Çπ„Éà„É™„Éº„É†„ÅåÂÖ¨Èñã„Åï„Çå„Åæ„Åó„Åü: ${publication.id}`);
                        const { stream } = await currentSkywayRoom.subscribe(publication.id);
                        if (remoteVideoRef.current && stream instanceof RemoteVideoStream) {
                            remoteVideoRef.current.srcObject = stream;
                            setHasRemoteStream(true);
                            setCameraStatus("„É≠„Éú„ÉÉ„ÉàÊò†ÂÉè„ÇíÂèó‰ø°‰∏≠");
                            console.log("„É™„É¢„Éº„ÉàÊò†ÂÉè„ÇíË°®Á§∫‰∏≠„ÄÇ");
                        }
                    }
                });

                // „É™„É¢„Éº„Éà„É°„É≥„Éê„Éº„Åå„Çπ„Éà„É™„Éº„É†„ÇíÈùûÂÖ¨Èñã„Å´„Åó„Åü„Å®„Åç„ÅÆ„Ç§„Éô„É≥„Éà„É™„Çπ„Éä„Éº
                currentSkywayRoom.onStreamUnpublished.add(({ publication }) => {
                    if (publication.contentType === 'video') {
                        console.log(`„É™„É¢„Éº„Éà„Éì„Éá„Ç™„Çπ„Éà„É™„Éº„É†„ÅåÈùûÂÖ¨Èñã„Å´„Å™„Çä„Åæ„Åó„Åü: ${publication.id}`);
                        // ÁèæÂú®Ë°®Á§∫„Åó„Å¶„ÅÑ„Çã„ÅÆ„ÅåÈùûÂÖ¨Èñã„Å´„Å™„Å£„Åü„Çπ„Éà„É™„Éº„É†„Åß„ÅÇ„Çã„Åã„ÇíÁ¢∫Ë™ç„Åó„ÄÅÂÅúÊ≠¢„Åô„Çã
                        // „Ç∑„É≥„Éó„É´Âåñ„ÅÆ„Åü„ÇÅ„ÄÅ„Åì„Åì„Åß„ÅØÂçò„Å´srcObject„Çínull„Å´Ë®≠ÂÆö„Åó„Åæ„Åô
                        if (remoteVideoRef.current && remoteVideoRef.current.srcObject) {
                             remoteVideoRef.current.srcObject = null;
                        }
                        setHasRemoteStream(false);
                        setCameraStatus("„Çπ„Éà„É™„Éº„É†„ÅåÂàáÊñ≠„Åï„Çå„Åæ„Åó„Åü„ÄÇÊé•Á∂ö„ÇíÂæÖÊ©ü‰∏≠...");
                        console.log("„É™„É¢„Éº„ÉàÊò†ÂÉè„ÅÆË°®Á§∫„ÇíÂÅúÊ≠¢„Åó„Åæ„Åó„Åü„ÄÇ");
                    }
                });

                // „É°„É≥„Éê„Éº„Åå„É´„Éº„É†„Åã„ÇâÈÄÄÂá∫„Åó„Åü„Å®„Åç„ÅÆ„Ç§„Éô„É≥„Éà„É™„Çπ„Éä„Éº
                currentSkywayRoom.onMemberLeft.add(({ member }) => {
                    console.log(`„É°„É≥„Éê„Éº„ÅåÈÄÄÂá∫„Åó„Åæ„Åó„Åü: ${member.id}`);
