import React, { useEffect, useMemo, useRef, useState } from "react";
import './style.css'; // style.cssを再度有効化
import {
  nowInSec,
  SkyWayAuthToken,
  SkyWayContext,
  SkyWayRoom,
  SkyWayStreamFactory,
  uuidV4,
  LocalP2PRoomMember,
  P2PRoom
} from '@skyway-sdk/room';

/**
 * ホーム画面コンポーネント
 * @param {object} props - プロパティ
 * @param {function} props.setIsLoggedIn - ログイン状態を設定する関数
 */
function Home({ setIsLoggedIn }) {
  // ルーム名
  const [roomName, setRoomName] = useState("");
  // 自分の参加者情報 (ルームに参加すると設定されます)
  const [me, setMe] = useState(null);
  // SkyWayコンテキストのインスタンス
  const [skywayContext, setSkywayContext] = useState(null);
  // SkyWayルームのインスタンス
  const [skywayRoom, setSkywayRoom] = useState(null);
  // リモートビデオ要素を管理するためのMap (memberId -> videoElement)
  const remoteVideoElements = useRef(new Map());
  // リモートビデオを表示するDOM要素への参照
  const robotDisplayRef = useRef(null);
  // ローカルストリームの参照を保持
  const localStreamRef = useRef(null);
  // 受信したメッセージを表示するための状態
  const [receivedMessages, setReceivedMessages] = useState([]);


  /**
   * ログアウトボタンクリック時のハンドラ
   */
  const handleLogout = () => {
    setIsLoggedIn(false); // ログイン状態をfalseに設定
  };

  // SkyWay App ID と Secret Key
  // 注意: 本番環境では、これらのキーをクライアントサイドにハードコードせず、
  // サーバーサイドでトークンを生成してクライアントに安全に渡すことを強く推奨します。
  const appId = useMemo(() => "4490dcb5-dbe8-4028-ba0a-6d0d143e4515", []);
  const secretKey = useMemo(() => "40HHGEIOx0BRByPdxqwUswK0a+7v2JaeaJ9CcFjMdAQ=", []);

  // SkyWay認証トークンの生成
  const token = useMemo(() => {
    if (appId == null || secretKey == null) return undefined;

    return new SkyWayAuthToken({
      jti: uuidV4(), // JWT ID
      iat: nowInSec(), // 発行時刻
      exp: nowInSec() + 60 * 60 * 24, // 有効期限 (24時間)
      version: 3,
      scope: {
        appId: appId,
        rooms: [
          {
            name: "*", // すべてのルーム名に適用
            methods: ["create", "close", "updateMetadata"], // ルーム操作の権限
            member: {
              name: "*", // すべてのメンバーに適用
              methods: ["publish", "subscribe", "updateMetadata", "sendData"] // メンバー操作の権限にsendDataを追加
            }
          }],
        turn: {
          enabled: true // TURNサーバーの使用を許可 (NAT越えのため)
        }
      }
    }).encode(secretKey);

  }, [appId, secretKey]);

  /**
   * SkyWayContextの初期化とクリーンアップを行うuseEffect
   * トークンが利用可能になったときに一度だけ実行されます。
   */
  useEffect(() => {
    if (!token) return;

    let currentContext;
    const initContext = async () => {
      try {
        currentContext = await SkyWayContext.Create(token);
        setSkywayContext(currentContext);
        console.log("SkyWayContext created successfully.");
      } catch (error) {
        console.error("Failed to create SkyWayContext:", error);
      }
    };

    initContext();

    // コンポーネントのアンマウント時にコンテキストを破棄
    return () => {
      if (currentContext) {
        currentContext.dispose();
        console.log("SkyWayContext disposed.");
      }
    };
  }, [token]); // tokenが変更されたときにのみ実行

  /**
   * リモートストリームをDOMに追加するヘルパー関数
   * @param {MediaStream} stream - 追加するメディアストリーム
   * @param {string} memberId - ストリームのパブリッシャーのメンバーID
   */
  const attachRemoteStream = (stream, memberId) => {
    if (robotDisplayRef.current) {
      const video = document.createElement("video");
      video.autoplay = true; // 自動再生
      video.playsInline = true; // インライン再生 (iOSなどで必要)
      video.className = "remote-video"; // スタイリング用のクラス
      video.dataset.memberId = memberId; // メンバーIDをデータ属性に保存
      stream.attach(video); // ストリームをビデオ要素のソースに設定
      robotDisplayRef.current.appendChild(video);
      remoteVideoElements.current.set(memberId, video);
      console.log(`Attached stream from ${memberId} to DOM.`);
    }
  };

  /**
   * ルームに参加するハンドラ
   * SkyWayContextが初期化され、ルーム名が入力されている場合に実行されます。
   */
  const handleJoinRoom = async () => {
    if (!skywayContext) {
      console.error("SkyWayContext is not initialized. Please wait or check token.");
      return;
    }
    if (!roomName) {
      console.error("Room name is empty. Please enter a room name to join.");
      return;
    }
    if (skywayRoom && me) {
      console.warn("Already in a room. Please leave the current room first.");
      return;
    }

    try {
      // 指定されたルーム名でP2Pルームを作成または検索
      const room = await SkyWayRoom.FindOrCreate(skywayContext, {
        type: "p2p", // P2Pルームタイプ
        name: roomName,
      });

      // roomオブジェクトが有効であることを確認
      if (!room) {
        console.error("SkyWayRoom.FindOrCreate returned null or undefined room.");
        throw new Error("Failed to create or find SkyWayRoom.");
      }

      setSkywayRoom(room);
      console.log(`SkyWayRoom '${roomName}' found or created.`);

      // ルームに参加し、自分のメンバー情報を取得
      const localMember = await room.join();
      // localMemberが有効であることを確認
      if (!localMember) {
        console.error("room.join() returned null or undefined localMember.");
        throw new Error("Failed to join the room.");
      }
      setMe(localMember);
      console.log(`Joined room '${roomName}' as member ID: ${localMember.id}`);

      // 自分のカメラとマイクのストリームを作成し、公開
      const { audio, video } = await SkyWayStreamFactory.createMicrophoneAudioAndCameraStream();
      localStreamRef.current = video; // ローカルストリームを保存
      await localMember.publish(video);
      await localMember.publish(audio);
      console.log("Published local camera and microphone stream.");

      // --- 既存のストリームを購読するロジック ---
      // ルーム内の既存の公開ストリームをすべて購読する
      for (const publication of room.publications) {
        // 自分のストリームは購読しない
        if (localMember.id === publication.publisher.id) {
          continue;
        }

        if (publication.contentType === 'video' || publication.contentType === 'audio') {
          try {
            const { stream } = await localMember.subscribe(publication.id);
            console.log(`Subscribed to existing media stream from ${publication.publisher.id}`);
            attachRemoteStream(stream, publication.publisher.id);
          } catch (subscribeError) {
            console.error(`Failed to subscribe to existing media stream from ${publication.publisher.id}:`, subscribeError);
          }
        } else if (publication.contentType === 'data') {
          try {
            const { stream: dataStream } = await localMember.subscribe(publication.id);
            console.log(`Subscribed to existing data stream from ${publication.publisher.id}`);
            // データストリームのonDataイベントリスナーを設定
            dataStream.onData.add((data) => {
              console.log(`Received data from ${publication.publisher.id}:`, data);
              setReceivedMessages(prevMessages => [...prevMessages, { senderId: publication.publisher.id, message: data, isLocal: false }]);
            });
          } catch (subscribeError) {
            console.error(`Failed to subscribe to existing data stream from ${publication.publisher.id}:`, subscribeError);
          }
        }
      }

      // --- ルームイベントリスナーの設定 ---

      // メンバーがルームに参加したときのイベント
      if (room.onMemberJoined) {
        room.onMemberJoined.add((e) => {
          console.log(`Member joined: ${e.member.id}`);
        });
      } else {
        console.error("room.onMemberJoined is undefined. This is unexpected.");
      }

      // メンバーがルームを離脱したときのイベント
      if (room.onMemberLeft) {
        room.onMemberLeft.add((e) => {
          console.log(`Member left: ${e.member.id}`);
          // 該当メンバーのビデオ要素をDOMから削除し、Mapからも削除
          const videoElement = remoteVideoElements.current.get(e.member.id);
          if (videoElement && robotDisplayRef.current) {
            robotDisplayRef.current.removeChild(videoElement);
            remoteVideoElements.current.delete(e.member.id);
            console.log(`Removed video for member ${e.member.id}`);
          }
        });
      } else {
        console.error("room.onMemberLeft is undefined. This is unexpected.");
      }

      // ストリームが公開されたときのイベント (ビデオ/オーディオのみを処理)
      if (room.onStreamPublished) {
        room.onStreamPublished.add(async (e) => {
          console.log(`Stream published by ${e.publication.publisher.id}, type: ${e.publication.contentType}`);
          // 自分のストリームは購読しない
          if (localMember.id !== e.publication.publisher.id) {
            if (e.publication.contentType === 'video' || e.publication.contentType === 'audio') {
              try {
                // リモートメディアストリームを購読
                const { stream } = await localMember.subscribe(e.publication.id);
                console.log(`Subscribed to new media stream from ${e.publication.publisher.id}`);
                attachRemoteStream(stream, e.publication.publisher.id);
              } catch (subscribeError) {
                console.error(`Failed to subscribe to new media stream from ${e.publication.publisher.id}:`, subscribeError);
              }
            }
            // データストリームはonDataPublishedで処理されるため、ここではスキップ
          } else {
            console.log(`Skipping subscription for own stream: ${e.publication.publisher.id}`);
          }
        });
      } else {
        console.error("room.onStreamPublished is undefined. This is unexpected.");
      }

      // ストリームが非公開になったときのイベント
      if (room.onStreamUnpublished) {
        room.onStreamUnpublished.add((e) => {
          console.log(`Stream unpublished by ${e.publication.publisher.id}`);
          // 該当ストリームのビデオ要素をDOMから削除し、Mapからも削除
          const videoElement = remoteVideoElements.current.get(e.publication.publisher.id);
          if (videoElement && robotDisplayRef.current) {
            robotDisplayRef.current.removeChild(videoElement);
            remoteVideoElements.current.delete(e.publication.publisher.id);
            console.log(`Removed unpublished video for member ${e.publication.publisher.id}`);
          }
        });
      } else {
        console.error("room.onStreamUnpublished is undefined. This is unexpected.");
      }

      // データストリームが公開されたときのイベント
      if (room.onDataPublished) {
        room.onDataPublished.add(async (e) => {
          console.log(`Data stream published by ${e.publication.publisher.id}`);
          if (localMember.id !== e.publication.publisher.id) {
            try {
              const { stream: dataStream } = await localMember.subscribe(e.publication.id);
              console.log(`Subscribed to new data stream from ${e.publication.publisher.id}`);
              // データストリームのonDataイベントリスナーを設定
              dataStream.onData.add((data) => {
                console.log(`Received data from ${e.publication.publisher.id}:`, data);
                setReceivedMessages(prevMessages => [...prevMessages, { senderId: e.publication.publisher.id, message: data, isLocal: false }]);
              });
            } catch (subscribeError) {
              console.error(`Failed to subscribe to new data stream from ${e.publication.publisher.id}:`, subscribeError);
            }
          }
        });
      } else {
        console.error("room.onDataPublished is undefined. This is unexpected.");
      }

    } catch (error) {
      console.error("Failed to join room:", error);
      // エラー発生時に状態をリセットして再試行可能にする
      setSkywayRoom(null);
      setMe(null);
      // エラー時に既存のビデオ要素をクリーンアップ
      if (robotDisplayRef.current) {
        Array.from(robotDisplayRef.current.children).forEach(child => {
          if (child.tagName === 'VIDEO') {
            robotDisplayRef.current.removeChild(child);
          }
        });
      }
      remoteVideoElements.current.clear();
      if (localStreamRef.current) {
        localStreamRef.current.stop(); // ローカルストリームを停止
        localStreamRef.current = null;
      }
    }
  };

  /**
   * ルームから退出するハンドラ
   */
  const handleLeaveRoom = async () => {
    if (skywayRoom && me) {
      try {
        // ローカルストリームを停止し、公開を解除
        if (localStreamRef.current) {
          localStreamRef.current.getTracks().forEach(track => track.stop()); // すべてのトラックを停止
          localStreamRef.current = null;
          console.log("Stopped local stream.");
        }
        await me.leave(); // ルームから退出
        console.log(`Left room '${roomName}'`);
        // ルームを閉じる (これはルームの作成者のみが効果的ですが、クリーンアップとして呼び出し)
        // P2PRoomの場合、close()は通常不要ですが、念のため
        if (skywayRoom instanceof P2PRoom) {
            // P2PRoomは明示的なclose()メソッドを持たないことが多いので、dispose()を検討
            // SDKのバージョンや実装によるため、エラーが出ないように注意
            // skywayRoom.dispose(); // 必要であれば
        } else {
            skywayRoom.close();
        }
        console.log("SkyWay Room resources cleaned up.");
      } catch (error) {
        console.error("Failed to leave room:", error);
      } finally {
        // 状態をリセット
        setMe(null);
        setSkywayRoom(null);
        setReceivedMessages([]); // 受信メッセージもクリア
        // すべてのリモートビデオ要素をDOMから削除
        if (robotDisplayRef.current) {
          Array.from(robotDisplayRef.current.children).forEach(child => {
            if (child.tagName === 'VIDEO') {
              robotDisplayRef.current.removeChild(child);
            }
          });
        }
        remoteVideoElements.current.clear();
      }
    }
  };

  /**
   * モーションを操作するボタンを押した時の処理
   * @param {string} message - 送信するメッセージ (ボタンのテキスト)
   */
  const handleSend = async (message) => {
    if (me && skywayRoom) {
      try {
        // me.sendData() を使用してデータを送信
        await me.sendData(message);
        console.log(`Sent message: "${message}"`);
        // 送信したメッセージも自身の画面に表示
        setReceivedMessages(prevMessages => [...prevMessages, { senderId: me.id, message: message, isLocal: true }]);
      } catch (error) {
        console.error("Failed to send data:", error);
      }
    } else {
      console.warn("Cannot send message: Not in a room or 'me' object is not available.");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col font-inter">
      <div className="header">
        <div className="header-left">
          <button className="motion-btn">モーション</button>
          <span className="robot-status">
            操作しているロボット：RX78-myakumyaku
          </span>
        </div>
        <div className="header-right">
          <button onClick={handleLogout} className="logout-btn">ログアウト</button>
          <button className="voice-btn">ボイス</button>
        </div>
      </div>
      <div className="main-container">
        <div className="side-panel">
          <div className="side-panel-content">
            <button onClick={() => handleSend('こんにちは')} className="side-btn">こんにちは</button>
            <button onClick={() => handleSend('バイバイ')} className="side-btn">バイバイ</button>
            <button onClick={() => handleSend('ペコリ')} className="side-btn">ペコリ</button>
            <button onClick={() => handleSend('バタバタ')} className="side-btn">バタバタ</button>
            <button onClick={() => handleSend('うえをむく')} className="side-btn">うえをむく</button>
            <button onClick={() => handleSend('したをむく')} className="side-btn">したをむく</button>
            <button onClick={() => handleSend('みぎをむく')} className="side-btn">みぎをむく</button>
            <button onClick={() => handleSend('ひだりをむく')} className="side-btn">ひだりをむく</button>
            <button onClick={() => handleSend('うなずく')} className="side-btn">うなずく</button>
            <button onClick={() => handleSend('かしげる')} className="side-btn">かしげる</button>
            <button onClick={() => handleSend('まわる')} className="side-btn">まわる</button>
            <button onClick={() => handleSend('ジャンプ')} className="side-btn">ジャンプ</button>
            <button onClick={() => handleSend('ダンス')} className="side-btn">ダンス</button>
            <button onClick={() => handleSend('おじぎ')} className="side-btn">おじぎ</button>
            <button onClick={() => handleSend('きをつけ')} className="side-btn">きをつけ</button>
            <button onClick={() => handleSend('休め')} className="side-btn">休め</button>
          </div>
        </div>
        <div className="main-area">
          <div className="room-controls">
            <input
              type="text"
              placeholder="ルーム名を入力"
              value={roomName}
              onChange={(e) => setRoomName(e.target.value)}
              className="room-input"
              disabled={!!me} // ルーム参加中は入力不可にする
            />
            {!me ? ( // ルームに参加していない場合
              <button onClick={handleJoinRoom} className="join-room-btn" disabled={!skywayContext || !roomName}>
                ルームに参加
              </button>
            ) : ( // ルームに参加している場合
              <button onClick={handleLeaveRoom} className="leave-room-btn">
                ルームを退出
              </button>
            )}
          </div>
          <div className="robot-display" ref={robotDisplayRef}>
            {/* リモートのビデオストリームがここに動的に追加されます */}
            {me ? (
              <div className="room-info">
                ルームID: {roomName} / 自分のID: {me.id}
              </div>
            ) : (
              <p>ルームに参加してください</p>
            )}
            {/* 受信メッセージ表示 */}
            <div className="received-messages-display">
              <h3>受信メッセージ:</h3>
              {receivedMessages.length === 0 ? (
                <p>メッセージはありません</p>
              ) : (
                <ul>
                  {receivedMessages.map((msg, index) => (
                    <li key={index} className={msg.isLocal ? 'local-message' : 'remote-message'}>
                      <strong>{msg.isLocal ? '自分' : msg.senderId.substring(0, 8)}:</strong> {msg.message}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        </div>
        <div className="side-panel right">
          <div className="side-panel-content">
            <button onClick={() => handleSend('こんにちは')} className="side-btn">こんにちは</button>
            <button onClick={() => handleSend('どこからきたの？')} className="side-btn">どこからきたの？</button>
            <button onClick={() => handleSend('バイバイ')} className="side-btn">バイバイ</button>
            <button onClick={() => handleSend('○○していますか？')} className="side-btn">○○していますか？</button>
            <button onClick={() => handleSend('ありがとう')} className="side-btn">ありがとう</button>
            <button onClick={() => handleSend('いいね')} className="side-btn">いいね</button>
            <button onClick={() => handleSend('おおきに')} className="side-btn">おおきに</button>
            <button onClick={() => handleSend('すごい')} className="side-btn">すごい</button>
            <button onClick={() => handleSend('がんばれ')} className="side-btn">がんばれ</button>
            <button onClick={() => handleSend('よろしく')} className="side-btn">よろしく</button>
            <button onClick={() => handleSend('お疲れ様')} className="side-btn">お疲れ様</button>
            <button onClick={() => handleSend('いいお天気')} className="side-btn">いいお天気</button>
            <button onClick={() => handleSend('暑いですね')} className="side-btn">暑いですね</button>
            <button onClick={() => handleSend('寒いですね')} className="side-btn">寒いですね</button>
            <button onClick={() => handleSend('楽しい')} className="side-btn">楽しい</button>
            <button onClick={() => handleSend('面白い')} className="side-btn">面白い</button>
            <button onClick={() => handleSend('すてき')} className="side-btn">すてき</button>
            <button onClick={() => handleSend('かわいい')} className="side-btn">かわいい</button>
            <button onClick={() => handleSend('かっこいい')} className="side-btn">かっこいい</button>
            <button onClick={() => handleSend('また明日')} className="side-btn">また明日</button>
          </div>
        </div>
      </div>
      <div className="bottom-controls">
        <button className="control-btn ai-btn">🤖 AIでいあんON</button>
        <button className="control-btn mic-btn" id="micBtn">
          🎤
        </button>
        <button className="control-btn manual-btn" id="manualBtn">
          📋 マニュアル
        </button>
        <button className="control-btn greeting-btn" id="languageBtn">
          👋 にほんご
        </button>
      </div>
    </div>
  );
}

export default Home;
