import React, { useEffect, useMemo, useRef, useState } from "react";
import './style.css'
// SkyWay SDKをnpmからインポートします (すべてのエクスポートをSkyWaySDKオブジェクトとして取得)
import * as SkyWaySDK from '@skyway-sdk/room';

// アプリケーションのメインコンポーネント
function Hoem() {
    // ログイン状態を管理するステート
    const [isLoggedIn, setIsLoggedIn] = useState(true); // 初期状態をログイン済みとする
    // SkyWayルーム名
    const [roomName, setRoomName] = useState("your-robot-room"); // ここに適切なルーム名を設定してください。ロボット側も同じルーム名で接続する必要があります。
    // 自身のSkyWay参加者情報
    const [me, setMe] = useState(null);
    // リモートの映像を表示するためのvideo要素への参照
    const remoteVideoRef = useRef(null);
    // ローカルの映像を表示するためのvideo要素への参照
    const localVideoRef = useRef(null); // ローカル映像用Refを追加
    // 映像が再生中かどうかを追跡するステート
    const [isRemoteVideoPlaying, setIsRemoteVideoPlaying] = useState(false);
    // ローカルの接続状態メッセージ
    const [connectionStatus, setConnectionStatus] = useState("SkyWayに接続中...");

    /**
     * ログアウトボタンクリック時のハンドラ
     * このデモではログイン状態をfalseにするだけで、実際のページ遷移は行いません。
     */
    const handleLogout = () => {
        setIsLoggedIn(false); // ログイン状態をfalseに設定
        // 実際のアプリケーションでは、ここでログインページへ遷移します
        console.log("Logged out. In a real app, you would navigate to the login page.");
    };

    // SkyWay App ID と Secret Key は、ご自身のものを設定してください。
    // この値はデモ用であり、本番環境では安全な方法で管理する必要があります。
    const appId = useMemo(() => "4490dcb5-dbe8-4028-ba0a-6d0d143e4515", []);
    const secretKey = useMemo(() => "40HHGEIOx0BRByPdxqwUswK0a+7v2JaeaJ9CcFjMdAQ=", []);

    // SkyWay認証トークンの生成
    const token = useMemo(() => {
        // App IDまたはSecret Keyが不足している場合はトークン生成をスキップ
        if (!appId || !secretKey) {
            console.log("SkyWay App ID or Secret Key is missing, skipping token generation.");
            setConnectionStatus("SkyWay App IDまたはSecret Keyが不足しています。");
            return undefined;
        }

        return new SkyWaySDK.SkyWayAuthToken({ // SkyWaySDK.SkyWayAuthTokenに変更
            jti: SkyWaySDK.uuidV4(), // SkyWaySDK.uuidV4に変更
            iat: SkyWaySDK.nowInSec(), // SkyWaySDK.nowInSecに変更
            exp: SkyWaySDK.nowInSec() + 60 * 60 * 24, // SkyWaySDK.nowInSecに変更
            version: 3,
            scope: {
                appId: appId,
                rooms: [
                    {
                        name: "*", // すべてのルーム名にアクセス許可
                        methods: ["create", "close", "updateMetadata"],
                        member: {
                            name: "*", // すべてのメンバーにアクセス許可
                            methods: ["publish", "subscribe", "updateMetadata"]
                        }
                    }
                ],
                turn: {
                    enabled: true // TURNサーバーの使用を許可
                }
            }
        }).encode(secretKey);
    }, [appId, secretKey]);

    // SkyWayルームへの接続とストリームの購読処理
    useEffect(() => {
        let localStream = null; // ローカルストリームを保持するための変数

        const joinRoom = async () => {
            // トークンまたはルーム名が利用可能でない場合はルーム参加をスキップ
            if (!token || !roomName) {
                console.log("SkyWay token or room name not available, skipping room join.");
                setConnectionStatus("SkyWayトークンまたはルーム名が利用できません。");
                return;
            }

            try {
                setConnectionStatus("SkyWayルームに接続中...");
                // SkyWayコンテキストの作成
                const context = await SkyWaySDK.SkyWayContext.Create(token); // SkyWaySDK.SkyWayContextに変更
                // SkyWayルームへの参加または作成
                const room = await SkyWaySDK.SkyWayRoom.FindOrCreate(context, { // SkyWaySDK.SkyWayRoomに変更
                    name: roomName,
                    type: "p2p", // P2Pルームとして設定
                });
                // ルームに参加し、自身のメンバー情報を取得
                const newMe = await room.join();
                setMe(newMe);
                console.log(`Joined room: ${roomName} as member: ${newMe.id}`);
                setConnectionStatus(`ルームに参加しました: ${roomName} (ID: ${newMe.id.substring(0, 8)}...)`);

                // ローカルのカメラとマイクのストリームを取得
                try {
                    // SkyWayStreamFactoryが正しくインポートされているか確認
                    // このチェックはSkyWaySDK.*にすることで不要になりますが、念のため残します
                    if (typeof SkyWaySDK.SkyWayStreamFactory.createMicrophoneAudioAndCameraStream !== 'function') {
                        throw new Error("SkyWaySDK.SkyWayStreamFactory.createMicrophoneAudioAndCameraStream is not a function. Check SkyWay SDK import.");
                    }
                    localStream = await SkyWaySDK.SkyWayStreamFactory.createMicrophoneAudioAndCameraStream(); // SkyWaySDK.SkyWayStreamFactoryに変更
                    if (localVideoRef.current) {
                        localVideoRef.current.srcObject = localStream.mediaStream;
                        
                        await localVideoRef.current.play();
                        console.log('fuckyou');
                        console.log("Local video stream started.");
                    }
                    // ローカルストリームをSkyWayルームに公開
                    await newMe.publish(localStream);
                    console.log("Local stream published to SkyWay.");
                } catch (mediaError) {
                    console.error("Failed to get local media stream or publish it:", mediaError);
                    setConnectionStatus(`カメラ/マイクへのアクセスに失敗しました: ${mediaError.message}`);
                }


                // リモートメンバーがストリームを公開したときのイベントリスナー
                newMe.onStreamPublished.add(async (e) => {
                    const { stream } = e;
                    console.log(`Stream published by ${e.publication.publisher.id}: ${stream.id}, ContentType: ${stream.contentType}`);
                    // 映像ストリームのみを対象とする
                    if (stream.contentType === "video") {
                        console.log(`Attempting to subscribe to video stream from ${e.publication.publisher.id}`);
                        // ストリームを購読
                        const subscribedStream = await newMe.subscribe(stream);
                        // video要素にストリームを設定し、再生を開始
                        if (remoteVideoRef.current) {
                            remoteVideoRef.current.srcObject = subscribedStream.mediaStream;
                            remoteVideoRef.current.play()
                                .then(() => {
                                    console.log("Remote video stream started successfully.");
                                    setIsRemoteVideoPlaying(true); // 映像が再生されたことを記録
                                })
                                .catch((error) => {
                                    console.error("Failed to play remote video (autoplay blocked?):", error);
                                    setIsRemoteVideoPlaying(false); // 再生失敗
                                });
                        }
                    }
                });

                // 既にルームにいるメンバーのストリームを購読する
                // これは、あなたがルームに参加する前に他のメンバーが既にストリームを公開していた場合に必要です。
                for (const member of room.members) {
                    if (member.id === newMe.id) continue; // 自身を除く
                    for (const publication of member.publications) {
                        // 映像ストリームのみを対象とする
                        if (publication.contentType === "video") {
                            console.log(`Attempting to subscribe to existing video stream from ${publication.publisher.id}`);
                            const subscribedStream = await newMe.subscribe(publication);
                            if (remoteVideoRef.current) {
                                remoteVideoRef.current.srcObject = subscribedStream.mediaStream;
                                remoteVideoRef.current.play()
                                    .then(() => {
                                        console.log("Existing remote video stream started successfully.");
                                        setIsRemoteVideoPlaying(true); // 映像が再生されたことを記録
                                    })
                                    .catch((error) => {
                                        console.error("Failed to play existing remote video (autoplay blocked?):", error);
                                        setIsRemoteVideoPlaying(false); // 再生失敗
                                    });
                            }
                        }
                    }
                }

            } catch (error) {
                console.error("Failed to join SkyWay room:", error);
                setConnectionStatus(`SkyWayルームへの接続に失敗しました: ${error.message}`);
            }
        };

        // ログイン状態がtrueの場合のみSkyWay接続を試みる
        if (isLoggedIn) {
            joinRoom();
        }

        // コンポーネントがアンマウントされる際のクリーンアップ関数
        return () => {
            // meがnullでない場合のみleaveを呼び出す
            if (me) {
                console.log(`Leaving room: ${roomName}`);
                me.leave(); // ルームから退出
                setMe(null); // meステートをリセット (これはコンポーネントが完全にアンマウントされる場合にのみ意味があります)
            }
            // ローカルストリームも停止
            if (localStream) {
                localStream.mediaStream.getTracks().forEach(track => track.stop());
                console.log("Local stream tracks stopped.");
            }
        };
    }, [token, roomName, isLoggedIn]); // 依存配列からmeを削除しました

    // ログインしていない場合は何も表示しない（実際のアプリではログイン画面を表示）
    if (!isLoggedIn) {
        return (
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '24px' }}>
                <p>ログアウトしました。ログインしてください。</p>
            </div>
        );
    }

    return(
        <>
            <meta charSet="UTF-8" />
            <meta name="viewport" content="width=device-width, initial-scale=1.0" />
            <title>ロボットチャット - RX78-myakumyaku</title>
            <div className="header">
                <div className="header-left">
                    <button className="motion-btn">モーション</button>
                    <span className="robot-status">
                        操作しているロボット：RX78-myakumyaku
                    </span>
                </div>
                <div className="header-right">
                    <button onClick={handleLogout} className="logout-btn">ログアウト</button>
                    <button className="voice-btn">ボイス</button>
                </div>
            </div>
            <div className="main-container">
                <div className="side-panel">
                    <div className="side-panel-content">
                        <button className="side-btn">こんにちは</button>
                        <button className="side-btn">バイバイ</button>
                        <button className="side-btn">ペコリ</button>
                        <button className="side-btn">バタバタ</button>
                        <button className="side-btn">うえをむく</button>
                        <button className="side-btn">したをむく</button>
                        <button className="side-btn">みぎをむく</button>
                        <button className="side-btn">ひだりをむく</button>
                        <button className="side-btn">うなずく</button>
                        <button className="side-btn">かしげる</button>
                        <button className="side-btn">まわる</button>
                        <button className="side-btn">ジャンプ</button>
                        <button className="side-btn">ダンス</button>
                        <button className="side-btn">おじぎ</button>
                        <button className="side-btn">きをつけ</button>
                        <button className="side-btn">休め</button>
                    </div>
                </div>
                <div className="main-area">
                    <div className="robot-display">
                        {/* ローカルの映像を表示するためのvideo要素 (自分のカメラ映像) */}
                        <video ref={localVideoRef} autoPlay playsInline muted style={{ width: '100%', height: '50%', objectFit: 'contain', borderBottom: '1px solid gray' }} />
                        {/* リモートの映像を表示するためのvideo要素 (他のメンバーのカメラ映像) */}
                        <video ref={remoteVideoRef} autoPlay playsInline muted style={{ width: '100%', height: '50%', objectFit: 'contain' }} />
                        {!isRemoteVideoPlaying && (
                            <div style={{
                                position: 'absolute',
                                top: '50%',
                                left: '50%',
                                transform: 'translate(-50%, -50%)',
                                color: 'white',
                                fontSize: '20px',
                                background: 'rgba(0, 0, 0, 0.7)',
                                padding: '10px 20px',
                                borderRadius: '10px',
                                zIndex: 10,
                                textAlign: 'center'
                            }}>
                                {connectionStatus}
                                <br />
                                他のメンバーの映像を待機中...
                            </div>
                        )}
                    </div>
                </div>
                <div className="side-panel right">
                    <div className="side-panel-content">
                        <button className="side-btn">こんにちは</button>
                        <button className="side-btn">どこからきたの？</button>
                        <button className="side-btn">バイバイ</button>
                        <button className="side-btn">○○していますか？</button>
                        <button className="side-btn">ありがとう</button>
                        <button className="side-btn">いいね</button>
                        <button className="side-btn">おおきに</button>
                        <button className="side-btn">すごい</button>
                        <button className="side-btn">がんばれ</button>
                        <button className="side-btn">よろしく</button>
                        <button className="side-btn">お疲れ様</button>
                        <button className="side-btn">いいお天気</button>
                        <button className="side-btn">暑いですね</button>
                        <button className="side-btn">寒いですね</button>
                        <button className="side-btn">楽しい</button>
                        <button className="side-btn">面白い</button>
                        <button className="side-btn">すてき</button>
                        <button className="side-btn">かわいい</button>
                        <button className="side-btn">かっこいい</button>
                        <button className="side-btn">また明日</button>
                    </div>
                </div>
            </div>
            <div className="bottom-controls">
                <button className="control-btn ai-btn">🤖 AIでいあんON</button>
                <button className="control-btn mic-btn" id="micBtn">
                    🎤
                </button>
                <button className="control-btn manual-btn" id="manualBtn">
                    📋 マニュアル
                </button>
                <button className="control-btn greeting-btn" id="languageBtn">
                    👋 にほんご
                </button>
            </div>
        </>
    );
}

export default Hoem;
