        console.log("Left room on component unmount.");
      }
      // ãƒ­ãƒ¼ã‚«ãƒ«ã‚¹ãƒˆãƒªãƒ¼ãƒ ã®ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
      if (localStreamRef.current) {
        localStreamRef.current.getTracks().forEach(track => track.stop());
        localStreamRef.current = null;
      }
      if (localDataStreamRef.current) {
        localDataStreamRef.current.close();
        localDataStreamRef.current = null;
      }
    };
  }, [skywayRoom]);


  /**
   * ãƒã‚¤ã‚¯ãƒŸãƒ¥ãƒ¼ãƒˆã®ã‚ªãƒ³ã‚ªãƒ•ã‚’åˆ‡ã‚Šæ›¿ãˆã‚‹ãƒãƒ³ãƒ‰ãƒ©
   */
  const handleMute = async () => {
    if (!audioPublicationRef.current) {
      console.warn("Audio publication not found. Cannot toggle mute.");
      return;
    }
    const publication = audioPublicationRef.current;
    if (isMuted) {
      // ãƒŸãƒ¥ãƒ¼ãƒˆè§£é™¤
      await publication.enable();
      console.log("Microphone enabled.");
    } else {
      // ãƒŸãƒ¥ãƒ¼ãƒˆ
      await publication.disable();
      console.log("Microphone disabled.");
    }
    setIsMuted(!isMuted);
  };

  /**
   * AIãƒ¢ãƒ¼ãƒ‰ã®ã‚ªãƒ³ã‚ªãƒ•ã‚’åˆ‡ã‚Šæ›¿ãˆã‚‹ãƒãƒ³ãƒ‰ãƒ©
   */
  const handleAiModeToggle = () => {
    // AIãƒ¢ãƒ¼ãƒ‰ãŒONã«ãªã£ãŸã‚‰ã€ææ¡ˆã‚’ãƒªã‚»ãƒƒãƒˆã™ã‚‹
    if (!isAiModeOn) {
