  /**
   * ææ¡ˆãƒœã‚¿ãƒ³ãŒã‚¯ãƒªãƒƒã‚¯ã•ã‚ŒãŸæ™‚ã®ãƒãƒ³ãƒ‰ãƒ©
   * @param {string} messageKey - é€ä¿¡ã™ã‚‹ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã®ã‚­ãƒ¼
   */
  const handleSuggestionClick = (messageKey) => {
    // ãƒ¢ãƒ¼ã‚·ãƒ§ãƒ³ãŒé€²è¡Œä¸­ã®å ´åˆã¯ä½•ã‚‚ã—ãªã„
    if (isMoveInProgress) {
      console.warn("Motion is already in progress. Ignoring new command.");
      return;
    }

    if (localDataStreamRef.current && isAiModeOn) {
      const messageToSend = messages[messageKey][currentLanguage];
      console.log(`Sending AI suggestion: ${messageToSend}`);
      localDataStreamRef.current.write(messageToSend);
      setIsMoveInProgress(true); // ãƒ¢ãƒ¼ã‚·ãƒ§ãƒ³é–‹å§‹
      // é€ä¿¡å¾Œã€ææ¡ˆã‚’ã‚¯ãƒªã‚¢ã™ã‚‹
      setAiSuggestions([null, null]);
    }
  };

  /**
   * ã‚µã‚¤ãƒ‰ãƒ‘ãƒãƒ«ã®ãƒœã‚¿ãƒ³ã‚¯ãƒªãƒƒã‚¯æ™‚ã®ãƒãƒ³ãƒ‰ãƒ©
   * @param {string} messageKey - é€ä¿¡ã™ã‚‹ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã®ã‚­ãƒ¼
   */
  const handleSidePanelButtonClick = (messageKey) => {
    // ãƒ¢ãƒ¼ã‚·ãƒ§ãƒ³ãŒé€²è¡Œä¸­ã®å ´åˆã¯ä½•ã‚‚ã—ãªã„
    if (isMoveInProgress) {
      console.warn("Motion is already in progress. Ignoring new command.");
      return;
    }

    if (localDataStreamRef.current) {
      const messageToSend = messages[messageKey][currentLanguage];
      console.log(`Sending manual command: ${messageToSend}`);
      localDataStreamRef.current.write(messageToSend);
      setIsMoveInProgress(true); // ãƒ¢ãƒ¼ã‚·ãƒ§ãƒ³é–‹å§‹
    }
  };


  return (
    <div className="app-container">
      {/* ãƒ˜ãƒƒãƒ€ãƒ¼éƒ¨åˆ† */}
      <header className="header">
        <div className="header-left">
          <button className="motion-btn" onClick={() => handleSidePanelButtonClick('hello')}>Reset</button>
          <div className="robot-status">
            {me ? `Room: ${roomName} / My ID: ${me.id}` : 'Not connected'}
          </div>
        </div>
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </header>

      {/* ãƒ¡ã‚¤ãƒ³ã‚³ãƒ³ãƒ†ãƒ³ãƒ„éƒ¨åˆ† */}
      <div className="main-container">
        {/* ã‚µã‚¤ãƒ‰ãƒ‘ãƒãƒ« */}
        <aside className="side-panel">
