            <button className="side-btn" onClick={() => handleSidePanelButtonClick('hello')}>ã“ã‚“ã«ã¡ã¯</button>
            <button className="side-btn" onClick={() => handleSidePanelButtonClick('goodbye')}>ã•ã‚ˆã†ãªã‚‰</button>
            <button className="side-btn" onClick={() => handleSidePanelButtonClick('bow')}>ãŠè¾å„€</button>
            <button className="side-btn" onClick={() => handleSidePanelButtonClick('flap')}>ãƒ‘ã‚¿ãƒ‘ã‚¿</button>
            <button className="side-btn" onClick={() => handleSidePanelButtonClick('lookUp')}>ä¸Šã‚’å‘ã</button>
            <button className="side-btn" onClick={() => handleSidePanelButtonClick('lookDown')}>ä¸‹ã‚’å‘ã</button>
            <button className="side-btn" onClick={() => handleSidePanelButtonClick('lookRight')}>å³ã‚’å‘ã</button>
            <button className="side-btn" onClick={() => handleSidePanelButtonClick('lookLeft')}>å·¦ã‚’å‘ã</button>
            <button className="side-btn" onClick={() => handleSidePanelButtonClick('nod')}>ã†ãªãšã</button>
            <button className="side-btn" onClick={() => handleSidePanelButtonClick('tiltHead')}>é¦–ã‚’å‚¾ã’ã‚‹</button>
            <button className="side-btn" onClick={() => handleSidePanelButtonClick('spin')}>ã‚¹ãƒ”ãƒ³</button>
            <button className="side-btn" onClick={() => handleSidePanelButtonClick('jump')}>ã‚¸ãƒ£ãƒ³ãƒ—</button>
            <button className="side-btn" onClick={() => handleSidePanelButtonClick('dance')}>ãƒ€ãƒ³ã‚¹</button>
          </div>
        </aside>

        {/* ãƒ¡ã‚¤ãƒ³è¡¨ç¤ºã‚¨ãƒªã‚¢ */}
        <main className="main-area">
          <div className="robot-display" ref={robotDisplayRef}>
            {/* ã“ã“ã«ãƒªãƒ¢ãƒ¼ãƒˆãƒ“ãƒ‡ã‚ªãŒè¡¨ç¤ºã•ã‚Œã¾ã™ */}
          </div>
          {/* ãƒ«ãƒ¼ãƒ å‚åŠ /é€€å‡ºUI */}
          <div className="room-controls">
            <input
              type="text"
              placeholder="ãƒ«ãƒ¼ãƒ åã‚’å…¥åŠ›"
              value={roomName}
              onChange={(e) => setRoomName(e.target.value)}
              className="room-name-input"
              disabled={me}
            />
            {me ? (
              <button onClick={handleLeaveRoom} className="join-leave-btn leave-btn">
                é€€å‡º
              </button>
            ) : (
              <button onClick={handleJoinRoom} className="join-leave-btn join-btn">
                å‚åŠ 
              </button>
            )}
          </div>
          {/* AIææ¡ˆãƒœã‚¿ãƒ³ */}
          {isAiModeOn && (aiSuggestions[0] || aiSuggestions[1]) && (
            <div className="ai-suggestions-container">
              {aiSuggestions.map((suggestion, index) => (
                suggestion && (
                  <button
                    key={index}
