          >
            ğŸ¤– AIãƒ¢ãƒ¼ãƒ‰ {isAiModeOn ? 'ON' : 'OFF'}
          </button>
          <button
            className={`control-btn mic-btn ${isMuted ? 'muted' : ''}`} // ãƒŸãƒ¥ãƒ¼ãƒˆçŠ¶æ…‹ã«å¿œã˜ã¦ã‚¯ãƒ©ã‚¹ã‚’è¿½åŠ 
            onClick={handleMute}
            id="micBtn"
            disabled={!me || !audioPublicationRef.current || isAiModeOn} // ãƒ«ãƒ¼ãƒ ã«å‚åŠ ã—ã¦ã„ãªã„ã€ã¾ãŸã¯ã‚ªãƒ¼ãƒ‡ã‚£ã‚ªãŒå…¬é–‹ã•ã‚Œã¦ã„ãªã„ã€AIãƒ¢ãƒ¼ãƒ‰ä¸­ã¯ç„¡åŠ¹
          >
            {isMuted ? 'ğŸ”‡ ãƒã‚¤ã‚¯ãƒŸãƒ¥ãƒ¼ãƒˆ' : 'ğŸ¤ ãƒã‚¤ã‚¯ON'} {/* ãƒŸãƒ¥ãƒ¼ãƒˆçŠ¶æ…‹ã«å¿œã˜ã¦ã‚¢ã‚¤ã‚³ãƒ³ã¨ãƒ†ã‚­ã‚¹ãƒˆã‚’å¤‰æ›´ */}
          </button>
          <button className="control-btn manual-btn" id="manualBtn" disabled={isAiModeOn || isMoveInProgress}>
            ğŸ“‹ ãƒãƒ‹ãƒ¥ã‚¢ãƒ«
          </button>
          <button
            className="control-btn greeting-btn"
            id="greetingBtn"
            disabled={isAiModeOn || isMoveInProgress} // AIãƒ¢ãƒ¼ãƒ‰ä¸­ã€ã¾ãŸã¯ãƒ¢ãƒ¼ã‚·ãƒ§ãƒ³ä¸­ã¯ç„¡åŠ¹
            onClick={() => handleSidePanelButtonClick('doingSomething')}
          >
            ğŸ‘‹ã‚ã„ã•ã¤
          </button>
        </div>
      </footer>
    </div>
  );
}

export default Home;
