import React, { useEffect, useRef, useState} from "react";
import { useNavigate } from 'react-router-dom';
import "./style.css"
import {
  nowInSec,
  SkyWayAuthToken,
  SkyWayContext,
  SkyWayRoom,
  SkyWayStreamFactory,
  uuidV4,
} from '@skyway-sdk/room';

/**
 * ホーム画面コンポーネント
 * @param {object} props - プロパティ
 * @param {function} props.setIsLoggedIn - ログイン状態を設定する関数
 */
function Home({setIsLoggedIn}) {

    const remoteVideoRef = useRef(null); // リモート映像を表示するためのvideo要素への参照

    const [cameraStatus, setCameraStatus] = useState("接続を待機中..."); // カメラ接続状態
    const [isConnectedToRoom, setIsConnectedToRoom] = useState(false); // SkyWayルームへの接続状態
    const [hasRemoteStream, setHasRemoteStream] = useState(false); // リモートストリームの有無

    const [skywayContext, setSkywayContext] = useState(null);
    const [skywayRoom, setSkywayRoom] = useState(null);

    // 固定のルームID
    const FIXED_ROOM_ID = 'robot-control-room-v2';

    const navigate = useNavigate();

  /**
   * ログアウトボタンクリック時のハンドラ
   */

    const handleLogout = () => {
    setIsLoggedIn(false); // ログイン状態をfalseに設定
    navigate('/'); // ログアウト後、ログインページへ遷移
    };

    //tokenの生成
    const token = new SkyWayAuthToken({
        jti: uuidV4(),
        iat: nowInSec(),
        exp: nowInSec() + 60 * 60 * 24,
        version: 3,
        scope: {
            appId: appId, //appIdの設定
            rooms: [
            {
            name: "*",
            methods: ["create", "close", "updateMetadata"],
            member: {
                name: "*",
                methods: ["publish", "subscribe", "updateMetadata"]
            }
            }],
            turn: {
            enabled: true
            }
        }
    }).encode(secret); //secretの設定

    useEffect(() => {
        let currentSkywayContext = null;
        let currentSkywayRoom = null;

        const initSkyWay = async () => {
            // SkyWay SDKのクラスがグローバルスコープで利用可能であるか確認
            if (typeof SkyWayContext === 'undefined' || typeof SkyWayRoom === 'undefined' || typeof RemoteVideoStream === 'undefined') {
                console.error("SkyWay SDKのクラスが見つかりません。HTMLでSkyWay SDKが正しく読み込まれているか確認してください。");
                setCameraStatus("SkyWay SDKが読み込まれていません。");
                return;
            }

            try {
                setCameraStatus("SkyWay Contextを初期化中...");
                const token = generateSkyWayToken();
                if (!token) {
                    setCameraStatus("SkyWayトークンの生成に失敗しました。");
                    return;
                }

                currentSkywayContext = await SkyWayContext.Create(token);
                setSkywayContext(currentSkywayContext);
                console.log("SkyWayContext initialized.");

                setCameraStatus(`ルーム '${FIXED_ROOM_ID}' に接続中...`);
                currentSkywayRoom = await SkyWayRoom.FindOrCreate(currentSkywayContext, {
                    type: 'p2p', // SFUルームタイプを使用
                    name: FIXED_ROOM_ID,
                });
                setSkywayRoom(currentSkywayRoom);
                setIsConnectedToRoom(true);
                setCameraStatus(`ルーム '${FIXED_ROOM_ID}' に接続しました。ストリームを待機中...`);

                // リモートメンバーがストリームを公開したときのイベントリスナー
                currentSkywayRoom.onStreamPublished.add(async ({ publication }) => {
                    // ビデオストリームのみを購読
                    if (publication.contentType === 'video') {
                        console.log(`リモートビデオストリームが公開されました: ${publication.id}`);
                        const { stream } = await currentSkywayRoom.subscribe(publication.id);
                        if (remoteVideoRef.current && stream instanceof RemoteVideoStream) {
                            remoteVideoRef.current.srcObject = stream;
                            setHasRemoteStream(true);
                            setCameraStatus("ロボット映像を受信中");
                            console.log("リモート映像を表示中。");
                        }
                    }
                });

                // リモートメンバーがストリームを非公開にしたときのイベントリスナー
                currentSkywayRoom.onStreamUnpublished.add(({ publication }) => {
                    if (publication.contentType === 'video') {
                        console.log(`リモートビデオストリームが非公開になりました: ${publication.id}`);
                        if (remoteVideoRef.current && remoteVideoRef.current.srcObject) {
                            remoteVideoRef.current.srcObject = null;
                            setHasRemoteStream(false);
                            setCameraStatus("ストリームが切断されました。接続を待機中...");
                            console.log("リモート映像の表示を停止しました。");
                        }
                    }
                });

                // ルームが閉じたときのイベントリスナー
                currentSkywayRoom.onClosed.add(() => {
                    console.log("ルームが閉じられました。");
                    setIsConnectedToRoom(false);
                    setSkywayRoom(null);
                    setHasRemoteStream(false);
                    if (remoteVideoRef.current) {
                        remoteVideoRef.current.srcObject = null;
                    }
                    setCameraStatus("ルームから切断されました。");
                });

            } catch (error) {
                console.error("SkyWayの初期化またはルームへの参加に失敗しました:", error);
                setCameraStatus("接続に失敗しました。");
                setIsConnectedToRoom(false);
                setHasRemoteStream(false);
            }
        };

        initSkyWay();

        // コンポーネントアンマウント時のクリーンアップ
        return () => {
            if (currentSkywayRoom) {
                currentSkywayRoom.close();
                console.log("SkyWayRoom closed on unmount.");
            }
            if (currentSkywayContext) {
                currentSkywayContext.dispose();
                console.log("SkyWayContext disposed on unmount.");
            }
            if (remoteVideoRef.current) {
                remoteVideoRef.current.srcObject = null;
            }
        };
    }, []); // 空の依存配列でコンポーネントのマウント時に一度だけ実行

    //表示部分の処理
    return(
    <>
        <meta charSet="UTF-8" />
