import React from "react";
import { useNavigate } from 'react-router-dom';
import "./style.css"
import {
  nowInSec,
  SkyWayAuthToken,
  SkyWayContext,
  SkyWayRoom,
  SkyWayStreamFactory,
  uuidV4,
} from '@skyway-sdk/room';

/**
 * „Éõ„Éº„É†ÁîªÈù¢„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
 * @param {object} props - „Éó„É≠„Éë„ÉÜ„Ç£
 * @param {function} props.setIsLoggedIn - „É≠„Ç∞„Ç§„É≥Áä∂ÊÖã„ÇíË®≠ÂÆö„Åô„ÇãÈñ¢Êï∞
 */
function Home({setIsLoggedIn}) {
    const navigate = useNavigate();

  /**
   * „É≠„Ç∞„Ç¢„Ç¶„Éà„Éú„Çø„É≥„ÇØ„É™„ÉÉ„ÇØÊôÇ„ÅÆ„Éè„É≥„Éâ„É©
   */

    const handleLogout = () => {
    setIsLoggedIn(false); // „É≠„Ç∞„Ç§„É≥Áä∂ÊÖã„Çífalse„Å´Ë®≠ÂÆö
    navigate('/'); // „É≠„Ç∞„Ç¢„Ç¶„ÉàÂæå„ÄÅ„É≠„Ç∞„Ç§„É≥„Éö„Éº„Ç∏„Å∏ÈÅ∑Áßª
    };

    //token„ÅÆÁîüÊàê
    // const token = new SkyWayAuthToken({
    //     jti: uuidV4(),
    //     iat: nowInSec(),
    //     exp: nowInSec() + 60 * 60 * 24,
    //     version: 3,
    //     scope: {
    //         appId: appId,
    //         rooms: [
    //         {
    //         name: "*",
    //         methods: ["create", "close", "updateMetadata"],
    //         member: {
    //             name: "*",
    //             methods: ["publish", "subscribe", "updateMetadata"]
    //         }
    //         }],
    //         turn: {
    //         enabled: true
    //         }
    //     }
    // }).encode(secret);

    return(
    <>
        <meta charSet="UTF-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <title>„É≠„Éú„ÉÉ„Éà„ÉÅ„É£„ÉÉ„Éà - RX78-myakumyaku</title>
        <link rel="stylesheet" href="style.css" type="text/css" />
        <div className="header">
            <div className="header-left">
            <button className="motion-btn">„É¢„Éº„Ç∑„Éß„É≥</button>
            <span className="robot-status">
                Êìç‰Ωú„Åó„Å¶„ÅÑ„Çã„É≠„Éú„ÉÉ„ÉàÔºöRX78-myakumyaku
            </span>
            </div>
            <div className="header-right">
            <button onClick={handleLogout} className="logout-btn">„É≠„Ç∞„Ç¢„Ç¶„Éà</button>
            <button className="voice-btn">„Éú„Ç§„Çπ</button>
            </div>
        </div>
        <div className="main-container">
            <div className="side-panel">
            <div className="side-panel-content">
                <button className="side-btn">„Åì„Çì„Å´„Å°„ÅØ</button>
                <button className="side-btn">„Éê„Ç§„Éê„Ç§</button>
                <button className="side-btn">„Éö„Ç≥„É™</button>
                <button className="side-btn">„Éê„Çø„Éê„Çø</button>
                <button className="side-btn">„ÅÜ„Åà„Çí„ÇÄ„Åè</button>
                <button className="side-btn">„Åó„Åü„Çí„ÇÄ„Åè</button>
                <button className="side-btn">„Åø„Åé„Çí„ÇÄ„Åè</button>
                <button className="side-btn">„Å≤„Å†„Çä„Çí„ÇÄ„Åè</button>
                <button className="side-btn">„ÅÜ„Å™„Åö„Åè</button>
                <button className="side-btn">„Åã„Åó„Åí„Çã</button>
                <button className="side-btn">„Åæ„Çè„Çã</button>
                <button className="side-btn">„Ç∏„É£„É≥„Éó</button>
                <button className="side-btn">„ÉÄ„É≥„Çπ</button>
                <button className="side-btn">„Åä„Åò„Åé</button>
                <button className="side-btn">„Åç„Çí„Å§„Åë</button>
                <button className="side-btn">‰ºë„ÇÅ</button>
            </div>
            </div>
            <div className="main-area">
            <div className="robot-display"></div>
            </div>
            <div className="side-panel right">
            <div className="side-panel-content">
                <button className="side-btn">„Åì„Çì„Å´„Å°„ÅØ</button>
                <button className="side-btn">„Å©„Åì„Åã„Çâ„Åç„Åü„ÅÆÔºü</button>
                <button className="side-btn">„Éê„Ç§„Éê„Ç§</button>
                <button className="side-btn">‚óã‚óã„Åó„Å¶„ÅÑ„Åæ„Åô„ÅãÔºü</button>
                <button className="side-btn">„ÅÇ„Çä„Åå„Å®„ÅÜ</button>
