            background-color: #dc2626;
        }
        .robot-display {
            position: relative;
            width: 100%;
            height: 100%;
            max-width: 800px;
            max-height: 600px;
            background-color: #2d3748;
            border-radius: 12px;
            overflow: hidden;
            box-shadow: 0 10px 15px rgba(0, 0, 0, 0.2);
            display: flex;
            justify-content: center;
            align-items: center;
        }
        .robot-display video.remote-video {
            width: 100%;
            height: 100%;
            object-fit: cover;
            position: absolute;
            top: 0;
            left: 0;
        }
        .bottom-controls {
            display: flex;
            justify-content: center;
            align-items: center;
            gap: 10px;
            padding: 15px;
            background-color: rgba(255, 255, 255, 0.8);
            backdrop-filter: blur(10px);
            border-top: 1px solid #ccc;
            flex-shrink: 0;
        }
        .control-btn {
            padding: 12px 24px;
            border-radius: 9999px; /* rounded-full */
            font-weight: bold;
            font-size: 1rem;
            cursor: pointer;
            transition: all 0.2s;
            border: none;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
        }
        .control-btn:disabled {
            background-color: #cbd5e0; /* gray-300 */
            color: #718096; /* gray-500 */
            cursor: not-allowed;
            box-shadow: none;
        }
        .ai-btn {
            background-color: #9333ea; /* bg-purple-600 */
            color: white;
        }
        .ai-btn:hover:not(:disabled) {
            background-color: #7e22ce; /* hover:bg-purple-700 */
            transform: translateY(-2px);
        }
        .ai-btn.ai-mode-on {
            box-shadow: 0 0 0 4px rgba(168, 85, 247, 0.5); /* ring-4 ring-purple-400 */
        }
        .mic-btn {
            background-color: #10b981; /* bg-emerald-500 */
            color: white;
        }
        .mic-btn:hover:not(:disabled) {
            background-color: #059669; /* hover:bg-emerald-600 */
            transform: translateY(-2px);
        }
        .mic-btn.muted {
            background-color: #ef4444; /* bg-red-500 */
        }
        .mic-btn.muted:hover:not(:disabled) {
            background-color: #dc2626; /* hover:bg-red-600 */
        }
        .manual-btn, .greeting-btn {
            background-color: #3b82f6; /* bg-blue-500 */
            color: white;
        }
        .manual-btn:hover:not(:disabled), .greeting-btn:hover:not(:disabled) {
            background-color: #2563eb; /* hover:bg-blue-600 */
            transform: translateY(-2px);
        }
        .ai-suggestions {
            position: absolute;
            bottom: 1rem;
            width: 91.666667%;
            background-color: rgba(0, 0, 0, 0.5);
            padding: 1rem;
            border-radius: 12px;
            display: flex;
            justify-content: center;
            gap: 1rem;
            z-index: 1;
        }
        .ai-suggestion-btn {
            flex: 1;
            padding: 1rem;
            background-color: rgba(255, 255, 255, 0.2);
            color: white;
            border: 2px solid white;
            border-radius: 9999px;
            font-size: 1rem;
            font-weight: bold;
            cursor: pointer;
            backdrop-filter: blur(5px);
            transition: all 0.2s;
        }
        .ai-suggestion-btn:hover:not(:disabled) {
            background-color: rgba(255, 255, 255, 0.4);
            transform: translateY(-2px);
        }
        .ai-suggestion-btn:disabled {
            background-color: rgba(255, 255, 255, 0.1);
            border-color: rgba(255, 255, 255, 0.3);
            color: #ddd;
            cursor: not-allowed;
        }
        @media (max-width: 768px) {
            .side-panel {
                width: 80px;
                padding: 10px 5px;
            }
            .side-btn {
                padding: 8px 5px;
                font-size: 12px;
            }
            .header {
                flex-direction: column;
                gap: 10px;
                padding: 10px;
            }
            .header-left, .header-right {
                width: 100%;
                justify-content: center;
                margin: 0;
            }
            .robot-status {
                margin-left: 10px;
                font-size: 0.9rem;
            }
            .bottom-controls {
                flex-wrap: wrap;
                gap: 5px;
            }
            .control-btn {
                padding: 8px 12px;
                font-size: 0.8rem;
            }
        }
