import React, { useEffect, useMemo, useRef, useState } from "react";
import {
  nowInSec,
  SkyWayAuthToken,
  SkyWayContext,
  SkyWayRoom,
  SkyWayStreamFactory,
  uuidV4,
  RemoteDataStream,
  RemoteVideoStream,
  RemoteAudioStream,
} from '@skyway-sdk/room';

/**
 * ホーム画面コンポーネント
 * @param {object} props - プロパティ
 * @param {function} props.setIsLoggedIn - ログイン状態を設定する関数
 */
function Home({ setIsLoggedIn }) {
  // ルーム名
  const [roomName, setRoomName] = useState("");
  // 自分の参加者情報 (ルームに参加すると設定されます)
  const [me, setMe] = useState(null);
  // SkyWayコンテキストのインスタンス
  const [skywayContext, setSkywayContext] = useState(null);
  // SkyWayルームのインスタンス
  const [skywayRoom, setSkywayRoom] = useState(null);
  // リモートビデオ要素を管理するためのMap (memberId -> videoElement)
  const remoteVideoElements = useRef(new Map());
  // リモートビデオを表示するDOM要素への参照
  const robotDisplayRef = useRef(null);
  // ローカルストリームの参照を保持
  const localStreamRef = useRef(null);
  // 受信したメッセージを表示するための状態
  const [receivedMessages, setReceivedMessages] = useState([]);
  // 送信したデータストリームの参照を保持
  const localDataStreamRef = useRef(null);
  // マイクの状態を表示するための状態 (ミュート状態を管理)
  const [isMuted, setIsMuted] = useState(false);
  // オーディオストリームのPublicationを保持するための参照
  const audioPublicationRef = useRef(null);
  // 現在の言語状態 ('japanese' または 'english')
  const [currentLanguage, setCurrentLanguage] = useState('japanese');
  // AIモードがONかどうかを管理する状態
  const [isAiModeOn, setIsAiModeOn] = useState(false);
  // AIからの提案を保持する状態
  const [aiSuggestions, setAiSuggestions] = useState([null, null]);
  // モーションが進行中かどうかを管理する状態
  const [isMoveInProgress, setIsMoveInProgress] = useState(false);

  // メッセージとモーションの多言語マッピング
  const messages = useMemo(() => ({
    hello: { japanese: 'リセット', english: 'Hello' },
    goodbye: { japanese: '決めポーズ', english: 'Goodbye' },
    bow: { japanese: 'バイバイ', english: 'Bow' },
    flap: { japanese: 'YES', english: 'Flap' },
    lookUp: { japanese: 'NO', english: 'Look up' },
    lookDown: { japanese: '右手を上げる', english: 'Look down' },
    lookRight: { japanese: '右手を下げる', english: 'Look right' },
    lookLeft: { japanese: '左手を上げる', english: 'Left hand up' },
    nod: { japanese: '左手を下げる', english: 'Left hand down' },
    tiltHead: { japanese: '上を向く', english: 'Look up' },
    spin: { japanese: '下を向く', english: 'Look down' },
    jump: { japanese: '右を向く', english: 'Look right' },
    dance: { japanese: '左を向く', english: 'Look left' },
    bowDeep: { japanese: 'パタパタ', english: 'Flap arms' },
    doingSomething: { japanese: 'こんにちは', english: 'Hello' },
    thankYou: { japanese: 'バイバイ', english: 'Goodbye' },
    good: { japanese: 'ようこそ', english: 'Welcome' },
    ookini: { japanese: 'ありがとう', english: 'Thank you (Kansai dialect)' },
    amazing: { japanese: '楽しんでね', english: 'Enjoy' }
  }), []);


  // SkyWay App ID と Secret Key
  const appId = useMemo(() => "4490dcb5-dbe8-4028-ba0a-6d0d143e4515", []);
  const secretKey = useMemo(() => "40HHGEIOx0BRByPdxqwUswK0a+7v2JaeaJ9CcFjMdAQ=", []);

  // SkyWay認証トークンの生成
  const token = useMemo(() => {
    if (appId == null || secretKey == null) return undefined;
    return new SkyWayAuthToken({
      jti: uuidV4(),
      iat: nowInSec(),
      exp: nowInSec() + 60 * 60 * 24,
      version: 3,
      scope: {
        appId: appId,
        rooms: [{
          name: "*",
          methods: ["create", "close", "updateMetadata"],
          member: {
            name: "*",
            methods: ["publish", "subscribe", "updateMetadata"]
          }
        }],
        turn: { enabled: true }
      }
    }).encode(secretKey);
  }, [appId, secretKey]);

  /**
   * SkyWayContextの初期化とクリーンアップを行うuseEffect
   */
  useEffect(() => {
    if (!token) return;
    let currentContext;
    const initContext = async () => {
      try {
        currentContext = await SkyWayContext.Create(token);
        setSkywayContext(currentContext);
        console.log("SkyWayContext created successfully.");
      } catch (error) {
        console.error("Failed to create SkyWayContext:", error);
      }
    };
    initContext();
    return () => {
      if (currentContext) {
        currentContext.dispose();
        console.log("SkyWayContext disposed.");
      }
    };
  }, [token]);

  /**
   * リモートストリームをDOMに追加するヘルパー関数
   * @param {RemoteVideoStream | RemoteAudioStream} stream - 追加するメディアストリーム
   * @param {string} memberId - ストリームのパブリッシャーのメンバーID
   */
  const attachRemoteStream = (stream, memberId) => {
    if (robotDisplayRef.current && (stream instanceof RemoteVideoStream || stream instanceof RemoteAudioStream)) {
      // 既存のビデオ要素があれば削除
      const existingVideo = remoteVideoElements.current.get(memberId);
      if (existingVideo) {
        // 既存のビデオ要素のトラックを停止し、srcObjectをクリア
        if (existingVideo.srcObject) {
            existingVideo.srcObject.getTracks().forEach(track => track.stop());
            existingVideo.srcObject = null;
        }
        robotDisplayRef.current.removeChild(existingVideo);
        remoteVideoElements.current.delete(memberId);
        console.log(`Removed existing video for member ${memberId}.`);
      }

      const video = document.createElement("video");
      video.autoplay = true;
      video.playsInline = true;
      video.className = "remote-video"; // スタイリング用のクラス
      video.dataset.memberId = memberId; // メンバーIDをデータ属性に保存
      stream.attach(video); // ストリームをビデオ要素のソースに設定
      robotDisplayRef.current.appendChild(video);
      remoteVideoElements.current.set(memberId, video);
      console.log(`Attached stream from ${memberId} to DOM. Stream type: ${stream instanceof RemoteVideoStream ? 'video' : 'audio'}.`);
    } else {
      console.warn(`Attempted to attach non-media stream or robotDisplayRef is null for member ${memberId}. Stream type: ${stream.constructor.name}.`);
    }
  };

  /**
   * データストリームの受信を処理するヘルパー関数
   * @param {RemoteDataStream} stream - 受信するデータストリーム
   * @param {string} memberId - ストリームのパブリッシャーのメンバーID
   */
  const handleDataStream = (stream, memberId) => {
    if (stream instanceof RemoteDataStream) {
      stream.onData.add((data) => {
        console.log(`Received data from ${memberId}:`, data);
        setReceivedMessages(prevMessages => [...prevMessages, { senderId: memberId, message: data }]);

        if (data === "move_finish") {
          console.log("Received 'move_finish' signal. Enabling side panel buttons.");
          setIsMoveInProgress(false);
        }

        const aiCombinedSuggestionRegex = /^ai_assist_1:(.*):ai_assist_2:(.*)$/;
        const combinedMatch = String(data).match(aiCombinedSuggestionRegex);

        if (combinedMatch) {
          const suggestion1Content = combinedMatch[1];
          const suggestion2Content = combinedMatch[2];
          setAiSuggestions([
            { key: 'ai_assist_1', text: suggestion1Content },
            { key: 'ai_assist_2', text: suggestion2Content }
          ]);
          console.log("Received combined AI suggestions.");
        } else {
          const aiSingleSuggestionRegex = /^ai_assist_(\d+):(.*)$/;
          const singleMatch = String(data).match(aiSingleSuggestionRegex);
          if (singleMatch) {
            const suggestionNumber = parseInt(singleMatch[1], 10);
            const suggestionContent = singleMatch[2];
            if (suggestionNumber === 1 || suggestionNumber === 2) {
              setAiSuggestions(prevSuggestions => {
                const newSuggestions = [...prevSuggestions];
                newSuggestions[suggestionNumber - 1] = {
                  key: `ai_assist_${suggestionNumber}`,
                  text: suggestionContent
                };
                return newSuggestions;
              });
              console.log(`Received single AI suggestion ${suggestionNumber}.`);
            }
          }
        }
      });
      console.log(`Listening for data from ${memberId}.`);
    } else {
      console.warn(`Attempted to handle non-data stream as data stream for member ${memberId}.`);
    }
  };

  /**
   * ルームに参加するハンドラ
   */
  const handleJoinRoom = async () => {
    if (!skywayContext || !roomName || (skywayRoom && me)) {
      console.error("Cannot join room: context not ready, room name is empty, or already in a room.");
      return;
    }

    try {
      const room = await SkyWayRoom.FindOrCreate(skywayContext, { type: "p2p", name: roomName });
      if (!room) throw new Error("Failed to create or find SkyWayRoom.");
      setSkywayRoom(room);
      console.log(`SkyWayRoom '${roomName}' found or created.`);

      const localMember = await room.join();
      if (!localMember) throw new Error("Failed to join the room.");
      setMe(localMember);
      console.log(`Joined room '${roomName}' as member ID: ${localMember.id}`);

      // ローカルメディアストリームを作成
      const streams = await SkyWayStreamFactory.createMicrophoneAudioAndCameraStream();
      localStreamRef.current = streams.video;
      await localMember.publish(streams.video);
      const publication_audio = await localMember.publish(streams.audio);
      audioPublicationRef.current = publication_audio;
      setIsMuted(false);
      console.log("Published local camera and microphone stream.");

      // ローカルデータストリームを作成
      const dataStream = await SkyWayStreamFactory.createDataStream();
      localDataStreamRef.current = dataStream;
      await localMember.publish(dataStream);
      console.log("Published local data stream.");

      setIsMoveInProgress(false);

      // 既存の公開ストリームを購読
      for (const publication of room.publications) {
        if (localMember.id === publication.publisher.id) continue;
        try {
          const { stream } = await localMember.subscribe(publication.id);
          console.log(`Subscribed to existing stream from ${publication.publisher.id}, type: ${publication.contentType}`);
          if (publication.contentType === 'video' || publication.contentType === 'audio') {
            attachRemoteStream(stream, publication.publisher.id);
          } else if (publication.contentType === 'data') {
            handleDataStream(stream, publication.publisher.id);
          }
        } catch (subscribeError) {
          console.error(`Failed to subscribe to existing stream from ${publication.publisher.id}:`, subscribeError);
        }
      }

      // ルームイベントリスナーの設定
      room.onMemberJoined.add((e) => {
        console.log(`Member joined: ${e.member.id}`);
      });
      room.onMemberLeft.add((e) => {
        console.log(`Member left: ${e.member.id}`);
        const videoElement = remoteVideoElements.current.get(e.member.id);
        if (videoElement && robotDisplayRef.current) {
          if (videoElement.srcObject) {
              videoElement.srcObject.getTracks().forEach(track => track.stop());
              videoElement.srcObject = null;
          }
          robotDisplayRef.current.removeChild(videoElement);
          remoteVideoElements.current.delete(e.member.id);
          console.log(`Removed video for member ${e.member.id}`);
        }
      });
      room.onStreamPublished.add(async (e) => {
        console.log(`Stream published by ${e.publication.publisher.id}, type: ${e.publication.contentType}`);
        if (localMember.id !== e.publication.publisher.id) {
          try {
            const { stream } = await localMember.subscribe(e.publication.id);
            console.log(`Subscribed to stream from ${e.publication.publisher.id}`);
            if (e.publication.contentType === 'video' || e.publication.contentType === 'audio') {
              attachRemoteStream(stream, e.publication.publisher.id);
            } else if (e.publication.contentType === 'data') {
              handleDataStream(stream, e.publication.publisher.id);
            }
          } catch (subscribeError) {
            console.error(`Failed to subscribe to stream from ${e.publication.publisher.id}:`, subscribeError);
          }
        }
      });
      room.onStreamUnpublished.add((e) => {
        console.log(`Stream unpublished by ${e.publication.publisher.id}`);
        const videoElement = remoteVideoElements.current.get(e.publication.publisher.id);
        if (videoElement && robotDisplayRef.current) {
          if (videoElement.srcObject) {
              videoElement.srcObject.getTracks().forEach(track => track.stop());
              videoElement.srcObject = null;
          }
          robotDisplayRef.current.removeChild(videoElement);
          remoteVideoElements.current.delete(e.publication.publisher.id);
          console.log(`Removed unpublished video for member ${e.publication.publisher.id}`);
        }
      });

    } catch (error) {
      console.error("Failed to join room:", error);
      // エラー発生時に状態をリセットし、クリーンアップ
      setSkywayRoom(null);
      setMe(null);
      if (robotDisplayRef.current) {
        Array.from(robotDisplayRef.current.children).forEach(child => {
          if (child.tagName === 'VIDEO') {
            if (child.srcObject && typeof child.srcObject.getTracks === 'function') {
              child.srcObject.getTracks().forEach(track => track.stop());
              child.srcObject = null;
            }
            child.remove();
          }
        });
      }
      remoteVideoElements.current.clear();
      if (localStreamRef.current) {
        localStreamRef.current.stop();
        localStreamRef.current = null;
      }
      localDataStreamRef.current = null;
      setIsMuted(false);
      audioPublicationRef.current = null;
      setIsMoveInProgress(false);
    }
  };

  /**
   * ルームから退出するハンドラ
   */
  const handleLeaveRoom = async () => {
    if (skywayRoom && me) {
      try {
        if (localStreamRef.current) {
          localStreamRef.current.stop();
          localStreamRef.current = null;
        }
        localDataStreamRef.current = null;
        audioPublicationRef.current = null;
        setIsMuted(false);

        await me.leave();
        console.log(`Left room '${roomName}'`);

        if (skywayRoom) {
          await skywayRoom.dispose();
          console.log("SkyWay Room disposed.");
        }
      } catch (error) {
          console.error("Failed to leave room:", error);
      } finally {
        setMe(null);
        setSkywayRoom(null);
        setReceivedMessages([]);
        if (robotDisplayRef.current) {
          Array.from(robotDisplayRef.current.children).forEach(child => {
            if (child.tagName === 'VIDEO') {
              if (child.srcObject && typeof child.srcObject.getTracks === 'function') {
                child.srcObject.getTracks().forEach(track => track.stop());
                child.srcObject = null;
              }
              child.remove();
            }
          });
        }
        remoteVideoElements.current.clear();
        setIsAiModeOn(false);
        setAiSuggestions([null, null]);
        setIsMoveInProgress(false);
      }
    }
  };

  /**
   * モーションを操作するボタンを押した時の処理
   */
  const handleSend = async (messageOrKey) => {
    if (me && skywayRoom && localDataStreamRef.current) {
      try {
        let messageToSend;
        let shouldSetMoveInProgress = false;

        if (typeof messageOrKey === 'string') {
          if (messageOrKey.startsWith("template_motion_")) {
            messageToSend = messageOrKey;
            shouldSetMoveInProgress = true;
          } else if (messageOrKey.startsWith("template_voice_")) {
            messageToSend = messageOrKey;
          } else {
            messageToSend = messages[messageOrKey]?.[currentLanguage];
          }
        } else if (typeof messageOrKey === 'object' && messageOrKey.key) {
          messageToSend = messageOrKey.key;
        }

        if (!messageToSend) {
          console.warn(`Message to send is empty or not found.`);
          return;
        }

        localDataStreamRef.current.write(messageToSend);
        console.log(`Sent message: "${messageToSend}"`);
        setReceivedMessages(prevMessages => [...prevMessages, { senderId: me.id, message: messageToSend, isLocal: true }]);
        setAiSuggestions([null, null]);

        if (shouldSetMoveInProgress) {
          setIsMoveInProgress(true);
        }

      } catch (error) {
        console.error("Failed to send data:", error);
      }
    } else {
      console.warn("Cannot send message: Not in a room, 'me' object is not available, or data stream is not ready.");
    }
  };

  /**
   * マイクのミュート/ミュート解除を切り替えるハンドラ
   */
  const handleMute = async () => {
    if (!audioPublicationRef.current) {
      console.warn("Audio publication not available. Cannot toggle mute.");
      return;
    }
    try {
      if (isMuted) {
        await audioPublicationRef.current.enable();
        console.log("Microphone unmuted.");
      } else {
        await audioPublicationRef.current.disable();
        console.log("Microphone muted.");
      }
      setIsMuted(!isMuted);
    } catch (error) {
      console.error("Failed to toggle microphone mute state:", error);
    }
  };

  const isInitialAiModeRender = useRef(true);

  /**
   * AIモードの状態変更を監視し、シグナルを送信するuseEffect
   */
  useEffect(() => {
    if (isInitialAiModeRender.current) {
      isInitialAiModeRender.current = false;
      return;
    }
    if (!me || !localDataStreamRef.current) {
      return;
    }
    if (isAiModeOn) {
      localDataStreamRef.current.write("ai_assist_on");
      console.log("Sent AI_MODE_REQUEST_SUGGESTIONS signal.");
      setAiSuggestions([null, null]);
    } else {
      localDataStreamRef.current.write("ai_assist_off");
      console.log("Sent AI_MODE_OFF_SIGNAL.");
      setAiSuggestions([null, null]);
    }
  }, [isAiModeOn, me, localDataStreamRef]);

  /**
   * 言語を切り替えるハンドラ
   */
  const handleToggleLanguage = () => {
    setCurrentLanguage(prevLang => (prevLang === 'japanese' ? 'english' : 'japanese'));
  };

  /**
   * AIモードを切り替えるハンドラ
   */
  const handleAiModeToggle = () => {
    setIsAiModeOn(prev => !prev);
  };


  return (
    <div className="app-container">
      <style>{`
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }
        html, body, #root {
            height: 100%;
            width: 100%;
            overflow: hidden;
        }
        body {
            font-family: 'Hiragino Sans', 'Yu Gothic', sans-serif;
            background: linear-gradient(135deg, #87CEEB 0%, #B0E0E6 50%, #E0F6FF 100%);
            height: 100vh;
            display: flex;
            flex-direction: column;
            overflow: hidden;
        }
        .app-container {
            display: flex;
            flex-direction: column;
            height: 100vh;
            width: 100%;
            overflow: hidden;
        }
        .header {
            background: linear-gradient(135deg, #8B7EC8 0%, #6A5ACD 100%);
            color: white;
            padding: 15px 20px;
            display: flex;
            justify-content: space-between;
            align-items: center;
            flex-shrink: 0;
            z-index: 10;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
        }
        .header-left, .header-right {
            display: flex;
            align-items: center;
        }
        .robot-status {
            margin-left: 20px;
            font-weight: bold;
            font-size: 1.1rem;
        }
        .logout-btn {
            background-color: #ef4444; /* bg-red-500 */
            color: white;
            padding: 8px 16px;
            border-radius: 8px; /* rounded-lg */
            font-weight: bold;
            cursor: pointer;
            transition: all 0.2s;
            border: none;
        }
        .logout-btn:hover {
            background-color: #dc2626; /* hover:bg-red-600 */
        }
        .motion-btn, .voice-btn {
            background-color: #6366f1; /* bg-indigo-500 */
            color: white;
            padding: 8px 16px;
            border-radius: 8px; /* rounded-lg */
            font-weight: bold;
            cursor: pointer;
            transition: all 0.2s;
            border: none;
            margin-right: 10px;
        }
        .motion-btn:hover, .voice-btn:hover {
            background-color: #4f46e5; /* hover:bg-indigo-600 */
        }
        .main-container {
            display: flex;
            flex: 1;
            overflow: hidden;
        }
        .side-panel {
            width: 120px;
            background-color: rgba(255, 255, 255, 0.8);
            backdrop-filter: blur(10px);
            padding: 15px;
            display: flex;
            flex-direction: column;
            flex-shrink: 0;
            overflow-y: auto;
            scrollbar-width: none;
        }
        .side-panel::-webkit-scrollbar {
            display: none;
        }
        .side-panel.right {
            align-items: flex-end;
        }
        .side-panel-content {
            display: flex;
            flex-direction: column;
            gap: 10px;
            width: 100%;
        }
        .side-btn {
            width: 100%;
            padding: 10px;
            background-color: #4a90e2;
            color: white;
            border: none;
            border-radius: 8px;
            cursor: pointer;
            font-size: 14px;
            font-weight: bold;
            transition: transform 0.1s ease-in-out, background-color 0.2s;
        }
        .side-btn:hover:not(:disabled) {
            background-color: #357bd8;
            transform: translateY(-2px);
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
        }
        .side-btn:disabled {
            background-color: #a0aec0;
            cursor: not-allowed;
        }
        .main-area {
            flex: 1;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            position: relative;
            padding: 20px;
            overflow: hidden;
        }
        .room-controls {
            display: flex;
            gap: 10px;
            margin-bottom: 20px;
            width: 100%;
            justify-content: center;
            flex-shrink: 0;
        }
        .room-input {
            padding: 10px;
            border: 2px solid #ddd;
            border-radius: 8px;
            font-size: 1rem;
            flex: 1;
            max-width: 300px;
        }
        .join-room-btn, .leave-room-btn {
            padding: 10px 20px;
            border: none;
            border-radius: 8px;
            font-size: 1rem;
            font-weight: bold;
            cursor: pointer;
            transition: background-color 0.2s;
        }
        .join-room-btn {
            background-color: #22c55e;
            color: white;
        }
        .join-room-btn:hover:not(:disabled) {
            background-color: #16a34a;
        }
        .leave-room-btn {
            background-color: #ef4444;
            color: white;
        }
        .leave-room-btn:hover:not(:disabled) {
            background-color: #dc2626;
        }
        .robot-display {
            position: relative;
            width: 100%;
            height: 100%;
            max-width: 800px;
            max-height: 600px;
            background-color: #2d3748;
            border-radius: 12px;
            overflow: hidden;
            box-shadow: 0 10px 15px rgba(0, 0, 0, 0.2);
            display: flex;
            justify-content: center;
            align-items: center;
        }
        .robot-display video.remote-video {
            width: 100%;
            height: 100%;
            object-fit: cover;
            position: absolute;
            top: 0;
            left: 0;
        }
        .bottom-controls {
            display: flex;
            justify-content: center;
            align-items: center;
            gap: 10px;
            padding: 15px;
            background-color: rgba(255, 255, 255, 0.8);
            backdrop-filter: blur(10px);
            border-top: 1px solid #ccc;
            flex-shrink: 0;
        }
        .control-btn {
            padding: 12px 24px;
            border-radius: 9999px; /* rounded-full */
            font-weight: bold;
            font-size: 1rem;
            cursor: pointer;
            transition: all 0.2s;
            border: none;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
        }
        .control-btn:disabled {
            background-color: #cbd5e0; /* gray-300 */
            color: #718096; /* gray-500 */
            cursor: not-allowed;
            box-shadow: none;
        }
        .ai-btn {
            background-color: #9333ea; /* bg-purple-600 */
            color: white;
        }
        .ai-btn:hover:not(:disabled) {
            background-color: #7e22ce; /* hover:bg-purple-700 */
            transform: translateY(-2px);
        }
        .ai-btn.ai-mode-on {
            box-shadow: 0 0 0 4px rgba(168, 85, 247, 0.5); /* ring-4 ring-purple-400 */
        }
        .mic-btn {
            background-color: #10b981; /* bg-emerald-500 */
            color: white;
        }
        .mic-btn:hover:not(:disabled) {
            background-color: #059669; /* hover:bg-emerald-600 */
            transform: translateY(-2px);
        }
        .mic-btn.muted {
            background-color: #ef4444; /* bg-red-500 */
        }
        .mic-btn.muted:hover:not(:disabled) {
            background-color: #dc2626; /* hover:bg-red-600 */
        }
        .manual-btn, .greeting-btn {
            background-color: #3b82f6; /* bg-blue-500 */
            color: white;
        }
        .manual-btn:hover:not(:disabled), .greeting-btn:hover:not(:disabled) {
            background-color: #2563eb; /* hover:bg-blue-600 */
            transform: translateY(-2px);
        }
        .ai-suggestions {
            position: absolute;
            bottom: 1rem;
            width: 91.666667%;
            background-color: rgba(0, 0, 0, 0.5);
            padding: 1rem;
            border-radius: 12px;
            display: flex;
            justify-content: center;
            gap: 1rem;
            z-index: 1;
        }
        .ai-suggestion-btn {
            flex: 1;
            padding: 1rem;
            background-color: rgba(255, 255, 255, 0.2);
            color: white;
            border: 2px solid white;
            border-radius: 9999px;
            font-size: 1rem;
            font-weight: bold;
            cursor: pointer;
            backdrop-filter: blur(5px);
            transition: all 0.2s;
        }
        .ai-suggestion-btn:hover:not(:disabled) {
            background-color: rgba(255, 255, 255, 0.4);
            transform: translateY(-2px);
        }
        .ai-suggestion-btn:disabled {
            background-color: rgba(255, 255, 255, 0.1);
            border-color: rgba(255, 255, 255, 0.3);
            color: #ddd;
            cursor: not-allowed;
        }
        @media (max-width: 768px) {
            .side-panel {
                width: 80px;
                padding: 10px 5px;
            }
            .side-btn {
                padding: 8px 5px;
                font-size: 12px;
            }
            .header {
                flex-direction: column;
                gap: 10px;
                padding: 10px;
            }
            .header-left, .header-right {
                width: 100%;
                justify-content: center;
                margin: 0;
            }
            .robot-status {
                margin-left: 10px;
                font-size: 0.9rem;
            }
            .bottom-controls {
                flex-wrap: wrap;
                gap: 5px;
            }
            .control-btn {
                padding: 8px 12px;
                font-size: 0.8rem;
            }
        }
      `}</style>
      <div className="header">
        <div className="header-left">
          <button className="motion-btn" disabled={isAiModeOn || isMoveInProgress}>モーション</button>
          <span className="robot-status">
            操作しているロボット：RX78-myakumyaku
          </span>
        </div>
        <div className="header-right">
          <button onClick={handleLogout} className="logout-btn">ログアウト</button>
          <button className="voice-btn" disabled={isAiModeOn || isMoveInProgress}>ボイス</button>
        </div>
      </div>
      <div className="main-container">
        <div className="side-panel">
          <div className="side-panel-content">
            {Object.keys(messages).slice(0, 14).map(key => (
              <button
                key={key}
                onClick={() => handleSend(`template_motion_${messages[key].japanese}`)}
                className="side-btn"
                disabled={isAiModeOn || !me || isMoveInProgress}
              >
                {messages[key].japanese}
              </button>
            ))}
          </div>
        </div>
        <div className="main-area">
          <div className="room-controls">
            <input
              type="text"
              placeholder="ルーム名を入力"
              value={roomName}
              onChange={(e) => setRoomName(e.target.value)}
              className="room-input"
              disabled={!!me}
            />
            {!me ? (
              <button onClick={handleJoinRoom} className="join-room-btn" disabled={!skywayContext || !roomName}>
                ルームに参加
              </button>
            ) : (
              <button onClick={handleLeaveRoom} className="leave-room-btn">
                ルームを退出
              </button>
            )}
          </div>
          <div className="robot-display" ref={robotDisplayRef}>
            {(aiSuggestions[0] || aiSuggestions[1]) && (
              <div className="ai-suggestions">
                {aiSuggestions[0] && (
                  <button
                    onClick={() => handleSend(aiSuggestions[0])}
                    className="ai-suggestion-btn"
                    disabled={isMoveInProgress}
                  >
                    {aiSuggestions[0].text}
                  </button>
                )}
                {aiSuggestions[1] && (
                  <button
                    onClick={() => handleSend(aiSuggestions[1])}
                    className="ai-suggestion-btn"
                    disabled={isMoveInProgress}
                  >
                    {aiSuggestions[1].text}
                  </button>
                )}
              </div>
            )}
          </div>
        </div>
        <div className="side-panel right">
          <div className="side-panel-content">
            {Object.keys(messages).slice(14).map(key => (
              <button
                key={key}
                onClick={() => handleSend(`template_voice_${messages[key].japanese}`)}
                className="side-btn"
                disabled={isAiModeOn || !me || isMoveInProgress}
              >
                {messages[key].japanese}
              </button>
            ))}
          </div>
        </div>
      </div>
      <div className="bottom-controls">
        <button
          className={`control-btn ai-btn ${isAiModeOn ? 'ai-mode-on' : ''}`}
          onClick={handleAiModeToggle}
          disabled={!me}
        >
          🔁 リセット {isAiModeOn ? 'ON' : 'OFF'}
        </button>
        <button
          className={`control-btn ai-btn ${isAiModeOn ? 'ai-mode-on' : ''}`}
          onClick={handleAiModeToggle}
          disabled={!me}
        >
          🤖 AIモード {isAiModeOn ? 'ON' : 'OFF'}
        </button>
        <button
          className={`control-btn mic-btn ${isMuted ? 'muted' : ''}`}
          onClick={handleMute}
          id="micBtn"
          disabled={!me || !audioPublicationRef.current || isAiModeOn}
        >
          {isMuted ? '🔇 マイクミュート' : '🎤 マイクON'}
        </button>
        <button className="control-btn manual-btn" id="manualBtn" disabled={isAiModeOn || isMoveInProgress}>
          📋 マニュアル
        </button>
        <button
          className="control-btn greeting-btn"
          id="languageBtn"
          onClick={handleToggleLanguage}
          disabled={isAiModeOn || isMoveInProgress}
        >
          👋 {currentLanguage === 'japanese' ? 'にほんご' : 'English'}
        </button>
      </div>
    </div>
  );
}

export default Home;
