import React, { useEffect, useRef, useState} from "react";
import { useNavigate } from 'react-router-dom';
import "./style.css"
import {
  nowInSec,
  SkyWayAuthToken,
  SkyWayContext,
  SkyWayRoom,
  //SkyWayStreamFactory,
  uuidV4,
  RemoteVideoStream
} from '@skyway-sdk/room';

/**
 * ホーム画面コンポーネント
 * @param {object} props - プロパティ
 * @param {function} props.setIsLoggedIn - ログイン状態を設定する関数
 */
function Home({setIsLoggedIn}) {

    const remoteVideoRef = useRef(null); // リモート映像を表示するためのvideo要素への参照

    const [cameraStatus, setCameraStatus] = useState("接続を待機中..."); // カメラ接続状態
    const [isConnectedToRoom, setIsConnectedToRoom] = useState(false); // SkyWayルームへの接続状態
    const [hasRemoteStream, setHasRemoteStream] = useState(false); // リモートストリームの有無

    const [skywayContext, setSkywayContext] = useState(null);
    const [skywayRoom, setSkywayRoom] = useState(null);

    // 固定のルームID
    const FIXED_ROOM_ID = 'robot-control-room-v2';

    const navigate = useNavigate();

  /**
   * ログアウトボタンクリック時のハンドラ
   */

    const handleLogout = () => {
        // ルームから切断し、リソースを解放
        if (skywayRoom) {
            skywayRoom.close();
        }
        if (skywayContext) {
            skywayContext.dispose();
        }
        setIsLoggedIn(false); // ログイン状態をfalseに設定
    };


    //tokenの生成
    const token = new SkyWayAuthToken({
        jti: uuidV4(),
        iat: nowInSec(),
        exp: nowInSec() + 60 * 60 * 24,
        version: 3,
        scope: {
            appId: "4490dcb5-dbe8-4028-ba0a-6d0d143e4515", //appIdの設定
            rooms: [
            {
            name: "*",
            methods: ["create", "close", "updateMetadata"],
            member: {
                name: "*",
                methods: ["publish", "subscribe", "updateMetadata"]
            }
            }],
            turn: {
            enabled: true
            }
        }
    }).encode("40HHGEIOx0BRByPdxqwUswK0a+7v2JaeaJ9CcFjMdAQ="); //secretの設定

    //ストリーミング画面の配信処理
    useEffect(() => {
        let currentSkywayContext = null;
        let currentSkywayRoom = null;

        const initSkyWay = async () => {
            // SkyWay SDKのクラスがグローバルスコープで利用可能であるか確認
            if (typeof SkyWayContext === 'undefined' || typeof SkyWayRoom === 'undefined' || typeof RemoteVideoStream === 'undefined') {
                console.error("SkyWay SDKのクラスが見つかりません。HTMLでSkyWay SDKが正しく読み込まれているか確認してください。");
                setCameraStatus("SkyWay SDKが読み込まれていません。");
                return;
            }

            try {
                setCameraStatus("SkyWay Contextを初期化中...");
                const token = token;
                if (!token) {
                    setCameraStatus("SkyWayトークンの生成に失敗しました。");
                    return;
                }

                currentSkywayContext = await SkyWayContext.Create(token);
                setSkywayContext(currentSkywayContext);
                console.log("SkyWayContext initialized.");

                setCameraStatus(`ルーム '${FIXED_ROOM_ID}' に接続中...`);
                currentSkywayRoom = await SkyWayRoom.FindOrCreate(currentSkywayContext, {
                    type: 'p2p', // P2Pルームタイプを使用
                    name: FIXED_ROOM_ID,
                });
                setSkywayRoom(currentSkywayRoom);
                setIsConnectedToRoom(true);
                setCameraStatus(`ルーム '${FIXED_ROOM_ID}' に接続しました。ストリームを待機中...`);

                // リモートメンバーがルームに参加したときのイベントリスナー
                currentSkywayRoom.onMemberJoined.add(async ({ member }) => {
                    console.log(`メンバーが参加しました: ${member.id}`);
                    // 参加したメンバーが既に公開しているストリームを購読
                    member.publications.forEach(async (publication) => {
                        // ビデオストリームのみを購読
                        if (publication.contentType === 'video') {
                            console.log(`参加メンバー ${member.id} がビデオストリームを公開済み: ${publication.id}`);
                            const { stream } = await currentSkywayRoom.subscribe(publication.id);
                            if (remoteVideoRef.current && stream instanceof RemoteVideoStream) {
                                remoteVideoRef.current.srcObject = stream;
                                setHasRemoteStream(true);
                                setCameraStatus("ロボット映像を受信中");
                                console.log("リモート映像を表示中。");
                            }
                        }
                    });
                });

                // リモートメンバーがストリームを公開したときのイベントリスナー (後から公開される場合のため)
                currentSkywayRoom.onStreamPublished.add(async ({ publication }) => {
                    // 自身の公開ストリームは購読しない（このアプリでは公開しないが念のため）
                    if (publication.origin.id === currentSkywayRoom.localPerson.id) {
                        return;
                    }
                    // ビデオストリームのみを購読
                    if (publication.contentType === 'video') {
                        console.log(`リモートビデオストリームが公開されました: ${publication.id}`);
                        const { stream } = await currentSkywayRoom.subscribe(publication.id);
                        if (remoteVideoRef.current && stream instanceof RemoteVideoStream) {
                            remoteVideoRef.current.srcObject = stream;
                            setHasRemoteStream(true);
                            setCameraStatus("ロボット映像を受信中");
                            console.log("リモート映像を表示中。");
                        }
                    }
                });

                // リモートメンバーがストリームを非公開にしたときのイベントリスナー
                currentSkywayRoom.onStreamUnpublished.add(({ publication }) => {
                    if (publication.contentType === 'video') {
                        console.log(`リモートビデオストリームが非公開になりました: ${publication.id}`);
                        // 現在表示しているのが非公開になったストリームであるかを確認し、停止する
                        // シンプル化のため、ここでは単にsrcObjectをnullに設定します
                        if (remoteVideoRef.current && remoteVideoRef.current.srcObject) {
                             remoteVideoRef.current.srcObject = null;
                        }
                        setHasRemoteStream(false);
                        setCameraStatus("ストリームが切断されました。接続を待機中...");
                        console.log("リモート映像の表示を停止しました。");
                    }
                });

                // メンバーがルームから退出したときのイベントリスナー
                currentSkywayRoom.onMemberLeft.add(({ member }) => {
                    console.log(`メンバーが退出しました: ${member.id}`);
                    // P2Pでは、メンバーが退出すると通常そのメンバーのストリームも自動的に切断される
                    // onStreamUnpublished が発火するはずだが、念のためリモート映像をクリアするロジックを維持
                    if (remoteVideoRef.current && remoteVideoRef.current.srcObject) {
                        remoteVideoRef.current.srcObject = null;
                        setHasRemoteStream(false);
                        setCameraStatus("ストリームが切断されました。接続を待機中...");
                        console.log("リモート映像をクリアしました。");
                    }
                });

                // ルームが閉じたときのイベントリスナー
                currentSkywayRoom.onClosed.add(() => {
                    console.log("ルームが閉じられました。");
                    setIsConnectedToRoom(false);
                    setSkywayRoom(null);
                    setHasRemoteStream(false);
                    if (remoteVideoRef.current) {
                        remoteVideoRef.current.srcObject = null;
                    }
                    setCameraStatus("ルームから切断されました。");
                });

            } catch (error) {
                console.error("SkyWayの初期化またはルームへの参加に失敗しました:", error);
                setCameraStatus("接続に失敗しました。");
                setIsConnectedToRoom(false);
                setHasRemoteStream(false);
            }
        };

        initSkyWay();

        // コンポーネントアンマウント時のクリーンアップ
        return () => {
            if (currentSkywayRoom) {
                currentSkywayRoom.close();
                console.log("SkyWayRoom closed on unmount.");
            }
            if (currentSkywayContext) {
                currentSkywayContext.dispose();
                console.log("SkyWayContext disposed on unmount.");
            }
            if (remoteVideoRef.current) {
                remoteVideoRef.current.srcObject = null;
            }
        };
    }, []); // 空の依存配列でコンポーネントのマウント時に一度だけ実行

    //表示部分の処理
    return(
    <>
        <meta charSet="UTF-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <title>ロボットチャット - RX78-myakumyaku</title>
        <link rel="stylesheet" href="style.css" type="text/css" />
        <div className="header">
            <div className="header-left">
            <button className="motion-btn">モーション</button>
            <span className="robot-status">
                操作しているロボット：RX78-myakumyaku
            </span>
            </div>
            <div className="header-right">
            <button onClick={handleLogout} className="logout-btn">ログアウト</button>
            <button className="voice-btn">ボイス</button>
            </div>
        </div>
        <div className="main-container">
            <div className="side-panel">
            <div className="side-panel-content">
                <button className="side-btn">こんにちは</button>
                <button className="side-btn">バイバイ</button>
                <button className="side-btn">ペコリ</button>
                <button className="side-btn">バタバタ</button>
                <button className="side-btn">うえをむく</button>
                <button className="side-btn">したをむく</button>
                <button className="side-btn">みぎをむく</button>
                <button className="side-btn">ひだりをむく</button>
                <button className="side-btn">うなずく</button>
                <button className="side-btn">かしげる</button>
                <button className="side-btn">まわる</button>
                <button className="side-btn">ジャンプ</button>
                <button className="side-btn">ダンス</button>
                <button className="side-btn">おじぎ</button>
                <button className="side-btn">きをつけ</button>
                <button className="side-btn">休め</button>
            </div>
            </div>
            <div className="main-area">
                <div className="robot-display">
                <video
                    ref={remoteVideoRef}
                    autoPlay
                    playsInline
                    className="w-full h-full object-contain rounded-lg bg-black absolute top-0 left-0"
                />
                {!hasRemoteStream && (
                                <div className="absolute inset-0 flex items-center justify-center text-white text-xl bg-black bg-opacity-75 rounded-lg">
                                    接続を待機中...
                                </div>
                            )}
                </div>
                <div className="status-display flex items-center space-x-2 text-lg font-semibold">
                    <span className={`status-dot w-3 h-3 rounded-full ${isConnectedToRoom && hasRemoteStream ? 'bg-green-500' : 'bg-red-500'}`} />
                    <span className={isConnectedToRoom && hasRemoteStream ? 'text-green-700' : 'text-red-700'}>{cameraStatus}</span>
                </div>
            </div>
            <div className="side-panel right">
            <div className="side-panel-content">
                <button className="side-btn">こんにちは</button>
                <button className="side-btn">どこからきたの？</button>
                <button className="side-btn">バイバイ</button>
                <button className="side-btn">○○していますか？</button>
                <button className="side-btn">ありがとう</button>
                <button className="side-btn">いいね</button>
                <button className="side-btn">おおきに</button>
                <button className="side-btn">すごい</button>
                <button className="side-btn">がんばれ</button>
                <button className="side-btn">よろしく</button>
                <button className="side-btn">お疲れ様</button>
                <button className="side-btn">いいお天気</button>
                <button className="side-btn">暑いですね</button>
                <button className="side-btn">寒いですね</button>
                <button className="side-btn">楽しい</button>
                <button className="side-btn">面白い</button>
                <button className="side-btn">すてき</button>
                <button className="side-btn">かわいい</button>
                <button className="side-btn">かっこいい</button>
                <button className="side-btn">また明日</button>
            </div>
            </div>
        </div>
        <div className="bottom-controls">
            <button className="control-btn ai-btn">🤖 AIでいあんON</button>
            <button className="control-btn mic-btn" id="micBtn">
            🎤
            </button>
            <button className="control-btn manual-btn" id="manualBtn">
            📋 マニュアル
            </button>
            <button className="control-btn greeting-btn" id="languageBtn">
            👋 にほんご
            </button>
        </div>
    </>

    )
}


export default Home;
