import React, { useEffect, useMemo, useRef, useState } from "react";
import './style.css'
// SkyWay SDKã‚’npmã‹ã‚‰ã‚¤ãƒ³ãƒãƒ¼ãƒˆã—ã¾ã™
import {
  nowInSec,
  SkyWayAuthToken,
  SkyWayContext,
  SkyWayRoom,
  SkyWayStreamFactory, // SkyWayStreamFactoryã‚’æ˜ç¤ºçš„ã«ã‚¤ãƒ³ãƒãƒ¼ãƒˆ
  uuidV4,
} from '@skyway-sdk/room';

// ã‚¢ãƒ—ãƒªã‚±ãƒ¼ã‚·ãƒ§ãƒ³ã®ãƒ¡ã‚¤ãƒ³ã‚³ãƒ³ãƒãƒ¼ãƒãƒ³ãƒˆ
function Home() {
    // ãƒ­ã‚°ã‚¤ãƒ³çŠ¶æ…‹ã‚’ç®¡ç†ã™ã‚‹ã‚¹ãƒ†ãƒ¼ãƒˆ
    const [isLoggedIn, setIsLoggedIn] = useState(true); // åˆæœŸçŠ¶æ…‹ã‚’ãƒ­ã‚°ã‚¤ãƒ³æ¸ˆã¿ã¨ã™ã‚‹
    // SkyWayãƒ«ãƒ¼ãƒ å
    const [roomName, setRoomName] = useState("your-robot-room"); // ã“ã“ã«é©åˆ‡ãªãƒ«ãƒ¼ãƒ åã‚’è¨­å®šã—ã¦ãã ã•ã„ã€‚ãƒ­ãƒœãƒƒãƒˆå´ã‚‚åŒã˜ãƒ«ãƒ¼ãƒ åã§æ¥ç¶šã™ã‚‹å¿…è¦ãŒã‚ã‚Šã¾ã™ã€‚
    // è‡ªèº«ã®SkyWayå‚åŠ è€…æƒ…å ±
    const [me, setMe] = useState(null);
    // ãƒªãƒ¢ãƒ¼ãƒˆã®æ˜ åƒã‚’è¡¨ç¤ºã™ã‚‹ãŸã‚ã®videoè¦ç´ ã¸ã®å‚ç…§
    const remoteVideoRef = useRef(null);
    // æ˜ åƒãŒå†ç”Ÿä¸­ã‹ã©ã†ã‹ã‚’è¿½è·¡ã™ã‚‹ã‚¹ãƒ†ãƒ¼ãƒˆ (ãƒªãƒ¢ãƒ¼ãƒˆæ˜ åƒç”¨)
    const [isRemoteVideoPlaying, setIsRemoteVideoPlaying] = useState(false);
    // ãƒ­ãƒ¼ã‚«ãƒ«ã®æ¥ç¶šçŠ¶æ…‹ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸
    const [connectionStatus, setConnectionStatus] = useState("SkyWayã«æ¥ç¶šä¸­...");

    /**
     * ãƒ­ã‚°ã‚¢ã‚¦ãƒˆãƒœã‚¿ãƒ³ã‚¯ãƒªãƒƒã‚¯æ™‚ã®ãƒãƒ³ãƒ‰ãƒ©
     * ã“ã®ãƒ‡ãƒ¢ã§ã¯ãƒ­ã‚°ã‚¤ãƒ³çŠ¶æ…‹ã‚’falseã«ã™ã‚‹ã ã‘ã§ã€å®Ÿéš›ã®ãƒšãƒ¼ã‚¸é·ç§»ã¯è¡Œã„ã¾ã›ã‚“ã€‚
     */
    const handleLogout = () => {
        setIsLoggedIn(false); // ãƒ­ã‚°ã‚¤ãƒ³çŠ¶æ…‹ã‚’falseã«è¨­å®š
        // å®Ÿéš›ã®ã‚¢ãƒ—ãƒªã‚±ãƒ¼ã‚·ãƒ§ãƒ³ã§ã¯ã€ã“ã“ã§ãƒ­ã‚°ã‚¤ãƒ³ãƒšãƒ¼ã‚¸ã¸é·ç§»ã—ã¾ã™
        console.log("Logged out. In a real app, you would navigate to the login page.");
    };

    // SkyWay App ID ã¨ Secret Key ã¯ã€ã”è‡ªèº«ã®ã‚‚ã®ã‚’è¨­å®šã—ã¦ãã ã•ã„ã€‚
    // ã“ã®å€¤ã¯ãƒ‡ãƒ¢ç”¨ã§ã‚ã‚Šã€æœ¬ç•ªç’°å¢ƒã§ã¯å®‰å…¨ãªæ–¹æ³•ã§ç®¡ç†ã™ã‚‹å¿…è¦ãŒã‚ã‚Šã¾ã™ã€‚
    const appId = useMemo(() => "4490dcb5-dbe8-4028-ba0a-6d0d143e4515", []);
    const secretKey = useMemo(() => "40HHGEIOx0BRByPdxqwUswK0a+7v2JaeaJ9CcFjMdAQ=", []);

    // SkyWayèªè¨¼ãƒˆãƒ¼ã‚¯ãƒ³ã®ç”Ÿæˆ
    const token = useMemo(() => {
        // App IDã¾ãŸã¯Secret KeyãŒä¸è¶³ã—ã¦ã„ã‚‹å ´åˆã¯ãƒˆãƒ¼ã‚¯ãƒ³ç”Ÿæˆã‚’ã‚¹ã‚­ãƒƒãƒ—
        if (!appId || !secretKey) {
            console.log("SkyWay App ID or Secret Key is missing, skipping token generation.");
            setConnectionStatus("SkyWay App IDã¾ãŸã¯Secret KeyãŒä¸è¶³ã—ã¦ã„ã¾ã™ã€‚");
            return undefined;
        }

        return new SkyWayAuthToken({
            jti: uuidV4(),
            iat: nowInSec(),
            exp: nowInSec() + 60 * 60 * 24, // ãƒˆãƒ¼ã‚¯ãƒ³ã®æœ‰åŠ¹æœŸé™ã‚’24æ™‚é–“ã«è¨­å®š
            version: 3,
            scope: {
                appId: appId,
                rooms: [
                    {
                        name: "*", // ã™ã¹ã¦ã®ãƒ«ãƒ¼ãƒ åã«ã‚¢ã‚¯ã‚»ã‚¹è¨±å¯
                        methods: ["create", "close", "updateMetadata"],
                        member: {
                            name: "*", // ã™ã¹ã¦ã®ãƒ¡ãƒ³ãƒãƒ¼ã«ã‚¢ã‚¯ã‚»ã‚¹è¨±å¯
                            methods: ["publish", "subscribe", "updateMetadata"]
                        }
                    }
                ],
                turn: {
                    enabled: true // TURNã‚µãƒ¼ãƒãƒ¼ã®ä½¿ç”¨ã‚’è¨±å¯
                }
            }
        }).encode(secretKey);
    }, [appId, secretKey]);

    // SkyWayãƒ«ãƒ¼ãƒ ã¸ã®æ¥ç¶šã¨ã‚¹ãƒˆãƒªãƒ¼ãƒ ã®è³¼èª­å‡¦ç†
    useEffect(() => {
        // ãƒ­ãƒ¼ã‚«ãƒ«ã‚¹ãƒˆãƒªãƒ¼ãƒ ã¯ã“ã“ã§ã¯å–å¾—ã—ãªã„ãŸã‚ã€å¤‰æ•°ã¯ä¸è¦

        const joinRoom = async () => {
            // ãƒˆãƒ¼ã‚¯ãƒ³ã¾ãŸã¯ãƒ«ãƒ¼ãƒ åãŒåˆ©ç”¨å¯èƒ½ã§ãªã„å ´åˆã¯ãƒ«ãƒ¼ãƒ å‚åŠ ã‚’ã‚¹ã‚­ãƒƒãƒ—
            if (!token || !roomName) {
                console.log("SkyWay token or room name not available, skipping room join.");
                setConnectionStatus("SkyWayãƒˆãƒ¼ã‚¯ãƒ³ã¾ãŸã¯ãƒ«ãƒ¼ãƒ åãŒåˆ©ç”¨ã§ãã¾ã›ã‚“ã€‚");
                return;
            }

            try {
                setConnectionStatus("SkyWayãƒ«ãƒ¼ãƒ ã«æ¥ç¶šä¸­...");
                // SkyWayã‚³ãƒ³ãƒ†ã‚­ã‚¹ãƒˆã®ä½œæˆ
                const context = await SkyWayContext.Create(token);
                // SkyWayãƒ«ãƒ¼ãƒ ã¸ã®å‚åŠ ã¾ãŸã¯ä½œæˆ
                const room = await SkyWayRoom.FindOrCreate(context, {
                    name: roomName,
                    type: "p2p", // P2Pãƒ«ãƒ¼ãƒ ã¨ã—ã¦è¨­å®š
                });
                // ãƒ«ãƒ¼ãƒ ã«å‚åŠ ã—ã€è‡ªèº«ã®ãƒ¡ãƒ³ãƒãƒ¼æƒ…å ±ã‚’å–å¾—
                const newMe = await room.join();
                setMe(newMe);
                console.log(`Joined room: ${roomName} as member: ${newMe.id}`);
                setConnectionStatus(`ãƒ«ãƒ¼ãƒ ã«å‚åŠ ã—ã¾ã—ãŸ: ${roomName} (ID: ${newMe.id.substring(0, 8)}...)`);

                // ãƒ­ãƒ¼ã‚«ãƒ«ã®ã‚«ãƒ¡ãƒ©ã¨ãƒã‚¤ã‚¯ã®ã‚¹ãƒˆãƒªãƒ¼ãƒ ã®å–å¾—ã¨å…¬é–‹ã¯è¡Œã‚ãªã„


                // ãƒªãƒ¢ãƒ¼ãƒˆãƒ¡ãƒ³ãƒãƒ¼ãŒã‚¹ãƒˆãƒªãƒ¼ãƒ ã‚’å…¬é–‹ã—ãŸã¨ãã®ã‚¤ãƒ™ãƒ³ãƒˆãƒªã‚¹ãƒŠãƒ¼
                newMe.onStreamPublished.add(async (e) => {
                    const { stream } = e;
                    console.log(`Stream published by ${e.publication.publisher.id}: ${stream.id}, ContentType: ${stream.contentType}`);
                    // æ˜ åƒã‚¹ãƒˆãƒªãƒ¼ãƒ ã®ã¿ã‚’å¯¾è±¡ã¨ã™ã‚‹
                    if (stream.contentType === "video") {
                        console.log(`Attempting to subscribe to video stream from ${e.publication.publisher.id}`);
                        // ã‚¹ãƒˆãƒªãƒ¼ãƒ ã‚’è³¼èª­
                        const subscribedStream = await newMe.subscribe(stream);
                        // videoè¦ç´ ã«ã‚¹ãƒˆãƒªãƒ¼ãƒ ã‚’è¨­å®šã—ã€å†ç”Ÿã‚’é–‹å§‹
                        if (remoteVideoRef.current) {
                            remoteVideoRef.current.srcObject = subscribedStream.mediaStream;
                            remoteVideoRef.current.play()
                                .then(() => {
                                    console.log("Remote video stream started successfully.");
                                    setIsRemoteVideoPlaying(true); // æ˜ åƒãŒå†ç”Ÿã•ã‚ŒãŸã“ã¨ã‚’è¨˜éŒ²
                                })
                                .catch((error) => {
                                    console.error("Failed to play remote video (autoplay blocked?):", error);
                                    setIsRemoteVideoPlaying(false); // å†ç”Ÿå¤±æ•—
                                    setConnectionStatus(`ãƒªãƒ¢ãƒ¼ãƒˆæ˜ åƒã®å†ç”Ÿã«å¤±æ•—ã—ã¾ã—ãŸ: ${error.message}. ãƒ–ãƒ©ã‚¦ã‚¶ã®è‡ªå‹•å†ç”Ÿãƒãƒªã‚·ãƒ¼ã‚’ç¢ºèªã—ã¦ãã ã•ã„ã€‚`);
                                });
                        }
                    }
                });

                // æ—¢ã«ãƒ«ãƒ¼ãƒ ã«ã„ã‚‹ãƒ¡ãƒ³ãƒãƒ¼ã®ã‚¹ãƒˆãƒªãƒ¼ãƒ ã‚’è³¼èª­ã™ã‚‹
                // ã“ã‚Œã¯ã€ã‚ãªãŸãŒãƒ«ãƒ¼ãƒ ã«å‚åŠ ã™ã‚‹å‰ã«ä»–ã®ãƒ¡ãƒ³ãƒãƒ¼ãŒæ—¢ã«ã‚¹ãƒˆãƒªãƒ¼ãƒ ã‚’å…¬é–‹ã—ã¦ã„ãŸå ´åˆã«å¿…è¦ã§ã™ã€‚
                for (const member of room.members) {
                    if (member.id === newMe.id) continue; // è‡ªèº«ã‚’é™¤ã
                    for (const publication of member.publications) {
                        // æ˜ åƒã‚¹ãƒˆãƒªãƒ¼ãƒ ã®ã¿ã‚’å¯¾è±¡ã¨ã™ã‚‹
                        if (publication.contentType === "video") {
                            console.log(`Attempting to subscribe to existing video stream from ${publication.publisher.id}`);
                            const subscribedStream = await newMe.subscribe(publication);
                            if (remoteVideoRef.current) {
                                remoteVideoRef.current.srcObject = subscribedStream.mediaStream;
                                remoteVideoRef.current.play()
                                    .then(() => {
                                        console.log("Existing remote video stream started successfully.");
                                        setIsRemoteVideoPlaying(true); // æ˜ åƒãŒå†ç”Ÿã•ã‚ŒãŸã“ã¨ã‚’è¨˜éŒ²
                                    })
                                    .catch((error) => {
                                        console.error("Failed to play existing remote video (autoplay blocked?):", error);
                                        setIsRemoteVideoPlaying(false); // å†ç”Ÿå¤±æ•—
                                        setConnectionStatus(`æ—¢å­˜ã®ãƒªãƒ¢ãƒ¼ãƒˆæ˜ åƒã®å†ç”Ÿã«å¤±æ•—ã—ã¾ã—ãŸ: ${error.message}. ãƒ–ãƒ©ã‚¦ã‚¶ã®è‡ªå‹•å†ç”Ÿãƒãƒªã‚·ãƒ¼ã‚’ç¢ºèªã—ã¦ãã ã•ã„ã€‚`);
                                    });
                            }
                        }
                    }
                }

            } catch (error) {
                console.error("Failed to join SkyWay room:", error);
                setConnectionStatus(`SkyWayãƒ«ãƒ¼ãƒ ã¸ã®æ¥ç¶šã«å¤±æ•—ã—ã¾ã—ãŸ: ${error.message}`);
            }
        };

        // ãƒ­ã‚°ã‚¤ãƒ³çŠ¶æ…‹ãŒtrueã®å ´åˆã®ã¿SkyWayæ¥ç¶šã‚’è©¦ã¿ã‚‹
        if (isLoggedIn) {
