  const remoteVideoElements = useRef(new Map());
  // „É™„É¢„Éº„Éà„Éì„Éá„Ç™„ÇíË°®Á§∫„Åô„ÇãDOMË¶ÅÁ¥†„Å∏„ÅÆÂèÇÁÖß
  const robotDisplayRef = useRef(null);
  // „É≠„Éº„Ç´„É´„Çπ„Éà„É™„Éº„É†„ÅÆÂèÇÁÖß„Çí‰øùÊåÅ
  const localStreamRef = useRef(null);
  // „Ç™„Éº„Éá„Ç£„Ç™„Çπ„Éà„É™„Éº„É†„ÅÆ Publication „Çí‰øùÊåÅ
  const audioPublicationRef = useRef(null);
  // ÁèæÂú®„ÅÆAI„É¢„Éº„Éâ
  const [isAiModeOn, setIsAiModeOn] = useState(false);
  // „Éû„Ç§„ÇØ„Éü„É•„Éº„ÉàÁä∂ÊÖã
  const [isMuted, setIsMuted] = useState(true);
  // Èü≥Â£∞ÂÖ•Âäõ„ÅÆÂÆüË°å‰∏≠Áä∂ÊÖã„ÇíÁÆ°ÁêÜ
  const [isMoveInProgress, setIsMoveInProgress] = useState(false);
  // AI„Åã„ÇâÊèêÊ°à„Åï„Çå„ÅüÂÄôË£ú„ÇíÁÆ°ÁêÜ
  const [aiSuggestions, setAiSuggestions] = useState([]);

  // SkyWayË™çË®º„Éà„Éº„ÇØ„É≥„ÇíÁîüÊàê
  const generateToken = () => {
    return new SkyWayAuthToken({
      jti: uuidV4(),
      iat: nowInSec(),
      exp: nowInSec() + 60 * 60 * 24,
      scope: {
        app: {
          id: 'b1480f7d-0114-41d5-b664-92e10507a2a6',
          turn: true,
          actions: ['read', 'write'],
          channels: [
            {
              id: '*',
              name: '*',
              actions: ['read', 'write'],
              members: [
                {
                  id: '*',
                  name: '*',
                  actions: ['read', 'write'],
                },
              ],
              sfuBots: [
                {
                  actions: ['read', 'write'],
                },
              ],
            },
          ],
        },
      },
    }).encode();
  };

  // SkyWay SDK„ÅÆÂàùÊúüÂåñ
  useEffect(() => {
    const initSkyWay = async () => {
      try {
        const token = generateToken();
        const context = await SkyWayContext.Create(token);
        setSkywayContext(context);
        console.log("SkyWay Context created.");
      } catch (error) {
        console.error("Failed to create SkyWay context:", error);
      }
    };
    initSkyWay();
  }, []);

  // „É≠„Ç∞„Ç§„É≥Áä∂ÊÖã„Çí„É≠„Ç∞„Ç¢„Ç¶„Éà„Å´Ë®≠ÂÆö
  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  // „Çπ„Éà„É™„Éº„É†„ÇíDOMË¶ÅÁ¥†„Å´„Ç¢„Çø„ÉÉ„ÉÅ„Åô„ÇãÈñ¢Êï∞
  const attachRemoteStream = (stream, memberId) => {
    // „É™„É¢„Éº„Éà„Éì„Éá„Ç™„ÇíË°®Á§∫„Åô„ÇãDOMË¶ÅÁ¥†„ÅåÂ≠òÂú®„Åó„ÄÅ„Åã„Å§„Çπ„Éà„É™„Éº„É†„Åå„É°„Éá„Ç£„Ç¢„Çπ„Éà„É™„Éº„É†„ÅÆÂ†¥Âêà„ÅÆ„ÅøÂá¶ÁêÜ„ÇíÂÆüË°å
    if (robotDisplayRef.current && (stream instanceof RemoteVideoStream || stream instanceof RemoteAudioStream)) {
      console.log(`Attaching stream from member ${memberId}.`);

      // Êó¢Â≠ò„ÅÆ„Éì„Éá„Ç™Ë¶ÅÁ¥†„Åå„ÅÇ„Çå„Å∞ÂâäÈô§
      const existingVideo = remoteVideoElements.current.get(memberId);
      if (existingVideo) {
        console.log(`Removing existing video for member ${memberId}.`);
        if (existingVideo.srcObject) {
          // srcObject„Çínull„Å´„Åó„Å¶ÂèÇÁÖß„ÇíËß£Êîæ
          existingVideo.srcObject = null;
        }
        existingVideo.remove();
        remoteVideoElements.current.delete(memberId);
      }

      const video = document.createElement('video');
      video.playsInline = true;
      video.autoplay = true;
      video.muted = false; // „Éá„Éï„Ç©„É´„Éà„Åß„Éü„É•„Éº„ÉàËß£Èô§
      video.classList.add('remote-video', 'object-cover'); // CSS„ÇØ„É©„Çπ„ÇíËøΩÂä†
      video.dataset.memberId = memberId;

      stream.attach(video); // „Çπ„Éà„É™„Éº„É†„Çí„Éì„Éá„Ç™Ë¶ÅÁ¥†„ÅÆ„ÇΩ„Éº„Çπ„Å´Ë®≠ÂÆö
      robotDisplayRef.current.appendChild(video);
      remoteVideoElements.current.set(memberId, video);
      console.log(`Successfully attached stream from member ${memberId} to DOM.`);
    } else {
      console.warn(`Attempted to attach non-media stream or robotDisplayRef is null for member ${memberId}.`);
    }
  };

  // „Éá„Éº„Çø„Çπ„Éà„É™„Éº„É†„ÇíÂá¶ÁêÜ„Åô„ÇãÈñ¢Êï∞
  const handleDataStream = (dataStream, memberId) => {
    console.log(`Received data stream from member ${memberId}.`);
    dataStream.onData.add((data) => {
      console.log(`Received data:`, data);
      // Âèó„ÅëÂèñ„Å£„Åü„Éá„Éº„Çø„Çí„Éë„Éº„Çπ
      try {
        const parsedData = JSON.parse(data);
        if (parsedData.type === 'suggestion' && Array.isArray(parsedData.suggestions)) {
          console.log("Received AI suggestions:", parsedData.suggestions);
          setAiSuggestions(parsedData.suggestions);
        } else if (parsedData.type === 'isMoveInProgress') {
          console.log("Received move status:", parsedData.value);
          setIsMoveInProgress(parsedData.value);
        }
      } catch (error) {
        console.error("Failed to parse received data:", error);
      }
    });
  };

  // „É´„Éº„É†„Å∏„ÅÆÂèÇÂä†
  const handleJoinRoom = async () => {
    if (!skywayContext || !roomName) {
      console.error("Context or room name is missing.");
      return;
    }
    console.log(`Joining room: ${roomName}`);
    try {
      const room = await SkyWayRoom.FindOrCreate(skywayContext, { name: roomName, type: 'sfu' });
      setSkywayRoom(room);
      setMe(room.localPerson);
      console.log(`Joined room: ${roomName} as member ${room.localPerson.id}`);

      // „É´„Éº„É†ÂÜÖ„ÅÆ„Ç§„Éô„É≥„Éà„É™„Çπ„Éä„Éº„ÇíË®≠ÂÆö
      // „Çπ„Éà„É™„Éº„É†„ÅåÂÖ¨Èñã„Åï„Çå„Åü„Å®„Åç„ÅÆÂá¶ÁêÜ
      room.onStreamPublished.add((e) => {
        console.log(`Stream published event for publication ID: ${e.publication.id}`);
        const { publication } = e;
        // publication.subscribe()„Åß„Çπ„Éà„É™„Éº„É†„ÇíË≥ºË™≠
        publication.subscribe().then(stream => {
          if (stream instanceof RemoteVideoStream || stream instanceof RemoteAudioStream) {
            console.log(`Subscribed to remote media stream.`);
            attachRemoteStream(stream, publication.publisher.id);
          } else if (stream instanceof RemoteDataStream) {
            console.log(`Subscribed to remote data stream.`);
            handleDataStream(stream, publication.publisher.id);
          }
        }).catch(err => {
          console.error(`Failed to subscribe to publication ${publication.id}:`, err);
        });
      });

      // „Çπ„Éà„É™„Éº„É†„ÅÆÂÖ¨Èñã„ÅåÂÅúÊ≠¢„Åï„Çå„Åü„Å®„Åç„ÅÆÂá¶ÁêÜ
      room.onStreamUnpublished.add((e) => {
        console.log(`Stream unpublished event for publication ID: ${e.publication.id}`);
        const video = remoteVideoElements.current.get(e.publication.publisher.id);
        if (video) {
          console.log(`Removing video for member ${e.publication.publisher.id}.`);
          if (video.srcObject) {
            video.srcObject = null;
          }
          video.remove();
          remoteVideoElements.current.delete(e.publication.publisher.id);
        }
      });
      
      // Êó¢Â≠ò„ÅÆ„Çπ„Éà„É™„Éº„É†„ÇíË≥ºË™≠
      room.publications.forEach(publication => {
        publication.subscribe().then(stream => {
          if (stream instanceof RemoteVideoStream || stream instanceof RemoteAudioStream) {
            console.log(`Subscribed to existing remote media stream.`);
            attachRemoteStream(stream, publication.publisher.id);
          } else if (stream instanceof RemoteDataStream) {
            console.log(`Subscribed to existing remote data stream.`);
            handleDataStream(stream, publication.publisher.id);
          }
        }).catch(err => {
          console.error(`Failed to subscribe to existing publication ${publication.id}:`, err);
        });
      });

      // „É≠„Éº„Ç´„É´„Éû„Ç§„ÇØ„Åã„Çâ„Ç™„Éº„Éá„Ç£„Ç™„Çπ„Éà„É™„Éº„É†„ÇíÂèñÂæó
      const { audio } = await SkyWayStreamFactory.CreateMicrophoneAudioStream();
      localStreamRef.current = audio;
      audio.track.enabled = isMuted; // ÊúÄÂàù„ÅØ„Éü„É•„Éº„ÉàÁä∂ÊÖã
      const publication = await room.localPerson.publish(audio);
      audioPublicationRef.current = publication;
      console.log("Local audio stream published.");
      
    } catch (error) {
      console.error("Failed to join room:", error);
    }
  };

  // „É´„Éº„É†„Åã„Çâ„ÅÆÈÄÄÂá∫
  const handleLeaveRoom = async () => {
    if (skywayRoom) {
      console.log("Leaving room...");
      try {
        await skywayRoom.close();
        setMe(null);
        setSkywayRoom(null);
        setAiSuggestions([]);
        // „Éì„Éá„Ç™Ë¶ÅÁ¥†„ÅÆ„ÇØ„É™„Éº„É≥„Ç¢„ÉÉ„Éó
        if (robotDisplayRef.current) {
          Array.from(robotDisplayRef.current.children).forEach(child => {
            if (child.tagName === 'VIDEO') {
              if (child.srcObject) {
                child.srcObject.getTracks().forEach(track => track.stop()); // „Éà„É©„ÉÉ„ÇØ„ÇíÂÅúÊ≠¢„Åó„Å¶„É™„ÇΩ„Éº„Çπ„ÇíËß£Êîæ
                child.srcObject = null;
              }
              child.remove();
            }
          });
        }
        remoteVideoElements.current.clear();
        console.log("Successfully left the room and cleaned up resources.");
      } catch (error) {
        console.error("Failed to leave room:", error);
      }
    }
  };

  // „Éû„Ç§„ÇØ„ÅÆ„Éü„É•„Éº„Éà/„Éü„É•„Éº„ÉàËß£Èô§
  const handleMute = () => {
    if (audioPublicationRef.current) {
      const newMuteState = !isMuted;
      audioPublicationRef.current.track.enabled = !newMuteState;
      setIsMuted(newMuteState);
      console.log(`Microphone is now ${newMuteState ? 'muted' : 'unmuted'}.`);
    }
  };

  // AI„É¢„Éº„Éâ„ÅÆÂàá„ÇäÊõø„Åà
  const handleAiModeToggle = () => {
    setIsAiModeOn(!isAiModeOn);
    setAiSuggestions([]);
    console.log(`AI mode is now ${!isAiModeOn ? 'ON' : 'OFF'}.`);
  };

  return (
    <div className="home-container">
      <header className="header">
        <h1 className="title">„É≠„Éú„ÉÉ„Éà„Ç≥„Éü„É•„Éã„Ç±„Éº„Ç∑„Éß„É≥</h1>
        <button className="logout-btn" onClick={handleLogout}>„É≠„Ç∞„Ç¢„Ç¶„Éà</button>
      </header>
      <main className="main-content">
        {!me ? (
          <div className="join-form-container">
            <h2 className="join-title">„É´„Éº„É†„Å´ÂèÇÂä†</h2>
            <div className="room-input-group">
              <input
                type="text"
                placeholder="„É´„Éº„É†Âêç„ÇíÂÖ•Âäõ"
                value={roomName}
                onChange={(e) => setRoomName(e.target.value)}
                className="room-input"
              />
              <button onClick={handleJoinRoom} className="join-btn">ÂèÇÂä†</button>
            </div>
          </div>
        ) : (
          <>
            <div className="robot-display-container">
              <div className="robot-display" ref={robotDisplayRef}>
                {/* „É™„É¢„Éº„Éà„Çπ„Éà„É™„Éº„É†Êò†ÂÉè„Åå„Åì„Åì„Å´ËøΩÂä†„Åï„Çå„Åæ„Åô */}
              </div>
            </div>
            {isAiModeOn && aiSuggestions.length > 0 && (
              <div className="ai-suggestion-container">
                {aiSuggestions.map((suggestion, index) => (
                  <button
                    key={index}
                    className="ai-suggestion-btn"
                    disabled={isMoveInProgress}
                    onClick={() => console.log("Suggestion clicked:", suggestion)}
                  >
                    {suggestion}
                  </button>
                ))}
              </div>
            )}
            <div className="bottom-controls">
              <button
                className={`control-btn ai-btn ${isAiModeOn ? 'ai-mode-on' : ''}`}
                onClick={handleAiModeToggle}
                disabled={!me}
              >
                ü§ñ AI„É¢„Éº„Éâ {isAiModeOn ? 'ON' : 'OFF'}
              </button>
              <button
                className={`control-btn mic-btn ${isMuted ? 'muted' : ''}`}
                onClick={handleMute}
                id="micBtn"
                disabled={!me || !audioPublicationRef.current || isAiModeOn}
              >
                {isMuted ? 'üîá „Éû„Ç§„ÇØ„Éü„É•„Éº„Éà' : 'üé§ „Éû„Ç§„ÇØON'}
              </button>
              <button className="control-btn manual-btn" id="manualBtn" disabled={isAiModeOn || isMoveInProgress}>
                üìã „Éû„Éã„É•„Ç¢„É´
              </button>
              <button
                className="control-btn leave-btn"
                onClick={handleLeaveRoom}
              >
                üö™ ÈÄÄÂá∫
              </button>
            </div>
          </>
        )}
      </main>
    </div>
  );
}

export default Home;
