          console.log(`Received translated text: ${translatedContent}`);
        } else {
          // ç¿»è¨³ãƒ‡ãƒ¼ã‚¿ã§ã¯ãªã„å ´åˆã€ç¿»è¨³ãƒ†ã‚­ã‚¹ãƒˆã‚’ã‚¯ãƒªã‚¢ã™ã‚‹ã‹ã¯è¦æ¤œè¨
          // setTranslatedText("");
        }
      });
      console.log(`Listening for data from ${memberId}.`);
    } else {
      console.warn(`Attempted to handle non-data stream as data stream for member ${memberId}.`);
    }
  };

  /**
   * ãƒ«ãƒ¼ãƒ ã«å‚åŠ ã™ã‚‹ãƒãƒ³ãƒ‰ãƒ©
   * SkyWayContextãŒåˆæœŸåŒ–ã•ã‚Œã€ãƒ«ãƒ¼ãƒ åãŒå…¥åŠ›ã•ã‚Œã¦ã„ã‚‹å ´åˆã«å®Ÿè¡Œã•ã‚Œã¾ã™ã€‚
   */
  const handleJoinRoom = async () => {
    if (!skywayContext) {
      console.error("SkyWayContext is not initialized. Please wait or check token.");
      return;
    }
    if (!roomName) {
      console.error("Room name is empty. Please enter a room name to join.");
      return;
    }
    if (skywayRoom && me) {
      console.warn("Already in a room. Please leave the current room first.");
      return;
    }
    try {
      // æŒ‡å®šã•ã‚ŒãŸãƒ«ãƒ¼ãƒ åã§P2Pãƒ«ãƒ¼ãƒ ã‚’ä½œæˆã¾ãŸã¯æ¤œç´¢
      const room = await SkyWayRoom.FindOrCreate(skywayContext, { type: "p2p", // P2Pãƒ«ãƒ¼ãƒ ã‚¿ã‚¤ãƒ—
        name: roomName,
      });
      // roomã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆãŒæœ‰åŠ¹ã§ã‚ã‚‹ã“ã¨ã‚’ç¢ºèª
      if (!room) {
        console.error("SkyWayRoom.FindOrCreate returned null or undefined room.");
        throw new Error("Failed to create or find SkyWayRoom.");
      }
      setSkywayRoom(room);
      console.log(`SkyWayRoom '${roomName}' found or created.`);
      // ãƒ«ãƒ¼ãƒ ã«å‚åŠ ã—ã€è‡ªåˆ†ã®ãƒ¡ãƒ³ãƒãƒ¼æƒ…å ±ã‚’å–å¾—
      const localMember = await room.join();
      // localMemberãŒæœ‰åŠ¹ã§ã‚ã‚‹ã“ã¨ã‚’ç¢ºèª
      if (!localMember) {
        console.error("room.join() returned null or undefined localMember.");
        throw new Error("Failed to join the room.");
      }
      setMe(localMember);
      console.log(`Joined room '${roomName}' as member ID: ${localMember.id}`);
      // ãƒ«ãƒ¼ãƒ IDã¨è‡ªåˆ†ã®IDã‚’ã‚³ãƒ³ã‚½ãƒ¼ãƒ«ã«è¡¨ç¤º
      console.log(`Room ID: ${roomName} / My ID: ${localMember.id}`);
      // è‡ªåˆ†ã®ã‚«ãƒ¡ãƒ©ã¨ãƒã‚¤ã‚¯ã®ã‚¹ãƒˆãƒªãƒ¼ãƒ ã‚’ä½œæˆã—ã€å…¬é–‹
      const { audio, video } = await SkyWayStreamFactory.createMicrophoneAudioAndCameraStream();
      localStreamRef.current = video; // ãƒ­ãƒ¼ã‚«ãƒ«ãƒ“ãƒ‡ã‚ªã‚¹ãƒˆãƒªãƒ¼ãƒ ã‚’ä¿å­˜
      await localMember.publish(video);
      const publication_audio = await localMember.publish(audio);
      audioPublicationRef.current = publication_audio; // ã‚ªãƒ¼ãƒ‡ã‚£ã‚ªã® Publication ã‚’ä¿æŒ
      // ãƒ‡ãƒ¼ã‚¿ã‚¹ãƒˆãƒªãƒ¼ãƒ ã‚’ä½œæˆã—ã€å…¬é–‹
      const localDataStream = await SkyWayStreamFactory.createDataStream();
      localDataStreamRef.current = localDataStream; // ãƒ‡ãƒ¼ã‚¿ã‚¹ãƒˆãƒªãƒ¼ãƒ ã®å‚ç…§ã‚’ä¿æŒ
      await localMember.publish(localDataStream);
      // ãƒ«ãƒ¼ãƒ å†…ã®æ—¢å­˜ã®ãƒ¡ãƒ³ãƒãƒ¼ã®ã‚¹ãƒˆãƒªãƒ¼ãƒ ã‚’è³¼èª­
      room.members.forEach(member => {
        if (member.id !== localMember.id) {
          member.publications.forEach(publication => {
            if (publication.contentType === "video" || publication.contentType === "audio") {
              const { stream } = publication.subscribe();
              attachRemoteStream(stream, member.id);
            } else if (publication.contentType === "data") {
              const { stream } = publication.subscribe();
              handleDataStream(stream, member.id);
            }
          });
        }
      });

      // æ–°ã—ã„ãƒ¡ãƒ³ãƒãƒ¼ãŒãƒ«ãƒ¼ãƒ ã«å‚åŠ ã—ãŸã¨ãã®ã‚¤ãƒ™ãƒ³ãƒˆãƒªã‚¹ãƒŠãƒ¼
      room.onMemberJoined.add((e) => {
        console.log(`Member joined: ${e.member.id}`);
        e.member.onStreamPublished.add(async (e) => {
          console.log(`Stream published by ${e.publication.publisher.id}`);
          if (e.publication.contentType === "video" || e.publication.contentType === "audio") {
            const { stream } = await e.publication.subscribe();
            attachRemoteStream(stream, e.publication.publisher.id);
          } else if (e.publication.contentType === "data") {
            const { stream } = await e.publication.subscribe();
            handleDataStream(stream, e.publication.publisher.id);
          }
        });
      });

      // ãƒ¡ãƒ³ãƒãƒ¼ãŒãƒ«ãƒ¼ãƒ ã‹ã‚‰é›¢è„±ã—ãŸã¨ãã®ã‚¤ãƒ™ãƒ³ãƒˆãƒªã‚¹ãƒŠãƒ¼
      room.onMemberLeft.add((e) => {
        console.log(`Member left: ${e.member.id}`);
        const videoElement = remoteVideoElements.current.get(e.member.id);
        if (videoElement) {
          robotDisplayRef.current.removeChild(videoElement);
          remoteVideoElements.current.delete(e.member.id);
        }
        // ãƒ«ãƒ¼ãƒ ã«èª°ã‚‚ã„ãªããªã£ãŸã‚‰AIãƒ¢ãƒ¼ãƒ‰ã‚’ãƒªã‚»ãƒƒãƒˆ
        if (room.members.length <= 1) {
          setIsAiModeOn(false);
        }
      });
      // Publicationã®åœæ­¢ï¼ˆunpublishï¼‰ã‚’æ¤œçŸ¥ã™ã‚‹ã‚¤ãƒ™ãƒ³ãƒˆãƒªã‚¹ãƒŠãƒ¼
      room.onPublicationUnpublished.add((e) => {
        console.log(`Publication unpublished: ${e.publication.id} by ${e.publication.publisher.id}`);
        const videoElement = remoteVideoElements.current.get(e.publication.publisher.id);
        if (videoElement) {
          robotDisplayRef.current.removeChild(videoElement);
          remoteVideoElements.current.delete(e.publication.publisher.id);
        }
      });
      console.log("Joined room and set up listeners successfully.");

    } catch (error) {
      console.error("Failed to join room:", error);
      // ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ãŸå ´åˆã€çŠ¶æ…‹ã‚’ã‚¯ãƒªã‚¢
      setSkywayRoom(null);
      setMe(null);
    }
  };

  /**
   * ãƒ«ãƒ¼ãƒ ã‹ã‚‰é€€å‡ºã™ã‚‹ãƒãƒ³ãƒ‰ãƒ©
   */
  const handleLeaveRoom = () => {
    if (skywayRoom) {
      skywayRoom.close(); // ãƒ«ãƒ¼ãƒ ã‚’é–‰ã˜ã‚‹
      setSkywayRoom(null);
      setMe(null);
      localStreamRef.current = null;
      localDataStreamRef.current = null;
      // ã™ã¹ã¦ã®ãƒªãƒ¢ãƒ¼ãƒˆãƒ“ãƒ‡ã‚ªè¦ç´ ã‚’å‰Šé™¤
      remoteVideoElements.current.forEach(video => {
        if (robotDisplayRef.current) {
          robotDisplayRef.current.removeChild(video);
        }
      });
      remoteVideoElements.current.clear();
      setIsAiModeOn(false); // é€€å‡ºæ™‚ã«AIãƒ¢ãƒ¼ãƒ‰ã‚’ãƒªã‚»ãƒƒãƒˆ
      console.log("Left the room and cleaned up.");
    }
  };

  /**
   * ãƒ‡ãƒ¼ã‚¿ã‚¹ãƒˆãƒªãƒ¼ãƒ ã§ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã‚’é€ä¿¡ã™ã‚‹ãƒãƒ³ãƒ‰ãƒ©
   * @param {string} message - é€ä¿¡ã™ã‚‹ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸
   */
  const handleSend = (message) => {
    if (localDataStreamRef.current) {
      localDataStreamRef.current.write(message);
      console.log(`Sent message: "${message}"`);
      // è‡ªåˆ†ã®ç”»é¢ã«ã‚‚è¡¨ç¤º
      setReceivedMessages(prevMessages => [...prevMessages, { senderId: me.id, message: message, isLocal: true }]);
      // ãƒ¢ãƒ¼ã‚·ãƒ§ãƒ³ã‚³ãƒãƒ³ãƒ‰é€ä¿¡å¾Œã€ãƒ¢ãƒ¼ã‚·ãƒ§ãƒ³å®Œäº†ã‚·ã‚°ãƒŠãƒ«ã‚’å¾…ã¤ãŸã‚ãƒœã‚¿ãƒ³ã‚’ç„¡åŠ¹åŒ–
      if (message.startsWith('template_move')) {
        setIsMoveInProgress(true);
      } else if (message.startsWith('ai_assist')) {
        // AIã‚¢ã‚·ã‚¹ãƒˆãƒœã‚¿ãƒ³ãŒæŠ¼ã•ã‚ŒãŸå ´åˆã€ææ¡ˆã‚’ãƒªã‚»ãƒƒãƒˆ
        setAiSuggestions([null, null]);
        setIsAiModeOn(false); // AIãƒ¢ãƒ¼ãƒ‰ã‚’è‡ªå‹•ã§OFFã«ã™ã‚‹
      }
    }
  };

  /**
   * ãƒã‚¤ã‚¯ã®ãƒŸãƒ¥ãƒ¼ãƒˆ/ãƒŸãƒ¥ãƒ¼ãƒˆè§£é™¤ã‚’åˆ‡ã‚Šæ›¿ãˆã‚‹ãƒãƒ³ãƒ‰ãƒ©
   */
  const handleMute = async () => {
    if (!audioPublicationRef.current) {
      console.error("Audio publication not found.");
      return;
    }
    const isMutedNow = !isMuted;
    try {
      if (isMutedNow) {
        await audioPublicationRef.current.unpublish();
        console.log("Audio stream unpublished (muted).");
      } else {
        const { audio } = await SkyWayStreamFactory.createMicrophoneAudioStream();
        const newPublication = await me.publish(audio);
        audioPublicationRef.current = newPublication;
        console.log("Audio stream published (unmuted).");
      }
      setIsMuted(isMutedNow);
    } catch (error) {
      console.error("Failed to toggle mute state:", error);
    }
  };

  /**
   * AIãƒ¢ãƒ¼ãƒ‰ã®ON/OFFã‚’åˆ‡ã‚Šæ›¿ãˆã‚‹ãƒãƒ³ãƒ‰ãƒ©
   */
  const handleAiModeToggle = () => {
    const newAiMode = !isAiModeOn;
    setIsAiModeOn(newAiMode);
    if (!newAiMode) {
      // AIãƒ¢ãƒ¼ãƒ‰ã‚’OFFã«ã™ã‚‹å ´åˆã€ææ¡ˆã‚’ãƒªã‚»ãƒƒãƒˆ
      setAiSuggestions([null, null]);
      // AIãƒ¢ãƒ¼ãƒ‰OFFã®ã‚·ã‚°ãƒŠãƒ«ã‚’é€ä¿¡
      if (localDataStreamRef.current) {
        localDataStreamRef.current.write('ai_mode_off');
      }
    } else {
      // AIãƒ¢ãƒ¼ãƒ‰ã‚’ONã«ã™ã‚‹å ´åˆã€ã‚·ã‚°ãƒŠãƒ«ã‚’é€ä¿¡
      if (localDataStreamRef.current) {
        localDataStreamRef.current.write('ai_mode_on');
      }
    }
  };
  
  // ğŸŒŸè¿½åŠ : éŸ³å£°èªè­˜ã®é–‹å§‹/åœæ­¢ã‚’åˆ‡ã‚Šæ›¿ãˆã‚‹ãƒãƒ³ãƒ‰ãƒ©
  const handleToggleListening = () => {
    if (isListening) {
      recognition.current.stop();
      setIsListening(false);
      console.log("Stopped listening.");
    } else {
      recognition.current.start();
      setIsListening(true);
      console.log("Started listening.");
    }
  };

  return (
    <div className="app-container">
      <header className="header">
        <div className="header-left">
          {/* AIææ¡ˆã®ãƒœã‚¿ãƒ³ */}
          {aiSuggestions.map((suggestion, index) =>
            suggestion && (
              <button
                key={index}
                className="ai-btn"
                onClick={() => handleSend(suggestion.key)}
              >
                {suggestion.text}
              </button>
            )
          )}
        </div>
        
        {/* room-controls ã‚’ãƒ˜ãƒƒãƒ€ãƒ¼ã®å·¦ç«¯ã‹ã‚‰ä¸­å¤®å¯„ã‚Šã«ç§»å‹• */}
        <div className="room-controls-container">
          <div className="room-controls">
            <input
              type="text"
              placeholder="Room Name"
              value={roomName}
              onChange={(e) => setRoomName(e.target.value)}
              className="room-name-input"
              disabled={!!me} // å‚åŠ æ¸ˆã¿ã®å ´åˆã¯ç„¡åŠ¹
            />
            {me ? (
              <button onClick={handleLeaveRoom} className="join-btn">
                ãƒ«ãƒ¼ãƒ ã‹ã‚‰é€€å‡º
              </button>
            ) : (
              <button onClick={handleJoinRoom} className="join-btn">
                ãƒ«ãƒ¼ãƒ ã«å‚åŠ 
              </button>
            )}
          </div>
        </div>

        <div className="header-right">
          <span className="robot-status">{me ? "æ¥ç¶šä¸­" : "æœªæ¥ç¶š"}</span>
          <button onClick={handleLogout} className="logout-btn">
            ãƒ­ã‚°ã‚¢ã‚¦ãƒˆ
          </button>
        </div>
      </header>
      <div className="main-container">
        <div className="main-area">
          <div ref={robotDisplayRef} className="robot-display">
            {me && <video ref={localStreamRef} autoPlay playsInline className="local-video object-cover" muted />}
          </div>
          {/* AIææ¡ˆã®ãƒœã‚¿ãƒ³ã‚’å‰Šé™¤ */}
        </div>
        <div className="side-panel">
          <div className="side-panel-content">
            <h2 className="panel-title">ãƒ¢ãƒ¼ã‚·ãƒ§ãƒ³</h2>
            {Object.keys(messages).map(key => (
              <button
                key={key}
                // é€ä¿¡ã™ã‚‹ãƒ‡ãƒ¼ã‚¿ã¯ "template_move_ã€‡ã€‡" å½¢å¼ã§é€ä¿¡
                onClick={() => handleSend(`template_move_${messages[key].english}`)}
                className="side-btn"
                // AIãƒ¢ãƒ¼ãƒ‰ä¸­ã€æœªå‚åŠ ã€ãƒ¢ãƒ¼ã‚·ãƒ§ãƒ³ä¸­ã¯ç„¡åŠ¹åŒ–
                disabled={isAiModeOn || !me || isMoveInProgress}
              >
                {messages[key].japanese}
              </button>
            ))}
          </div>
          <div className="side-panel-content">
            <h2 className="panel-title">éŸ³å£°</h2>
            {Object.keys(messages).map(key => (
              <button
                key={key}
                // é€ä¿¡ã™ã‚‹ãƒ‡ãƒ¼ã‚¿ã¯ "template_voice_ã€‡ã€‡" å½¢å¼ã§é€ä¿¡
                onClick={() => handleSend(`template_voice_${messages[key].japanese}`)}
                className="side-btn"
                disabled={isAiModeOn || !me || isMoveInProgress} // AIãƒ¢ãƒ¼ãƒ‰ä¸­ã€æœªå‚åŠ ã€ãƒ¢ãƒ¼ã‚·ãƒ§ãƒ³ä¸­ã¯ç„¡åŠ¹åŒ–
              >
                {messages[key].japanese} {/* å¸¸ã«æ—¥æœ¬èªè¡¨ç¤º */}
              </button>
            ))}
          </div>
        </div>
      </div>
      <div className="bottom-controls">
        <button
          className={`control-btn ai-btn`}
          onClick={() => handleAiModeToggle()}
          disabled={!me} // ãƒ«ãƒ¼ãƒ ã«å‚åŠ ã—ã¦ã„ãªã„å ´åˆã¯ç„¡åŠ¹
        >
          {isAiModeOn ? 'ãƒªã‚»ãƒƒãƒˆ' : 'AIãƒ¢ãƒ¼ãƒ‰ON'}
        </button>
        {/* ğŸŒŸè¿½åŠ : éŸ³å£°èªè­˜ãƒœã‚¿ãƒ³ */}
        <button
          className={`control-btn speech-recognition-btn ${isListening ? 'listening' : ''}`}
          onClick={handleToggleListening}
          disabled={!me || !localDataStreamRef.current || isAiModeOn} // ãƒ«ãƒ¼ãƒ æœªå‚åŠ ã€ãƒ‡ãƒ¼ã‚¿ã‚¹ãƒˆãƒªãƒ¼ãƒ æœªæº–å‚™ã€AIãƒ¢ãƒ¼ãƒ‰ä¸­ã¯ç„¡åŠ¹
        >
          {isListening ? 'ğŸ”´ éŸ³å£°èªè­˜ã‚’åœæ­¢' : 'ğŸ¤ éŸ³å£°èªè­˜ã‚’é–‹å§‹'}
        </button>
        <button
          className={`control-btn mic-btn ${isMuted ? 'muted' : ''}`} // ãƒŸãƒ¥ãƒ¼ãƒˆçŠ¶æ…‹ã«å¿œã˜ã¦ã‚¯ãƒ©ã‚¹ã‚’è¿½åŠ 
          onClick={handleMute}
          id="micBtn"
          disabled={!me || isAiModeOn} // ãƒ«ãƒ¼ãƒ ã«å‚åŠ ã—ã¦ã„ãªã„å ´åˆã¯ç„¡åŠ¹
        >
          {isMuted ? 'ãƒã‚¤ã‚¯ON' : 'ãƒã‚¤ã‚¯OFF'}
        </button>
      </div>
    </div>
  );
}

export default Home;
