import React, { useEffect, useMemo, useRef, useState } from "react";
import './style.css'; // style.css„ÇíÂÜçÂ∫¶ÊúâÂäπÂåñ
import {
  nowInSec,
  SkyWayAuthToken,
  SkyWayContext,
  SkyWayRoom,
  SkyWayStreamFactory,
  uuidV4,
  LocalP2PRoomMember,
  RemoteDataStream, // RemoteDataStream„Çí„Ç§„É≥„Éù„Éº„Éà
  RemoteVideoStream, // RemoteVideoStream„Çí„Ç§„É≥„Éù„Éº„Éà
  RemoteAudioStream, // RemoteAudioStream„Çí„Ç§„É≥„Éù„Éº„Éà
} from '@skyway-sdk/room';

/**
 * „Éõ„Éº„É†ÁîªÈù¢„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
 * @param {object} props - „Éó„É≠„Éë„ÉÜ„Ç£
 * @param {function} props.setIsLoggedIn - „É≠„Ç∞„Ç§„É≥Áä∂ÊÖã„ÇíË®≠ÂÆö„Åô„ÇãÈñ¢Êï∞
 */
function Home({ setIsLoggedIn }) {
  // „É´„Éº„É†Âêç
  const [roomName, setRoomName] = useState("");
  // Ëá™ÂàÜ„ÅÆÂèÇÂä†ËÄÖÊÉÖÂ†± („É´„Éº„É†„Å´ÂèÇÂä†„Åô„Çã„Å®Ë®≠ÂÆö„Åï„Çå„Åæ„Åô)
  const [me, setMe] = useState(null);
  // SkyWay„Ç≥„É≥„ÉÜ„Ç≠„Çπ„Éà„ÅÆ„Ç§„É≥„Çπ„Çø„É≥„Çπ
  const [skywayContext, setSkywayContext] = useState(null);
  // SkyWay„É´„Éº„É†„ÅÆ„Ç§„É≥„Çπ„Çø„É≥„Çπ
  const [skywayRoom, setSkywayRoom] = useState(null);
  // „É™„É¢„Éº„Éà„Éì„Éá„Ç™Ë¶ÅÁ¥†„ÇíÁÆ°ÁêÜ„Åô„Çã„Åü„ÇÅ„ÅÆMap (memberId -> videoElement)
  const remoteVideoElements = useRef(new Map());
  // „É™„É¢„Éº„Éà„Éì„Éá„Ç™„ÇíË°®Á§∫„Åô„ÇãDOMË¶ÅÁ¥†„Å∏„ÅÆÂèÇÁÖß
  const robotDisplayRef = useRef(null);
  // „É≠„Éº„Ç´„É´„Çπ„Éà„É™„Éº„É†„ÅÆÂèÇÁÖß„Çí‰øùÊåÅ
  const localStreamRef = useRef(null);
  // Âèó‰ø°„Åó„Åü„É°„ÉÉ„Çª„Éº„Ç∏„ÇíË°®Á§∫„Åô„Çã„Åü„ÇÅ„ÅÆÁä∂ÊÖã
  const [receivedMessages, setReceivedMessages] = useState([]);
  // ÈÄÅ‰ø°„Åó„Åü„Éá„Éº„Çø„Çπ„Éà„É™„Éº„É†„ÅÆÂèÇÁÖß„Çí‰øùÊåÅ
  const localDataStreamRef = useRef(null);
  // „Éû„Ç§„ÇØ„ÅÆÁä∂ÊÖã„ÇíË°®Á§∫„Åô„Çã„Åü„ÇÅ„ÅÆÁä∂ÊÖã („Éü„É•„Éº„ÉàÁä∂ÊÖã„ÇíÁÆ°ÁêÜ)
  const [isMuted, setIsMuted] = useState(false);
  // „Ç™„Éº„Éá„Ç£„Ç™„Çπ„Éà„É™„Éº„É†„ÅÆPublication„Çí‰øùÊåÅ„Åô„Çã„Åü„ÇÅ„ÅÆÂèÇÁÖß
  const audioPublicationRef = useRef(null);
  // ÁèæÂú®„ÅÆË®ÄË™ûÁä∂ÊÖã ('japanese' „Åæ„Åü„ÅØ 'english')
  const [currentLanguage, setCurrentLanguage] = useState('japanese');
  // AI„É¢„Éº„Éâ„ÅåON„Åã„Å©„ÅÜ„Åã„ÇíÁÆ°ÁêÜ„Åô„ÇãÁä∂ÊÖã
  const [isAiModeOn, setIsAiModeOn] = useState(false);
  // AI„Åã„Çâ„ÅÆÊèêÊ°à„Çí‰øùÊåÅ„Åô„ÇãÁä∂ÊÖã
  // { key: 'ai_suggestion_1', text: 'ÊèêÊ°àÂÜÖÂÆπ' } „ÅÆÂΩ¢Âºè„ÅßÊ†ºÁ¥ç
  const [aiSuggestions, setAiSuggestions] = useState([null, null]); // 2„Å§„ÅÆÊèêÊ°à„Çí‰øùÊåÅ„Åô„Çã„Åü„ÇÅ„ÄÅÂàùÊúüÂÄ§„Çínull„ÅßÂüã„ÇÅ„Çã

  // „É°„ÉÉ„Çª„Éº„Ç∏„Å®„É¢„Éº„Ç∑„Éß„É≥„ÅÆÂ§öË®ÄË™û„Éû„ÉÉ„Éî„É≥„Ç∞
  const messages = useMemo(() => ({
    hello: {
      japanese: '„Åì„Çì„Å´„Å°„ÅØ',
      english: 'Hello'
    },
    goodbye: {
      japanese: '„Éê„Ç§„Éê„Ç§',
      english: 'Goodbye'
    },
    bow: {
      japanese: '„Éö„Ç≥„É™',
      english: 'Bow'
    },
    flap: {
      japanese: '„Éê„Çø„Éê„Çø',
      english: 'Flap'
    },
    lookUp: {
      japanese: '„ÅÜ„Åà„Çí„ÇÄ„Åè',
      english: 'Look up'
    },
    lookDown: {
      japanese: '„Åó„Åü„Çí„ÇÄ„Åè',
      english: 'Look down'
    },
    lookRight: {
      japanese: '„Åø„Åé„Çí„ÇÄ„Åè',
      english: 'Look right'
    },
    lookLeft: {
      japanese: '„Å≤„Å†„Çä„Çí„ÇÄ„Åè',
      english: 'Look left'
    },
    nod: {
      japanese: '„ÅÜ„Å™„Åö„Åè',
      english: 'Nod'
    },
    tiltHead: {
      japanese: '„Åã„Åó„Åí„Çã',
      english: 'Tilt head'
    },
    spin: {
      japanese: '„Åæ„Çè„Çã',
      english: 'Spin'
    },
    jump: {
      japanese: '„Ç∏„É£„É≥„Éó',
      english: 'Jump'
    },
    dance: {
      japanese: '„ÉÄ„É≥„Çπ',
      english: 'Dance'
    },
    bowDeep: {
      japanese: '„Åä„Åò„Åé',
      english: 'Deep bow'
    },
    attention: {
      japanese: '„Åç„Çí„Å§„Åë',
      english: 'Attention'
    },
    rest: {
      japanese: '‰ºë„ÇÅ',
      english: 'Rest'
    },
    whereFrom: {
      japanese: '„Å©„Åì„Åã„Çâ„Åç„Åü„ÅÆÔºü',
      english: 'Where are you from?'
    },
    doingSomething: {
      japanese: '‚óã‚óã„Åó„Å¶„ÅÑ„Åæ„Åô„ÅãÔºü',
      english: 'Are you doing XX?'
    },
    thankYou: {
      japanese: '„ÅÇ„Çä„Åå„Å®„ÅÜ',
      english: 'Thank you'
    },
    good: {
      japanese: '„ÅÑ„ÅÑ„Å≠',
      english: 'Good'
    },
    ookini: {
      japanese: '„Åä„Åä„Åç„Å´',
      english: 'Thank you (Kansai dialect)'
    },
    amazing: {
      japanese: '„Åô„Åî„ÅÑ',
      english: 'Amazing'
    },
    goodLuck: {
      japanese: '„Åå„Çì„Å∞„Çå',
      english: 'Good luck'
    },
    niceToMeetYou: {
      japanese: '„Çà„Çç„Åó„Åè',
      english: 'Nice to meet you'
    },
    goodJob: {
      japanese: '„ÅäÁñ≤„ÇåÊßò',
      english: 'Good job'
    },
    niceWeather: {
      japanese: '„ÅÑ„ÅÑ„ÅäÂ§©Ê∞ó',
      english: 'Nice weather'
    },
    hot: {
      japanese: 'Êöë„ÅÑ„Åß„Åô„Å≠',
      english: 'It\'s hot'
    },
    cold: {
      japanese: 'ÂØí„ÅÑ„Åß„Åô„Å≠',
      english: 'It\'s cold'
    },
    fun: {
      japanese: 'Ê•Ω„Åó„ÅÑ',
      english: 'Fun'
    },
    interesting: {
      japanese: 'Èù¢ÁôΩ„ÅÑ',
      english: 'Interesting'
    },
    wonderful: {
      japanese: '„Åô„Å¶„Åç',
      english: 'Wonderful'
    },
    cute: {
      japanese: '„Åã„Çè„ÅÑ„ÅÑ',
      english: 'Cute'
    },
    cool: {
      japanese: '„Åã„Å£„Åì„ÅÑ„ÅÑ',
      english: 'Cool'
    },
    seeYouTomorrow: {
      japanese: '„Åæ„ÅüÊòéÊó•',
      english: 'See you tomorrow'
    }
  }), []);


  /**
   * „É≠„Ç∞„Ç¢„Ç¶„Éà„Éú„Çø„É≥„ÇØ„É™„ÉÉ„ÇØÊôÇ„ÅÆ„Éè„É≥„Éâ„É©
   */
  const handleLogout = () => {
    setIsLoggedIn(false); // „É≠„Ç∞„Ç§„É≥Áä∂ÊÖã„Çífalse„Å´Ë®≠ÂÆö
  };

  // SkyWay App ID „Å® Secret Key
  // Ê≥®ÊÑè: Êú¨Áï™Áí∞Â¢É„Åß„ÅØ„ÄÅ„Åì„Çå„Çâ„ÅÆ„Ç≠„Éº„Çí„ÇØ„É©„Ç§„Ç¢„É≥„Éà„Çµ„Ç§„Éâ„Å´„Éè„Éº„Éâ„Ç≥„Éº„Éâ„Åõ„Åö„ÄÅ
  // „Çµ„Éº„Éê„Éº„Çµ„Ç§„Éâ„Åß„Éà„Éº„ÇØ„É≥„ÇíÁîüÊàê„Åó„Å¶„ÇØ„É©„Ç§„Ç¢„É≥„Éà„Å´ÂÆâÂÖ®„Å´Ê∏°„Åô„Åì„Å®„ÇíÂº∑„ÅèÊé®Â•®„Åó„Åæ„Åô„ÄÇ
  const appId = useMemo(() => "4490dcb5-dbe8-4028-ba0a-6d0d143e4515", []);
  const secretKey = useMemo(() => "40HHGEIOx0BRByPdxqwUswK0a+7v2JaeaJ9CcFjMdAQ=", []);

  // SkyWayË™çË®º„Éà„Éº„ÇØ„É≥„ÅÆÁîüÊàê
  const token = useMemo(() => {
    if (appId == null || secretKey == null) return undefined;

    return new SkyWayAuthToken({
      jti: uuidV4(), // JWT ID
      iat: nowInSec(), // Áô∫Ë°åÊôÇÂàª
      exp: nowInSec() + 60 * 60 * 24, // ÊúâÂäπÊúüÈôê (24ÊôÇÈñì)
      version: 3,
      scope: {
        appId: appId,
        rooms: [
          {
            name: "*", // „Åô„Åπ„Å¶„ÅÆ„É´„Éº„É†Âêç„Å´ÈÅ©Áî®
            methods: ["create", "close", "updateMetadata"], // „É´„Éº„É†Êìç‰Ωú„ÅÆÊ®©Èôê
            member: {
              name: "*", // „Åô„Åπ„Å¶„ÅÆ„É°„É≥„Éê„Éº„Å´ÈÅ©Áî®
              methods: ["publish", "subscribe", "updateMetadata"] // „É°„É≥„Éê„ÉºÊìç‰Ωú„ÅÆÊ®©Èôê
            }
          }],
        turn: {
          enabled: true // TURN„Çµ„Éº„Éê„Éº„ÅÆ‰ΩøÁî®„ÇíË®±ÂèØ (NATË∂ä„Åà„ÅÆ„Åü„ÇÅ)
        }
      }
    }).encode(secretKey);

  }, [appId, secretKey]);

  /**
   * SkyWayContext„ÅÆÂàùÊúüÂåñ„Å®„ÇØ„É™„Éº„É≥„Ç¢„ÉÉ„Éó„ÇíË°å„ÅÜuseEffect
   * „Éà„Éº„ÇØ„É≥„ÅåÂà©Áî®ÂèØËÉΩ„Å´„Å™„Å£„Åü„Å®„Åç„Å´‰∏ÄÂ∫¶„Å†„ÅëÂÆüË°å„Åï„Çå„Åæ„Åô„ÄÇ
   */
  useEffect(() => {
    if (!token) return;

    let currentContext;
    const initContext = async () => {
      try {
        currentContext = await SkyWayContext.Create(token);
        setSkywayContext(currentContext);
        console.log("SkyWayContext created successfully.");
      } catch (error) {
        console.error("Failed to create SkyWayContext:", error);
      }
    };

    initContext();

    // „Ç≥„É≥„Éù„Éº„Éç„É≥„Éà„ÅÆ„Ç¢„É≥„Éû„Ç¶„É≥„ÉàÊôÇ„Å´„Ç≥„É≥„ÉÜ„Ç≠„Çπ„Éà„ÇíÁ†¥Ê£Ñ
    return () => {
      if (currentContext) {
        currentContext.dispose();
        console.log("SkyWayContext disposed.");
      }
    };
  }, [token]); // token„ÅåÂ§âÊõ¥„Åï„Çå„Åü„Å®„Åç„Å´„ÅÆ„ÅøÂÆüË°å

  /**
   * „É™„É¢„Éº„Éà„Çπ„Éà„É™„Éº„É†„ÇíDOM„Å´ËøΩÂä†„Åô„Çã„Éò„É´„Éë„ÉºÈñ¢Êï∞
   * @param {RemoteVideoStream | RemoteAudioStream} stream - ËøΩÂä†„Åô„Çã„É°„Éá„Ç£„Ç¢„Çπ„Éà„É™„Éº„É†
   * @param {string} memberId - „Çπ„Éà„É™„Éº„É†„ÅÆ„Éë„Éñ„É™„ÉÉ„Ç∑„É£„Éº„ÅÆ„É°„É≥„Éê„ÉºID
   */
  const attachRemoteStream = (stream, memberId) => {
    if (robotDisplayRef.current && (stream instanceof RemoteVideoStream || stream instanceof RemoteAudioStream)) {
      const video = document.createElement("video");
      video.autoplay = true; // Ëá™ÂãïÂÜçÁîü
      video.playsInline = true; // „Ç§„É≥„É©„Ç§„É≥ÂÜçÁîü (iOS„Å™„Å©„ÅßÂøÖË¶Å)
      video.className = "remote-video"; // „Çπ„Çø„Ç§„É™„É≥„Ç∞Áî®„ÅÆ„ÇØ„É©„Çπ
      video.dataset.memberId = memberId; // „É°„É≥„Éê„ÉºID„Çí„Éá„Éº„ÇøÂ±ûÊÄß„Å´‰øùÂ≠ò
      stream.attach(video); // „Çπ„Éà„É™„Éº„É†„Çí„Éì„Éá„Ç™Ë¶ÅÁ¥†„ÅÆ„ÇΩ„Éº„Çπ„Å´Ë®≠ÂÆö
      robotDisplayRef.current.appendChild(video);
      remoteVideoElements.current.set(memberId, video);
      console.log(`Attached stream from ${memberId} to DOM.`);
    } else {
      console.warn(`Attempted to attach non-media stream or robotDisplayRef is null for member ${memberId}.`);
    }
  };

  /**
   * „Éá„Éº„Çø„Çπ„Éà„É™„Éº„É†„ÅÆÂèó‰ø°„ÇíÂá¶ÁêÜ„Åô„Çã„Éò„É´„Éë„ÉºÈñ¢Êï∞
   * @param {RemoteDataStream} stream - Âèó‰ø°„Åô„Çã„Éá„Éº„Çø„Çπ„Éà„É™„Éº„É†
   * @param {string} memberId - „Çπ„Éà„É™„Éº„É†„ÅÆ„Éë„Éñ„É™„ÉÉ„Ç∑„É£„Éº„ÅÆ„É°„É≥„Éê„ÉºID
   */
  const handleDataStream = (stream, memberId) => {
    if (stream instanceof RemoteDataStream) {
      stream.onData.add((data) => {
        console.log(`Received data from ${memberId}:`, data);
        // Âèó‰ø°„Åó„Åü„É°„ÉÉ„Çª„Éº„Ç∏„ÇíÁä∂ÊÖã„Å´ËøΩÂä†
        setReceivedMessages(prevMessages => [...prevMessages, { senderId: memberId, message: data }]);

        // AI„É¢„Éº„Éâ„ÅåON„ÅÆÂ†¥Âêà„ÄÅAIÊèêÊ°à„ÅÆ„Éï„Ç©„Éº„Éû„ÉÉ„Éà„Çí„ÉÅ„Çß„ÉÉ„ÇØ
        if (isAiModeOn) {
          // "ÊèêÊ°àÂÜÖÂÆπ" (AIÊèêÊ°àÔºãÊï∞Â≠óÔºâ„ÅÆ„Éï„Ç©„Éº„Éï„Ç©„Éº„Éû„ÉÉ„Éà„ÇíËß£Êûê
          const aiSuggestionRegex = /(.+)\s+\(AIÊèêÊ°à(\d+)\)/;
          const match = String(data).match(aiSuggestionRegex);

          if (match) {
            const suggestionContent = match[1];
            const suggestionNumber = parseInt(match[2], 10);

            if (suggestionNumber === 1 || suggestionNumber === 2) {
              setAiSuggestions(prevSuggestions => {
                const newSuggestions = [...prevSuggestions];
                newSuggestions[suggestionNumber - 1] = {
                  key: `ai_suggestion_${suggestionNumber}`, // „ÉÄ„Éü„Éº„ÅÆ„Ç≠„Éº
                  text: suggestionContent
                };
                return newSuggestions;
              });
            }
          }
        }
      });
      console.log(`Listening for data from ${memberId}.`);
    } else {
      console.warn(`Attempted to handle non-data stream as data stream for member ${memberId}.`);
    }
  };

  /**
   * „É´„Éº„É†„Å´ÂèÇÂä†„Åô„Çã„Éè„É≥„Éâ„É©
   * SkyWayContext„ÅåÂàùÊúüÂåñ„Åï„Çå„ÄÅ„É´„Éº„É†Âêç„ÅåÂÖ•Âäõ„Åï„Çå„Å¶„ÅÑ„ÇãÂ†¥Âêà„Å´ÂÆüË°å„Åï„Çå„Åæ„Åô„ÄÇ
   */
  const handleJoinRoom = async () => {
    if (!skywayContext) {
      console.error("SkyWayContext is not initialized. Please wait or check token.");
      return;
    }
    if (!roomName) {
      console.error("Room name is empty. Please enter a room name to join.");
      return;
    }
    if (skywayRoom && me) {
      console.warn("Already in a room. Please leave the current room first.");
      return;
    }

    try {
      // ÊåáÂÆö„Åï„Çå„Åü„É´„Éº„É†Âêç„ÅßP2P„É´„Éº„É†„Çí‰ΩúÊàê„Åæ„Åü„ÅØÊ§úÁ¥¢
      const room = await SkyWayRoom.FindOrCreate(skywayContext, {
        type: "p2p", // P2P„É´„Éº„É†„Çø„Ç§„Éó
        name: roomName,
      });

      // room„Ç™„Éñ„Ç∏„Çß„ÇØ„Éà„ÅåÊúâÂäπ„Åß„ÅÇ„Çã„Åì„Å®„ÇíÁ¢∫Ë™ç
      if (!room) {
        console.error("SkyWayRoom.FindOrCreate returned null or undefined room.");
        throw new Error("Failed to create or find SkyWayRoom.");
      }

      setSkywayRoom(room);
      console.log(`SkyWayRoom '${roomName}' found or created.`);

      // „É´„Éº„É†„Å´ÂèÇÂä†„Åó„ÄÅËá™ÂàÜ„ÅÆ„É°„É≥„Éê„ÉºÊÉÖÂ†±„ÇíÂèñÂæó
      const localMember = await room.join();
      // localMember„ÅåÊúâÂäπ„Åß„ÅÇ„Çã„Åì„Å®„ÇíÁ¢∫Ë™ç
      if (!localMember) {
        console.error("room.join() returned null or undefined localMember.");
        throw new Error("Failed to join the room.");
      }
      setMe(localMember);
      console.log(`Joined room '${roomName}' as member ID: ${localMember.id}`);

      // Ëá™ÂàÜ„ÅÆ„Ç´„É°„É©„Å®„Éû„Ç§„ÇØ„ÅÆ„Çπ„Éà„É™„Éº„É†„Çí‰ΩúÊàê„Åó„ÄÅÂÖ¨Èñã
      const { audio, video } = await SkyWayStreamFactory.createMicrophoneAudioAndCameraStream();
      localStreamRef.current = video; // „É≠„Éº„Ç´„É´„Éì„Éá„Ç™„Çπ„Éà„É™„Éº„É†„Çí‰øùÂ≠ò
      await localMember.publish(video);
      const publication_audio = await localMember.publish(audio);
      audioPublicationRef.current = publication_audio; // „Ç™„Éº„Éá„Ç£„Ç™Publication„Çí‰øùÂ≠ò
      setIsMuted(false);
      console.log("Published local camera and microphone stream.");

      // Ëá™ÂàÜ„ÅÆ„Éá„Éº„Çø„Çπ„Éà„É™„Éº„É†„Çí‰ΩúÊàê„Åó„ÄÅÂÖ¨Èñã
      const dataStream = await SkyWayStreamFactory.createDataStream();
      localDataStreamRef.current = dataStream; // „É≠„Éº„Ç´„É´„Éá„Éº„Çø„Çπ„Éà„É™„Éº„É†„Çí‰øùÂ≠ò
      await localMember.publish(dataStream);
      console.log("Published local data stream.");


      // --- Êó¢Â≠ò„ÅÆ„Çπ„Éà„É™„Éº„É†„ÇíË≥ºË™≠„Åô„Çã„É≠„Ç∏„ÉÉ„ÇØ ---
      // „É´„Éº„É†ÂÜÖ„ÅÆÊó¢Â≠ò„ÅÆÂÖ¨Èñã„Çπ„Éà„É™„Éº„É†„Çí„Åô„Åπ„Å¶Ë≥ºË™≠„Åô„Çã
      for (const publication of room.publications) {
        // Ëá™ÂàÜ„ÅÆ„Çπ„Éà„É™„Éº„É†„ÅØË≥ºË™≠„Åó„Å™„ÅÑ
        if (localMember.id === publication.publisher.id) {
          continue;
        }
        try {
          const { stream } = await localMember.subscribe(publication.id);
          console.log(`Subscribed to existing stream from ${publication.publisher.id}, type: ${publication.contentType}`);

          if (publication.contentType === 'video' || publication.contentType === 'audio') {
            attachRemoteStream(stream, publication.publisher.id);
          } else if (publication.contentType === 'data') {
            handleDataStream(stream, publication.publisher.id);
          } else {
            console.warn(`Unknown stream content type: ${publication.contentType} from ${publication.publisher.id}`);
          }
        } catch (subscribeError) {
          console.error(`Failed to subscribe to existing stream from ${publication.publisher.id}:`, subscribeError);
        }
      }

      // --- „É´„Éº„É†„Ç§„Éô„É≥„Éà„É™„Çπ„Éä„Éº„ÅÆË®≠ÂÆö ---

      // „É°„É≥„Éê„Éº„Åå„É´„Éº„É†„Å´ÂèÇÂä†„Åó„Åü„Å®„Åç„ÅÆ„Ç§„Éô„É≥„Éà
      if (room.onMemberJoined) {
        room.onMemberJoined.add((e) => {
          console.log(`Member joined: ${e.member.id}`);
        });
      } else {
        console.error("room.onMemberJoined is undefined. This is unexpected.");
      }

      // „É°„É≥„Éê„Éº„Åå„É´„Éº„É†„ÇíÈõ¢ËÑ±„Åó„Åü„Å®„Åç„ÅÆ„Ç§„Éô„É≥„Éà
      if (room.onMemberLeft) {
        room.onMemberLeft.add((e) => {
          console.log(`Member left: ${e.member.id}`);
          // Ë©≤ÂΩì„É°„É≥„Éê„Éº„ÅÆ„Éì„Éá„Ç™Ë¶ÅÁ¥†„ÇíDOM„Åã„ÇâÂâäÈô§„Åó„ÄÅMap„Åã„Çâ„ÇÇÂâäÈô§
          const videoElement = remoteVideoElements.current.get(e.member.id);
          if (videoElement && robotDisplayRef.current) {
            robotDisplayRef.current.removeChild(videoElement);
            remoteVideoElements.current.delete(e.member.id);
            console.log(`Removed video for member ${e.member.id}`);
          }
          // „Éá„Éº„Çø„Çπ„Éà„É™„Éº„É†Èñ¢ÈÄ£„ÅÆ„ÇØ„É™„Éº„É≥„Ç¢„ÉÉ„Éó„ÇÇ„Åì„Åì„Å´ËøΩÂä†ÂèØËÉΩ„Åß„Åô„Åå„ÄÅ
          // onData„É™„Çπ„Éä„Éº„ÅØ„Çπ„Éà„É™„Éº„É†„ÅåÈñâ„Åò„Çâ„Çå„Çã„Å®Ëá™ÂãïÁöÑ„Å´ÂÅúÊ≠¢„Åó„Åæ„Åô„ÄÇ
        });
      } else {
        console.error("room.onMemberLeft is undefined. This is unexpected.");
      }

      // „Çπ„Éà„É™„Éº„É†„ÅåÂÖ¨Èñã„Åï„Çå„Åü„Å®„Åç„ÅÆ„Ç§„Éô„É≥„Éà
      if (room.onStreamPublished) {
        room.onStreamPublished.add(async (e) => {
          console.log(`Stream published by ${e.publication.publisher.id}, type: ${e.publication.contentType}`);
          // Ëá™ÂàÜ„ÅÆ„Çπ„Éà„É™„Éº„É†„ÅØË≥ºË™≠„Åó„Å™„ÅÑ
          if (localMember.id !== e.publication.publisher.id) {
            try {
              // „É™„É¢„Éº„Éà„Çπ„Éà„É™„Éº„É†„ÇíË≥ºË™≠
              const { stream } = await localMember.subscribe(e.publication.id);
              console.log(`Subscribed to stream from ${e.publication.publisher.id}`);

              if (e.publication.contentType === 'video' || e.publication.contentType === 'audio') {
                attachRemoteStream(stream, e.publication.publisher.id);
              } else if (e.publication.contentType === 'data') {
                handleDataStream(stream, e.publication.publisher.id);
              } else {
                console.warn(`Unknown stream content type: ${e.publication.contentType} from ${e.publication.publisher.id}`);
              }
            } catch (subscribeError) {
              console.error(`Failed to subscribe to stream from ${e.publication.publisher.id}:`, subscribeError);
            }
          } else {
            console.log(`Skipping subscription for own stream: ${e.publication.publisher.id}`);
          }
        });
      } else {
        console.error("room.onStreamPublished is undefined. This is unexpected.");
      }

      // „Çπ„Éà„É™„Éº„É†„ÅåÈùûÂÖ¨Èñã„Å´„Å™„Å£„Åü„Å®„Åç„ÅÆ„Ç§„Éô„É≥„Éà
      if (room.onStreamUnpublished) {
        room.onStreamUnpublished.add((e) => {
          console.log(`Stream unpublished by ${e.publication.publisher.id}`);
          // Ë©≤ÂΩì„Çπ„Éà„É™„Éº„É†„ÅÆ„Éì„Éá„Ç™Ë¶ÅÁ¥†„ÇíDOM„Åã„ÇâÂâäÈô§„Åó„ÄÅMap„Åã„Çâ„ÇÇÂâäÈô§
          const videoElement = remoteVideoElements.current.get(e.publication.publisher.id);
          if (videoElement && robotDisplayRef.current) {
            robotDisplayRef.current.removeChild(videoElement);
            remoteVideoElements.current.delete(e.publication.publisher.id);
            console.log(`Removed unpublished video for member ${e.publication.publisher.id}`);
          }
        });
      } else {
        console.error("room.onStreamUnpublished is undefined. This is unexpected.");
      }

      // `room.onDataPublished` „ÅØÂ≠òÂú®„Åó„Å™„ÅÑ„Åü„ÇÅ„ÄÅ„Åì„ÅÆ„Éñ„É≠„ÉÉ„ÇØ„ÅØÂâäÈô§„Åó„Åæ„Åô„ÄÇ
      // „Éá„Éº„Çø„ÅØ `RemoteDataStream.onData` „Ç§„Éô„É≥„Éà„ÅßÂèó‰ø°„Åó„Åæ„Åô„ÄÇ

    } catch (error) {
      console.error("Failed to join room:", error);
      // „Ç®„É©„ÉºÁô∫ÁîüÊôÇ„Å´Áä∂ÊÖã„Çí„É™„Çª„ÉÉ„Éà„Åó„Å¶ÂÜçË©¶Ë°åÂèØËÉΩ„Å´„Åô„Çã
      setSkywayRoom(null);
      setMe(null);
      // „Ç®„É©„ÉºÊôÇ„Å´Êó¢Â≠ò„ÅÆ„Éì„Éá„Ç™Ë¶ÅÁ¥†„Çí„ÇØ„É™„Éº„É≥„Ç¢„ÉÉ„Éó
      if (robotDisplayRef.current) {
        Array.from(robotDisplayRef.current.children).forEach(child => {
          if (child.tagName === 'VIDEO') {
            robotDisplayRef.current.removeChild(child);
          }
        });
      }
      remoteVideoElements.current.clear();
      if (localStreamRef.current) {
        localStreamRef.current.stop(); // „É≠„Éº„Ç´„É´„Çπ„Éà„É™„Éº„É†„ÇíÂÅúÊ≠¢
        localStreamRef.current = null;
      }
      if (localDataStreamRef.current) {
        localDataStreamRef.current.close(); // „É≠„Éº„Ç´„É´„Éá„Éº„Çø„Çπ„Éà„É™„Éº„É†„ÇíÈñâ„Åò„Çã
        localDataStreamRef.current = null;
      }
      // „Éü„É•„Éº„ÉàÁä∂ÊÖã„ÇÇ„É™„Çª„ÉÉ„Éà
      setIsMuted(false);
      audioPublicationRef.current = null;
    }
  };

  /**
   * „É´„Éº„É†„Åã„ÇâÈÄÄÂá∫„Åô„Çã„Éè„É≥„Éâ„É©
   */
  const handleLeaveRoom = async () => {
    if (skywayRoom && me) {
      try {
        // „É≠„Éº„Ç´„É´„Çπ„Éà„É™„Éº„É†„ÇíÂÅúÊ≠¢„Åó„ÄÅÂÖ¨Èñã„ÇíËß£Èô§
        if (localStreamRef.current) {
          localStreamRef.current.stop();
          localStreamRef.current = null;
          console.log("Stopped local media stream.");
        }
        // „É≠„Éº„Ç´„É´„Éá„Éº„Çø„Çπ„Éà„É™„Éº„É†„ÇíÈñâ„Åò„Çã
        if (localDataStreamRef.current) {
          localDataStreamRef.current.close();
          localDataStreamRef.current = null;
          console.log("Closed local data stream.");
        }
        // „Ç™„Éº„Éá„Ç£„Ç™Publication„ÅÆÂèÇÁÖß„Çí„ÇØ„É™„Ç¢
        audioPublicationRef.current = null;
        setIsMuted(false); // „Éü„É•„Éº„ÉàÁä∂ÊÖã„Çí„É™„Çª„ÉÉ„Éà

        await me.leave(); // „É´„Éº„É†„Åã„ÇâÈÄÄÂá∫
        console.log(`Left room '${roomName}'`);
        skywayRoom.close(); // „É´„Éº„É†„ÇíÈñâ„Åò„Çã („Åì„Çå„ÅØ„É´„Éº„É†„ÅÆ‰ΩúÊàêËÄÖ„ÅÆ„Åø„ÅåÂäπÊûúÁöÑ„Åß„Åô)
        console.log("SkyWay Room closed.");
      } catch (error) {
        console.error("Failed to leave room:", error);
      } finally {
        // Áä∂ÊÖã„Çí„É™„Çª„ÉÉ„Éà
        setMe(null);
        setSkywayRoom(null);
        setReceivedMessages([]); // Âèó‰ø°„É°„ÉÉ„Çª„Éº„Ç∏„ÇÇ„ÇØ„É™„Ç¢
        // „Åô„Åπ„Å¶„ÅÆ„É™„É¢„Éº„Éà„Éì„Éá„Ç™Ë¶ÅÁ¥†„ÇíDOM„Åã„ÇâÂâäÈô§
        if (robotDisplayRef.current) {
          Array.from(robotDisplayRef.current.children).forEach(child => {
            if (child.tagName === 'VIDEO') {
              robotDisplayRef.current.removeChild(child);
            }
          });
        }
        remoteVideoElements.current.clear();
        // AI„É¢„Éº„Éâ„ÇÇ„Ç™„Éï„Å´„Åô„Çã
        setIsAiModeOn(false);
        setAiSuggestions([null, null]); // ÊèêÊ°à„ÇÇ„ÇØ„É™„Ç¢
      }
    }
  };

  /**
   * „É¢„Éº„Ç∑„Éß„É≥„ÇíÊìç‰Ωú„Åô„Çã„Éú„Çø„É≥„ÇíÊäº„Åó„ÅüÊôÇ„ÅÆÂá¶ÁêÜ
   * @param {string | object} messageOrKey - ÈÄÅ‰ø°„Åô„Çã„É°„ÉÉ„Çª„Éº„Ç∏„ÅÆ„Ç≠„Éº (messages„Ç™„Éñ„Ç∏„Çß„ÇØ„Éà„ÅÆ„Ç≠„Éº) „Åæ„Åü„ÅØAIÊèêÊ°à„Ç™„Éñ„Ç∏„Çß„ÇØ„Éà
   */
  const handleSend = async (messageOrKey) => {
    if (me && skywayRoom && localDataStreamRef.current) {
      try {
        let messageToSend;
        if (typeof messageOrKey === 'string') {
          // ÈÄöÂ∏∏„ÅÆ„Çµ„Ç§„Éâ„Éú„Çø„É≥„Åã„Çâ„ÅÆÈÄÅ‰ø°
          messageToSend = messages[messageOrKey]?.[currentLanguage];
        } else if (typeof messageOrKey === 'object' && messageOrKey.text) {
          // AIÊèêÊ°à„Éú„Çø„É≥„Åã„Çâ„ÅÆÈÄÅ‰ø°
          messageToSend = messageOrKey.text;
        }

        if (!messageToSend) {
          console.warn(`Message to send is empty or not found.`);
          return;
        }

        localDataStreamRef.current.write(messageToSend);
        console.log(`Sent message: "${messageToSend}"`);
        // ÈÄÅ‰ø°„Åó„Åü„É°„ÉÉ„Çª„Éº„Ç∏„ÇÇËá™Ë∫´„ÅÆÁîªÈù¢„Å´Ë°®Á§∫
        setReceivedMessages(prevMessages => [...prevMessages, { senderId: me.id, message: messageToSend, isLocal: true }]);
        setAiSuggestions([null, null]); // ÈÄÅ‰ø°„Åó„Åü„ÇâÊèêÊ°à„Çí„ÇØ„É™„Ç¢
      } catch (error) {
        console.error("Failed to send data:", error);
      }
    } else {
      console.warn("Cannot send message: Not in a room, 'me' object is not available, or data stream is not ready.");
    }
  };

  /**
   * „Éû„Ç§„ÇØ„ÅÆ„Éü„É•„Éº„Éà/„Éü„É•„Éº„ÉàËß£Èô§„ÇíÂàá„ÇäÊõø„Åà„Çã„Éè„É≥„Éâ„É©
   */
  const handleMute = async () => {
    console.log("start");
    if (!audioPublicationRef.current) {
      console.warn("Audio publication not available. Cannot toggle mute.");
      return;
    }

    try {
      if (isMuted) {
        // ÁèæÂú®„Éü„É•„Éº„ÉàÁä∂ÊÖã„Å™„Çâ„ÄÅ„Éü„É•„Éº„Éà„ÇíËß£Èô§
        await audioPublicationRef.current.enable();
        console.log("Microphone unmuted.");
      } else {
        // ÁèæÂú®„Éü„É•„Éº„ÉàÁä∂ÊÖã„Åß„Å™„ÅÑ„Å™„Çâ„ÄÅ„Éü„É•„Éº„Éà
        await audioPublicationRef.current.disable();
        console.log("Microphone muted.");
      }
      setIsMuted(!isMuted); // „Éü„É•„Éº„ÉàÁä∂ÊÖã„Çí„Éà„Ç∞„É´
    } catch (error) {
      console.error("Failed to toggle microphone mute state:", error);
    }
  };

  /**
   * Ë®ÄË™û„ÇíÂàá„ÇäÊõø„Åà„Çã„Éè„É≥„Éâ„É©
   */
  const handleToggleLanguage = () => {
    setCurrentLanguage(prevLang => (prevLang === 'japanese' ? 'english' : 'japanese'));
  };

  /**
   * AI„É¢„Éº„Éâ„ÇíÂàá„ÇäÊõø„Åà„Çã„Éè„É≥„Éâ„É©
   * AI„É¢„Éº„Éâ„ÅåON„Å´„Å™„Å£„Åü„Çâ„ÄÅAI„Å´ÊèêÊ°à„ÇíË¶ÅÊ±Ç„Åô„Çã„Ç∑„Ç∞„Éä„É´„ÇíÈÄÅ‰ø°„Åó„Åæ„Åô„ÄÇ
   */
  const handleAiModeToggle = () => {
    setIsAiModeOn(prev => {
      const newAiModeState = !prev;
      if (newAiModeState) {
        // AI„É¢„Éº„Éâ„ÅåON„Å´„Å™„Å£„Åü„Çâ„ÄÅAI„Å´ÊèêÊ°à„ÇíË¶ÅÊ±Ç„Åô„Çã„É°„ÉÉ„Çª„Éº„Ç∏„ÇíÈÄÅ‰ø°
        if (me && localDataStreamRef.current) {
          localDataStreamRef.current.write("AI_MODE_REQUEST_SUGGESTIONS");
          console.log("Sent AI_MODE_REQUEST_SUGGESTIONS signal.");
        }
        setAiSuggestions([null, null]); // Êñ∞„Åó„ÅÑ„Çª„ÉÉ„Ç∑„Éß„É≥„ÅÆ„Åü„ÇÅ„Å´ÊèêÊ°à„Çí„ÇØ„É™„Ç¢
      } else {
        // AI„É¢„Éº„Éâ„ÅåOFF„Å´„Å™„Å£„Åü„ÇâÊèêÊ°à„Çí„ÇØ„É™„Ç¢„Åó„ÄÅAI„É¢„Éº„ÉâÁµÇ‰∫Ü„ÅÆ„Ç∑„Ç∞„Éä„É´„ÇíÈÄÅ‰ø°
        setAiSuggestions([null, null]);
        if (me && localDataStreamRef.current) {
          localDataStreamRef.current.write("AI_MODE_OFF_SIGNAL");
          console.log("Sent AI_MODE_OFF_SIGNAL.");
        }
      }
      return newAiModeState;
    });
  };


  return (
    <div className="app-container">
      <div className="header">
        <div className="header-left">
          <button className="motion-btn" disabled={isAiModeOn}>„É¢„Éº„Ç∑„Éß„É≥</button>
          <span className="robot-status">
            Êìç‰Ωú„Åó„Å¶„ÅÑ„Çã„É≠„Éú„ÉÉ„ÉàÔºöRX78-myakumyaku
          </span>
        </div>
        <div className="header-right">
          <button onClick={handleLogout} className="logout-btn">„É≠„Ç∞„Ç¢„Ç¶„Éà</button>
          <button className="voice-btn" disabled={isAiModeOn}>„Éú„Ç§„Çπ</button>
        </div>
      </div>
      <div className="main-container">
        <div className="side-panel">
          <div className="side-panel-content">
            {Object.keys(messages).slice(0, 17).map(key => ( // Â∑¶ÂÅ¥„ÅÆ„Éú„Çø„É≥Êï∞„ÇíË™øÊï¥
              <button
                key={key}
                onClick={() => handleSend(key)}
                className="side-btn"
                disabled={isAiModeOn || !me} // AI„É¢„Éº„Éâ‰∏≠„ÅØÁÑ°ÂäπÂåñ
              >
                {messages[key].japanese} {/* Â∏∏„Å´Êó•Êú¨Ë™ûË°®Á§∫ */}
              </button>
            ))}
          </div>
        </div>
        <div className="main-area">
          <div className="room-controls">
            <input
              type="text"
              placeholder="„É´„Éº„É†Âêç„ÇíÂÖ•Âäõ"
              value={roomName}
              onChange={(e) => setRoomName(e.target.value)}
              className="room-input"
              disabled={!!me} // „É´„Éº„É†ÂèÇÂä†‰∏≠„ÅØÂÖ•Âäõ‰∏çÂèØ„Å´„Åô„Çã
            />
            {!me ? ( // „É´„Éº„É†„Å´ÂèÇÂä†„Åó„Å¶„ÅÑ„Å™„ÅÑÂ†¥Âêà
              <button onClick={handleJoinRoom} className="join-room-btn" disabled={!skywayContext || !roomName}>
                „É´„Éº„É†„Å´ÂèÇÂä†
              </button>
            ) : ( // „É´„Éº„É†„Å´ÂèÇÂä†„Åó„Å¶„ÅÑ„ÇãÂ†¥Âêà
              <button onClick={handleLeaveRoom} className="leave-room-btn">
                „É´„Éº„É†„ÇíÈÄÄÂá∫
              </button>
            )}
          </div>
          <div className="robot-display" ref={robotDisplayRef}>
            {/* „É™„É¢„Éº„Éà„ÅÆ„Éì„Éá„Ç™„Çπ„Éà„É™„Éº„É†„Åå„Åì„Åì„Å´ÂãïÁöÑ„Å´ËøΩÂä†„Åï„Çå„Åæ„Åô */}
            {me ? (
              <div className="room-info">
                „É´„Éº„É†ID: {roomName} / Ëá™ÂàÜ„ÅÆID: {me.id}
              </div>
            ) : (
              <p>„É´„Éº„É†„Å´ÂèÇÂä†„Åó„Å¶„Åè„Å†„Åï„ÅÑ</p>
            )}

            {/* AI„Åã„Çâ„ÅÆÊèêÊ°àË°®Á§∫ */}
            {isAiModeOn && (aiSuggestions[0] || aiSuggestions[1]) && (
              <div className="ai-suggestions">
                {aiSuggestions[0] && (
                  <button
                    onClick={() => handleSend(aiSuggestions[0])}
                    className="ai-suggestion-btn"
                  >
                    {aiSuggestions[0].text}
                  </button>
                )}
                {aiSuggestions[1] && (
                  <button
                    onClick={() => handleSend(aiSuggestions[1])}
                    className="ai-suggestion-btn"
                  >
                    {aiSuggestions[1].text}
                  </button>
                )}
              </div>
            )}
          </div>
        </div>
        <div className="side-panel right">
          <div className="side-panel-content">
            {Object.keys(messages).slice(17).map(key => ( // Âè≥ÂÅ¥„ÅÆ„Éú„Çø„É≥Êï∞„ÇíË™øÊï¥
              <button
                key={key}
                onClick={() => handleSend(key)}
                className="side-btn"
                disabled={isAiModeOn || !me} // AI„É¢„Éº„Éâ‰∏≠„ÅØÁÑ°ÂäπÂåñ
              >
                {messages[key].japanese} {/* Â∏∏„Å´Êó•Êú¨Ë™ûË°®Á§∫ */}
              </button>
            ))}
          </div>
        </div>
      </div>
      <div className="bottom-controls">
        <button
          className={`control-btn ai-btn ${isAiModeOn ? 'ai-mode-on' : ''}`}
          onClick={handleAiModeToggle}
          disabled={!me} // „É´„Éº„É†„Å´ÂèÇÂä†„Åó„Å¶„ÅÑ„Å™„ÅÑÂ†¥Âêà„ÅØÁÑ°Âäπ
        >
          ü§ñ AI„É¢„Éº„Éâ {isAiModeOn ? 'ON' : 'OFF'}
        </button>
        <button
          className={`control-btn mic-btn ${isMuted ? 'muted' : ''}`} // „Éü„É•„Éº„ÉàÁä∂ÊÖã„Å´Âøú„Åò„Å¶„ÇØ„É©„Çπ„ÇíËøΩÂä†
          onClick={handleMute}
          id="micBtn"
          disabled={!me || !audioPublicationRef.current || isAiModeOn} // „É´„Éº„É†„Å´ÂèÇÂä†„Åó„Å¶„ÅÑ„Å™„ÅÑ„ÄÅ„Åæ„Åü„ÅØ„Ç™„Éº„Éá„Ç£„Ç™„ÅåÂÖ¨Èñã„Åï„Çå„Å¶„ÅÑ„Å™„ÅÑ„ÄÅAI„É¢„Éº„Éâ‰∏≠„ÅØÁÑ°Âäπ
        >
          {isMuted ? 'üîá „Éû„Ç§„ÇØ„Éü„É•„Éº„Éà' : 'üé§ „Éû„Ç§„ÇØON'} {/* „Éü„É•„Éº„ÉàÁä∂ÊÖã„Å´Âøú„Åò„Å¶„Ç¢„Ç§„Ç≥„É≥„Å®„ÉÜ„Ç≠„Çπ„Éà„ÇíÂ§âÊõ¥ */}
        </button>
        <button className="control-btn manual-btn" id="manualBtn" disabled={isAiModeOn}>
          üìã „Éû„Éã„É•„Ç¢„É´
        </button>
        <button
          className="control-btn greeting-btn"
          id="languageBtn"
          onClick={handleToggleLanguage} // Ë®ÄË™ûÂàá„ÇäÊõø„Åà„Éè„É≥„Éâ„É©„ÇíË®≠ÂÆö
          disabled={isAiModeOn} // AI„É¢„Éº„Éâ‰∏≠„ÅØÁÑ°ÂäπÂåñ
        >
          üëã {currentLanguage === 'japanese' ? '„Å´„Åª„Çì„Åî' : 'English'} {/* ÁèæÂú®„ÅÆË®ÄË™û„Å´Âøú„Åò„Å¶„Éú„Çø„É≥„ÅÆ„ÉÜ„Ç≠„Çπ„Éà„ÇíÂ§âÊõ¥ */}
        </button>
      </div>
    </div>
  );
}

export default Home;
